mapper.select:

SELECT 
group_id,
hos_id,
copy_code,
ass_in_no,
ass_id,
ass_no,
in_amount,
price,
note,
reg_no
FROM ASS_IN_DETAIL_LAND 


AND group_id = #{group_id,jdbcType=INTEGER}


AND hos_id = #{hos_id,jdbcType=INTEGER}


AND copy_code = #{copy_code,jdbcType=VARCHAR}


AND ass_in_no = #{ass_in_no,jdbcType=VARCHAR}


AND ass_id = #{ass_id,jdbcType=INTEGER}


AND ass_no = #{ass_no,jdbcType=INTEGER}


AND in_amount = #{in_amount,jdbcType=INTEGER}


AND price = #{price,jdbcType=INTEGER}


AND note = #{note,jdbcType=VARCHAR}


order by group_id asc

SELECT a.group_id, a.hos_id, a.copy_code, a.ass_in_no, a.ass_id, a.ass_no, a.in_amount, a.price, a.note, b.is_measure, b.is_depre , b.ass_depre_code, b.depre_years, b.gb_code, b.is_bar, b.bar_type, b.is_manage_depre, b.manage_depr_method, b.manage_depre_amount, '01' bus_type_code,
d.ven_id, d.ven_no, d.note, a.reg_no FROM ASS_IN_DETAIL_land a left join ass_no_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.ass_id = b.ass_id and b.is_stop = 0 left join ASS_IN_MAIN_Land d on a.group_id = d.group_id and a.hos_id = d.hos_id and a.copy_code = d.copy_code and a.ass_in_no = d.ass_in_no
AND a.group_id = #{group_id,jdbcType=INTEGER}
AND a.hos_id = #{hos_id,jdbcType=INTEGER}
AND a.copy_code = #{copy_code,jdbcType=VARCHAR}
AND a.ass_in_no = #{ass_in_no,jdbcType=VARCHAR}
AND a.ass_id = #{ass_id,jdbcType=INTEGER}
AND a.ass_no = #{ass_no,jdbcType=INTEGER}
AND a.ass_spec = #{ass_spec,jdbcType=VARCHAR}
AND a.ass_model = #{ass_model,jdbcType=VARCHAR}
AND a.ass_brand = #{ass_brand,jdbcType=VARCHAR}
AND a.fac_id = #{fac_id,jdbcType=INTEGER}
AND a.fac_no = #{fac_no,jdbcType=INTEGER}
AND a.unit_code = #{unit_code,jdbcType=VARCHAR}
AND a.in_amount = #{in_amount,jdbcType=INTEGER}
AND a.price = #{price,jdbcType=INTEGER}
AND a.note = #{note,jdbcType=VARCHAR}
order by a.ass_id asc


