		audit_date = #{item.audit_date} ,


	is_inner = #{item.is_inner,}
	,


	outer_measure_org = #{item.outer_measure_org}
	,



		state=0

		AND group_id = #{item.group_id}


		AND hos_id = #{item.hos_id}


		AND copy_code = #{item.copy_code}


		AND plan_id = #{item.plan_id}

