mapper.select:
SELECT amp.group_id, amp.hos_id, amp.copy_code, amp.plan_id, amp.plan_no, amp.plan_name, amp.plan_year, amp.ass_nature, an.naturs_name, amp.create_emp, su.user_name create_emp_name, amp.create_date, amp.audit_emp, suu.user_name audit_emp_name, amp.audit_date, amp.state, amp.check_way, amp.note , amp.is_inner , amp.outer_measure_org FROM ASS_MEASURE_PLAN amp left join (select group_id, hos_id, copy_code, plan_id, max(ass_card_no) ass_card_no from ass_measure_plan_ass group by group_id, hos_id, copy_code, plan_id) aa on aa.group_id = amp.group_id and aa.hos_id = amp.hos_id and aa.copy_code = amp.copy_code and aa.plan_id = amp.plan_id left join v_ass_card ca on ca.GROUP_ID = aa.group_id and ca.HOS_ID = aa.hos_id and ca.COPY_CODE = aa.copy_code and ca.ASS_CARD_NO = aa.ass_card_no left join ass_no_dict di on di.group_id = ca.GROUP_ID and di.hos_id = ca.HOS_ID and di.copy_code = ca.COPY_CODE and di.ass_id = ca.ASS_ID and di.ass_no = ca.ASS_NO and di.is_stop = 0 left join ass_naturs an on amp.ass_nature = an.naturs_code left join sys_user su on amp.create_emp = su.user_id left join sys_user suu on amp.audit_emp = suu.user_id
AND amp.group_id = #{group_id,jdbcType=INTEGER}
AND amp.hos_id = #{hos_id,jdbcType=INTEGER}
AND amp.copy_code = #{copy_code,jdbcType=VARCHAR}
AND amp.plan_id = #{plan_id,jdbcType=INTEGER}
AND amp.plan_no like '%${plan_no}%'
AND amp.plan_name like '%${plan_name}%'
AND amp.plan_year = #{plan_year,jdbcType=VARCHAR}
AND amp.ass_nature = #{ass_nature,jdbcType=VARCHAR}
AND amp.create_emp = #{create_emp,jdbcType=VARCHAR}
AND amp.create_date = #{create_date,jdbcType=DATE}
AND amp.create_date between to_date(#{create_date_begin},'yyyy/mm/dd') and to_date(#{create_date_end},'yyyy/mm/dd')
AND amp.audit_emp = #{audit_emp,jdbcType=VARCHAR}
AND amp.audit_date = #{audit_date,jdbcType=DATE}
AND amp.audit_date between to_date(#{audit_date_begin},'yyyy/mm/dd') and to_date(#{audit_date_end},'yyyy/mm/dd')
AND amp.state = #{state,jdbcType=INTEGER}
AND amp.check_way = #{check_way,jdbcType=VARCHAR}
AND amp.note = #{note,jdbcType=VARCHAR}
AND (di.ass_name like '%${ass_table}%' or aa.ass_card_no like '%${ass_table}%' or di.ass_code like '%${ass_table}%' )
order by amp.plan_id asc


