SELECT an.naturs_code||' '||an.naturs_name ass_nature, an.naturs_code naturs_code, a.ass_card_no , b.ass_code, b.ass_name, b.ass_spec, b.ass_model, b.ass_brand, hu.unit_name ass_unit_name, hfd.fac_code||' '||hfd.fac_name fac_name, hsd.sup_code||' '||hsd.sup_name ven_name, a.price, a.depre_money, a.manage_depre_money, a.cur_money, a.fore_money, a.add_depre_month, a.add_manage_month, a.buy_type, a.use_state, a.in_date, a.ven_id, a.ven_no, a.store_id, a.store_no, a.is_dept, hdd.dept_name, a.fac_id, a.fac_no, a.ass_seq_no FROM ASS_CARD_SPECIAL a left join ass_no_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.ass_id = b.ass_id and b.is_stop = 0 and b.is_measure = 1 left join ass_type_dict c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.copy_code = c.copy_code and b.ass_type_id = c.ass_type_id and c.is_stop = 0
left join ass_naturs an on c.ass_naturs = an.naturs_code
left join hos_unit hu on b.group_id=hu.group_id and b.hos_id=hu.hos_id and b.ass_unit=hu.unit_code and hu.is_stop=0
left join hos_fac_dict hfd on a.group_id = hfd.group_id and a.hos_id = hfd.hos_id and a.fac_id = hfd.fac_id and hfd.is_stop = 0
left join hos_sup_dict hsd on a.group_id = hsd.group_id and a.hos_id = hsd.hos_id and a.ven_id = hsd.sup_id and hsd.is_stop = 0
left join hos_dept_dict hdd on a.group_id = hdd.group_id and a.hos_id = hdd.hos_id and a.dept_id = hdd.dept_id and hdd.is_stop = 0
left join ( select t1.group_id, t1.hos_id, t1.copy_code, t1.ass_card_no, nvl(sum(t1.unpay_money),0) unpay_money, nvl(sum(t1.pay_money),0) pay_money , nvl(sum(t1.pay_plan_money),0) pay_plan_money
from ass_pay_stage_SPECIAL t1 left join ass_card_SPECIAL t2 on t1.group_id = t2.group_id and t1.hos_id = t2.hos_id and t1.copy_code = t2.copy_code and t1.ass_card_no = t2.ass_card_no group by t1.group_id, t1.hos_id, t1.copy_code, t1.ass_card_no ) pay on a.group_id = pay.group_id and a.hos_id = pay.hos_id and a.copy_code = pay.copy_code and a.ass_card_no = pay.ass_card_no
and a.group_id = #{group_id} and a.hos_id = #{hos_id} and a.copy_code = #{copy_code} and a.is_measure = 1 and a.use_state in (0,1,2,3,4,5,6)
and an.naturs_code = #{ass_nature}
and a.buy_type = #{bus_type_code}
and a.ass_card_no like '%${ass_card_no}%'
and b.ass_name like '%${ass_name}%'
and a.dept_id = #{dept_id}
and a.dept_no = #{dept_no}
and a.use_state in (${use_state})
and a.in_date <= to_date(#{in_date},'YYYY-MM-DD')
and a.store_id = #{store_id}
and a.store_no = #{store_no}
and a.ven_id = #{ven_id}
and a.ven_no = #{ven_no}
and a.is_dept = #{is_dept}
and a.contract_id = #{contract_id}
and exists(
select 1 from ass_share_dept_SPECIAL t1 where a.hos_id = t1.hos_id and a.copy_code = t1.copy_code and a.ass_card_no = t1.ass_card_no and t1.dept_id = #{dept_id} and t1.dept_no = #{dept_no} )
and pay.pay_plan_money - pay.pay_money > 0
and pay.pay_money > 0
and a.ass_card_no not in (${ass_card_not_exists})
and ( a.ass_card_no like '%${key}%' or a.ass_id like '%${key}%' or b.ass_code like '%${key}%' or b.ass_name like '%${key}%' or an.naturs_code like '%${key}%' or b.spell_code like '%${key}%' or b.wbx_code like '%${key}%' )
and not exists ( ${sql} )
order by a.ass_card_no

