SELECT amp.group_id, amp.hos_id, amp.copy_code, amp.plan_id, amp.plan_no , amp.plan_name, amp.plan_year, amp.ass_nature, an.naturs_name, amp.create_emp, su.user_name create_emp_name, amp.create_date , amp.audit_emp, suu.user_name audit_emp_name, amp.audit_date, case when nvl(ROUND(TO_NUMBER(add_months(amp.AUDIT_DATE,amp.measure_cycle) - to_date(#{newDate,jdbcType=DATE},'yyyy-MM-dd'))), 0) < 0 then '' when nvl(ROUND(TO_NUMBER(add_months(amp.AUDIT_DATE,amp.measure_cycle) - to_date(#{newDate,jdbcType=DATE},'yyyy-MM-dd'))), 0) = 0 then '' when nvl(ROUND(TO_NUMBER(add_months(amp.AUDIT_DATE,amp.measure_cycle) - to_date(#{newDate,jdbcType=DATE},'yyyy-MM-dd'))), 0) < p.para_value then '' end as state_name , amp.check_way, amp.note , amp.is_inner , amp.outer_measure_org FROM ASS_MEASURE_PLAN amp left join ass_naturs an on amp.ass_nature = an.naturs_code left join sys_user su on amp.create_emp = su.user_id left join sys_user suu on amp.audit_emp = suu.user_id left join acc_para p on amp.group_id = p.group_id and amp.hos_id = p.hos_id and amp.copy_code = p.copy_code and p.para_code = '05004'
AND amp.group_id = #{group_id,jdbcType=INTEGER}
AND amp.hos_id = #{hos_id,jdbcType=INTEGER}
AND amp.copy_code = #{copy_code,jdbcType=VARCHAR}
AND amp.state = 1
and nvl(ROUND(TO_NUMBER(add_months(amp.AUDIT_DATE,amp.measure_cycle) - to_date(#{newDate,jdbcType=DATE},'yyyy-MM-dd'))), 0) > 0 and nvl(ROUND(TO_NUMBER(add_months(amp.AUDIT_DATE,amp.measure_cycle) - to_date(#{queryDate,jdbcType=DATE},'yyyy-MM-dd'))), 0) < p.para_value
and nvl(ROUND(TO_NUMBER(add_months(amp.AUDIT_DATE,amp.measure_cycle) - to_date(#{newDate,jdbcType=DATE},'yyyy-MM-dd'))), 0) = 0
and nvl(ROUND(TO_NUMBER(add_months(amp.AUDIT_DATE,amp.measure_cycle) - to_date(#{newDate,jdbcType=DATE},'yyyy-MM-dd'))), 0) < 0
AND amp.ass_nature = #{ass_nature,jdbcType=VARCHAR}
AND amp.plan_name = #{plan_name,jdbcType=VARCHAR}
order by amp.plan_id asc

SELECT a.plan_id as id, a.ass_card_no, c.ASS_SPEC, c.ASS_MONDL, c.ASS_BRAND, b.ass_code, b.ass_name, d.fac_code, d.fac_name, a.measure_cycle, case when a.measure_kind = 0 then '' when a.measure_kind = 1 then '' else '' end measure_kind_name, a.plan_exec_date, a.measure_desc
FROM ASS_MEASURE_PLAN_ASS a left join v_ass_card c on a.group_id = c.GROUP_ID and a.hos_id = c.HOS_ID and a.copy_code = c.COPY_CODE and a.ass_card_no = c.ASS_CARD_NO left join ASS_NO_DICT b on b.group_id = c.GROUP_ID and b.hos_id = c.HOS_ID and b.copy_code = c.COPY_CODE and b.ass_id = c.ASS_ID and b.ass_no = c.ASS_NO left join HOS_FAC_DICT d on d.group_id = c.GROUP_ID and d.hos_id = c.HOS_ID and d.fac_id = c.FAC_ID and d.fac_no = c.FAC_NO where a.group_id = #{group_id,jdbcType=INTEGER} AND a.hos_id = #{hos_id,jdbcType=INTEGER} AND a.copy_code = #{copy_code,jdbcType=VARCHAR}
AND a.plan_id in (${paraId})
AND a.plan_id = #{plan_id,jdbcType=VARCHAR}
order by a.plan_id asc

