mapper.insert:
INSERT INTO ASS_PAY_STAGE_HOUSE ( group_id , hos_id , copy_code , ass_card_no , stage_no , stage_name , pay_term_id , pay_plan_date , pay_plan_percent , pay_plan_money , pay_money , unpay_money , ven_id , ven_no , note , bill_money , unbill_money ) select t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.ass_card_no} , #{item.stage_no} , nvl(#{item.stage_name},'') , #{item.pay_term_id} , to_date(#{item.pay_plan_date},'YYYY-MM-DD') , nvl(#{item.pay_plan_percent},0) , nvl(#{item.pay_plan_money},0) , nvl(#{item.pay_money},0) , nvl(#{item.unpay_money},0) , #{item.ven_id} , #{item.ven_no} , nvl(#{item.note},'') , nvl(#{item.bill_money},0) , nvl(#{item.unbill_money},0) from dual
)t

