mapper.insert:
INSERT INTO ASS_PAY_STAGE ( group_id , hos_id , copy_code , pay_no , bill_no , pay_code , pay_money , note
) select t.* from(
select #{item.group_id,jdbcType=INTEGER} , #{item.hos_id,jdbcType=INTEGER} , #{item.copy_code,jdbcType=VARCHAR} , #{item.pay_no,jdbcType=VARCHAR} , #{item.bill_no,jdbcType=VARCHAR} , #{item.pay_code,jdbcType=VARCHAR} , nvl(#{item.pay_money,jdbcType=INTEGER},0) , nvl(#{item.note,jdbcType=VARCHAR},'')
from dual
)t

mapper.update:

UPDATE ass_pay_stage 


pay_money = #{pay_money}
,


note = #{note}
,




AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND pay_no = #{pay_no}


AND bill_no = #{bill_no}


AND pay_code = #{pay_code}


AND pay_money = #{pay_money}


AND note = #{note}

update.foreach:
UPDATE ass_pay_stage 


pay_money = #{item.pay_money}
,


note = #{item.note}
,




AND group_id = #{item.group_id}


AND hos_id = #{item.hos_id}


AND copy_code = #{item.copy_code}


AND pay_no = #{item.pay_no}


AND bill_no = #{item.bill_no}


AND pay_code = #{item.pay_code}

mapper.delete:

DELETE FROM ass_pay_stage 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND pay_no = #{pay_no}


AND bill_no = #{bill_no}


AND pay_code = #{pay_code}

