DELETE FROM ass_pay_stage 



		group_id = #{item.group_id}


		AND hos_id = #{item.hos_id}


		AND copy_code = #{item.copy_code}


		AND pay_no = #{item.pay_no}


		AND bill_no = #{item.bill_no}

mapper.select:

SELECT 
	a.group_id,
	a.hos_id,
	a.copy_code,
	a.pay_no,
	a.bill_no,
	a.pay_code,
	a.pay_code||' '||b.pay_name pay_name,
	a.pay_money,
	a.note
FROM ASS_PAY_STAGE a
left join acc_pay_type b on
a.group_id = b.group_id
and a.hos_id = b.hos_id
and a.copy_code = b.copy_code
and a.pay_code = b.pay_code

AND a.group_id = #{group_id}
AND a.hos_id = #{hos_id}
AND a.copy_code = #{copy_code}
and b.source_code='HRP'

AND a.pay_no = #{pay_no}


AND a.bill_no = #{bill_no}


AND a.pay_code = #{pay_code}


AND a.pay_money = #{pay_money}


AND a.note = #{note}


order by a.pay_code asc


SELECT 
group_id,
hos_id,
copy_code,
pay_no,
bill_no,
pay_code,
pay_money,
note
FROM ASS_PAY_STAGE 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND pay_no = #{pay_no}


AND bill_no = #{bill_no}


AND pay_code = #{pay_code}


AND pay_money = #{pay_money}


AND note = #{note}


order by group_id asc


SELECT 
group_id,
hos_id,
copy_code,
pay_no,
bill_no,
pay_code,
pay_money,
note
FROM ASS_PAY_STAGE 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND pay_no = #{pay_no}


AND bill_no = #{bill_no}


AND pay_code = #{pay_code}


AND pay_money = #{pay_money}


AND note = #{note}


order by group_id asc
