mapper.insert:
INSERT INTO ASS_SHARE_DEPT_HOUSE ( group_id , hos_id , copy_code , ass_card_no , dept_id , dept_no , area , note
) select t.* from(
select #{item.group_id,jdbcType=INTEGER} , #{item.hos_id,jdbcType=INTEGER} , #{item.copy_code,jdbcType=VARCHAR} , #{item.ass_card_no,jdbcType=VARCHAR} , #{item.dept_id,jdbcType=INTEGER} , #{item.dept_no,jdbcType=INTEGER} , #{item.area,jdbcType=INTEGER} , #{item.note,jdbcType=VARCHAR}
from dual
)t

mapper.update:

UPDATE ass_share_dept_house 


area = #{area,jdbcType=INTEGER}
,


note = #{note,jdbcType=VARCHAR}
,




AND group_id = #{group_id,jdbcType=INTEGER}


AND hos_id = #{hos_id,jdbcType=INTEGER}


AND copy_code = #{copy_code,jdbcType=VARCHAR}


AND ass_card_no = #{ass_card_no,jdbcType=VARCHAR}


AND dept_id = #{dept_id,jdbcType=INTEGER}


AND dept_no = #{dept_no,jdbcType=INTEGER}

