mapper.insert:
INSERT INTO BUDG_CASH ( group_id , hos_id , copy_code , budg_year , month , cash_begain , cash_in , cash_out , cash_add , cash_end
) select t.* from(
select #{item.group_id,jdbcType=INTEGER} , #{item.hos_id,jdbcType=INTEGER} , #{item.copy_code,jdbcType=VARCHAR} , #{item.budg_year,jdbcType=VARCHAR} , #{item.month,jdbcType=VARCHAR} , #{item.cash_begain,jdbcType=INTEGER} , #{item.cash_in,jdbcType=INTEGER} , #{item.cash_out,jdbcType=INTEGER} , #{item.cash_add,jdbcType=INTEGER} , #{item.cash_end,jdbcType=INTEGER}
from dual
)t

mapper.update:

UPDATE budg_cash 
SET
	cash_begain = #{cash_begain,jdbcType=INTEGER}
	,
	cash_in = #{cash_in,jdbcType=INTEGER}
	,
	cash_out = #{cash_out,jdbcType=INTEGER}
	,
	cash_add = #{cash_add,jdbcType=INTEGER}
	,
	cash_end = #{cash_end,jdbcType=INTEGER}
WHERE                     
	group_id = #{group_id,jdbcType=INTEGER}

	AND hos_id = #{hos_id,jdbcType=INTEGER}

	AND copy_code = #{copy_code,jdbcType=VARCHAR}

	AND budg_year = #{budg_year,jdbcType=VARCHAR}

	AND month = #{month,jdbcType=VARCHAR}

update.foreach:
UPDATE budg_cash 
SET
	cash_begain = #{item.cash_begain,jdbcType=INTEGER}
	,
	cash_in = #{item.cash_in,jdbcType=INTEGER}
	,
	cash_out = #{item.cash_out,jdbcType=INTEGER}
	,
	cash_add = #{item.cash_add,jdbcType=INTEGER}
	,
	cash_end = #{item.cash_end,jdbcType=INTEGER}
WHERE                     
	group_id = #{item.group_id,jdbcType=INTEGER}

	AND hos_id = #{item.hos_id,jdbcType=INTEGER}

	AND copy_code = #{item.copy_code,jdbcType=VARCHAR}

	AND budg_year = #{item.budg_year,jdbcType=VARCHAR}

	AND month = #{item.month,jdbcType=VARCHAR}

mapper.delete:

