mapper.insert:
INSERT INTO BUDG_MED_INCOME_EDIT_PLAN ( group_id , hos_id , copy_code , budg_year , subj_code , is_single_count , edit_method , get_way , formula_id , fun_id , resolve_or_sum , resolve_way
) select BUDG_MED_INCOME_EDIT_PLAN_SEQ.nextval , t.* from(
select #{item.group_id,jdbcType=INTEGER} , #{item.hos_id,jdbcType=INTEGER} , #{item.copy_code,jdbcType=VARCHAR} , #{item.budg_year,jdbcType=VARCHAR} , #{item.subj_code,jdbcType=VARCHAR} , #{item.is_single_count,jdbcType=INTEGER} , #{item.edit_method,jdbcType=VARCHAR} , #{item.get_way,jdbcType=VARCHAR} , #{item.formula_id,jdbcType=VARCHAR} , #{item.fun_id,jdbcType=VARCHAR} , #{item.resolve_or_sum,jdbcType=VARCHAR} , #{item.resolve_way,jdbcType=VARCHAR}
from dual
)t

mapper.update:

UPDATE budg_med_income_edit_plan set
	is_single_count = #{is_single_count,jdbcType=INTEGER}
	,
	edit_method = #{edit_method,jdbcType=VARCHAR}
	,
	get_way = #{get_way,jdbcType=VARCHAR}
	,
	formula_id = #{formula_id,jdbcType=VARCHAR}
	,
	fun_id = #{fun_id,jdbcType=VARCHAR}
	,
	resolve_or_sum = #{resolve_or_sum,jdbcType=VARCHAR}
	,
	resolve_way = #{resolve_way,jdbcType=VARCHAR}

where                     
	group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=VARCHAR}
	AND budg_year = #{budg_year,jdbcType=VARCHAR}
	AND subj_code = #{subj_code,jdbcType=VARCHAR}

update.foreach:
UPDATE budg_med_income_edit_plan set
	is_single_count = #{item.is_single_count,jdbcType=INTEGER}
	,
	edit_method = #{item.edit_method,jdbcType=VARCHAR}
	,
	get_way = #{item.get_way,jdbcType=VARCHAR}
	,
	formula_id = #{item.formula_id,jdbcType=VARCHAR}
	,
	fun_id = #{item.fun_id,jdbcType=VARCHAR}
	,
	resolve_or_sum = #{item.resolve_or_sum,jdbcType=VARCHAR}
	,
	resolve_way = #{item.resolve_way,jdbcType=VARCHAR}
where                     
	group_id = #{item.group_id,jdbcType=INTEGER}
	AND hos_id = #{item.hos_id,jdbcType=INTEGER}
	AND copy_code = #{item.copy_code,jdbcType=VARCHAR}
	AND budg_year = #{item.budg_year,jdbcType=VARCHAR}
	AND subj_code = #{item.subj_code,jdbcType=VARCHAR}

mapper.delete:

DELETE FROM budg_med_income_edit_plan 
where                     
	group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=VARCHAR}
	AND budg_year = #{budg_year,jdbcType=VARCHAR}

	AND subj_code = #{subj_code,jdbcType=VARCHAR}

DELETE FROM budg_med_income_edit_plan 
where

		group_id = #{item.group_id,jdbcType=INTEGER}
		AND hos_id = #{item.hos_id,jdbcType=INTEGER}
		AND copy_code = #{item.copy_code,jdbcType=VARCHAR}
		AND budg_year = #{item.budg_year,jdbcType=VARCHAR}
		AND subj_code = #{item.subj_code,jdbcType=VARCHAR}

mapper.select:
SELECT bmp.group_id, bmp.hos_id, bmp.copy_code, bmp.budg_year, bmp.subj_code, bis.subj_name, bis.subj_name_all, bis.subj_level, bmp.is_single_count, bmp.edit_method, em.value_name edit_method_name, bmp.get_way, gw.value_name get_way_name, bmp.formula_id, bfs.formula_name, bmp.fun_id, bf.fun_name, bmp.resolve_or_sum, ros.value_name resolve_or_sum_name, bmp.resolve_way, rw.value_name resolve_way_name FROM BUDG_MED_INCOME_EDIT_PLAN bmp left join budg_income_subj bis on bmp.group_id=bis.group_id and bmp.hos_id=bis.hos_id and bmp.copy_code=bis.copy_code and bmp.subj_code=bis.subj_code and bmp.budg_year=bis.budg_year Left join BUDG_FORMULA_SET bfs on bmp.group_id = bfs.group_id and bmp.hos_id = bfs.hos_id and bmp.copy_code= bfs.copy_code and bmp.formula_id = bfs.formula_id Left join BUDG_FUN bf on bmp.group_id = bf.group_id and bmp.hos_id = bf.hos_id and bmp.copy_code= bf.copy_code and bmp.fun_id = bf.fun_code left join budg_sys_dict em on em.value_code=bmp.edit_method and em.f_code='EDIT_METHOD' left join budg_sys_dict gw on gw.value_code=bmp.get_way and gw.f_code='GET_WAY' left join budg_sys_dict ros on ros.value_code=bmp.resolve_or_sum and ros.f_code='RESOLVE_OR_SUM' left join budg_sys_dict rw on rw.value_code=bmp.resolve_way and rw.f_code='RESOLVE_WAY' where bmp.group_id = #{group_id,jdbcType=INTEGER}
AND bmp.hos_id = #{hos_id,jdbcType=INTEGER}
AND bmp.copy_code = #{copy_code,jdbcType=VARCHAR}
AND bmp.budg_year = #{budg_year,jdbcType=VARCHAR}
and bmp.subj_code in ( select bis2.subj_code from budg_income_subj bis2 start with bis2.subj_code=#{subj_code,jdbcType=VARCHAR} connect by bis2.super_code=prior bis2.subj_code )
AND bis.subj_level = #{subj_level,jdbcType=INTEGER}
order by bmp.subj_code asc


SELECT 
group_id,
hos_id,
copy_code,
budg_year,
subj_code,
is_single_count,
edit_method,
get_way,
formula_id,
fun_id,
resolve_or_sum,
resolve_way
FROM BUDG_MED_INCOME_EDIT_PLAN 


AND group_id = #{group_id,jdbcType=INTEGER}


AND hos_id = #{hos_id,jdbcType=INTEGER}


AND copy_code = #{copy_code,jdbcType=VARCHAR}


AND budg_year = #{budg_year,jdbcType=VARCHAR}


AND subj_code = #{subj_code,jdbcType=VARCHAR}


AND is_single_count = #{is_single_count,jdbcType=INTEGER}


AND edit_method = #{edit_method,jdbcType=VARCHAR}


AND get_way = #{get_way,jdbcType=VARCHAR}


AND formula_id = #{formula_id,jdbcType=VARCHAR}


AND fun_id = #{fun_id,jdbcType=VARCHAR}


AND resolve_or_sum = #{resolve_or_sum,jdbcType=VARCHAR}


AND resolve_way = #{resolve_way,jdbcType=VARCHAR}


order by group_id asc


SELECT 
group_id,
hos_id,
copy_code,
budg_year,
subj_code,
is_single_count,
edit_method,
get_way,
formula_id,
fun_id,
resolve_or_sum,
resolve_way
FROM BUDG_MED_INCOME_EDIT_PLAN 


AND group_id = #{group_id,jdbcType=INTEGER}


AND hos_id = #{hos_id,jdbcType=INTEGER}


AND copy_code = #{copy_code,jdbcType=VARCHAR}


AND budg_year = #{budg_year,jdbcType=VARCHAR}


AND subj_code = #{subj_code,jdbcType=VARCHAR}


AND is_single_count = #{is_single_count,jdbcType=INTEGER}


AND edit_method = #{edit_method,jdbcType=VARCHAR}


AND get_way = #{get_way,jdbcType=VARCHAR}


AND formula_id = #{formula_id,jdbcType=VARCHAR}


AND fun_id = #{fun_id,jdbcType=VARCHAR}


AND resolve_or_sum = #{resolve_or_sum,jdbcType=VARCHAR}


AND resolve_way = #{resolve_way,jdbcType=VARCHAR}


order by group_id asc

SELECT 
	count(0)
FROM budg_med_income_edit_plan 
WHERE 
group_id = #{group_id,jdbcType=INTEGER}   
and hos_id = #{hos_id,jdbcType=INTEGER}   
and copy_code = #{copy_code,jdbcType=VARCHAR} 
and budg_year = #{budg_year,jdbcType=VARCHAR}   
and subj_code = #{subj_code,jdbcType=VARCHAR}
