mapper.insert:
INSERT INTO BUDG_MAT_PUR_ADJ ( group_id , hos_id , copy_code , budg_year , adj_code , adj_file , adj_remark , state , maker , make_data , checker , check_date , last_check_code
) select t.* from(
select #{item.group_id,jdbcType=INTEGER} , #{item.hos_id,jdbcType=INTEGER} , #{item.copy_code,jdbcType=VARCHAR} , #{item.budg_year,jdbcType=VARCHAR} , #{item.adj_code,jdbcType=VARCHAR} , #{item.adj_file,jdbcType=VARCHAR} , #{item.adj_remark,jdbcType=VARCHAR} , #{item.state,jdbcType=VARCHAR} , #{item.maker,jdbcType=INTEGER} , to_date(#{item.make_data},'yyyy-MM-dd') , #{item.checker,jdbcType=INTEGER} , to_date(#{item.check_date},'yyyy-MM-dd') , to_date(#{item.last_check_code},'yyyy-MM-dd') from dual
)t

mapper.update:

UPDATE BUDG_MAT_PUR_ADJ set
	adj_file = #{adj_file,jdbcType=VARCHAR}
	,
	adj_remark = #{adj_remark,jdbcType=VARCHAR}
	,
	state = #{state,jdbcType=VARCHAR}
where                     
	group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=VARCHAR}
	AND budg_year = #{budg_year,jdbcType=VARCHAR}
	AND adj_code = #{adj_code,jdbcType=VARCHAR}

