mapper.select:
SELECT a.group_id, a.hos_id, a.copy_code, a.budg_year, a.adj_code, a.adj_file, a.adj_remark, a.state, a.maker, b.emp_name make_name , a.make_data, a.checker, c.emp_name check_name , a.check_date, a.last_check_code, d.bc_state FROM BUDG_MAT_PUR_ADJ a left join BUDG_MAT_PUR_CHECK d on a.group_id = d.group_id and a.hos_id = d.hos_id and a.copy_code = d.copy_code and a.budg_year = d.budg_year and a.last_check_code = d.check_code left join hos_emp_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.maker = b.emp_id and b.is_stop = 0 left join hos_emp_dict c on a.group_id = c.group_id and a.hos_id = c.hos_id and a.checker = c.emp_id and c.is_stop = 0 where a.group_id = #{group_id,jdbcType=INTEGER} AND a.hos_id = #{hos_id,jdbcType=INTEGER} AND a.copy_code = #{copy_code,jdbcType=VARCHAR}
AND a.budg_year = #{budg_year,jdbcType=VARCHAR}
AND a.state = #{state,jdbcType=VARCHAR}
order by a.adj_code asc

SELECT group_id, hos_id, copy_code, budg_year, adj_code, adj_file, adj_remark, state, maker, make_data, checker, check_date, last_check_code FROM BUDG_MAT_PUR_ADJ WHERE group_id = #{group_id,jdbcType=INTEGER}
AND hos_id = #{hos_id,jdbcType=INTEGER}
AND copy_code = #{copy_code,jdbcType=VARCHAR}
AND budg_year = #{budg_year,jdbcType=VARCHAR}
AND adj_code = #{adj_code,jdbcType=VARCHAR}

SELECT group_id, hos_id, copy_code, budg_year, adj_code, adj_file, adj_remark, state, maker, make_data, checker, check_date, last_check_code FROM BUDG_MAT_PUR_ADJ WHERE group_id = #{group_id,jdbcType=INTEGER}
AND hos_id = #{hos_id,jdbcType=INTEGER}
AND copy_code = #{copy_code,jdbcType=VARCHAR}
AND budg_year = #{budg_year,jdbcType=VARCHAR}
AND adj_code = #{adj_code,jdbcType=VARCHAR}

update.foreach:
UPDATE BUDG_MAT_PUR_ADJ set
	state = #{item.state,jdbcType=VARCHAR} ,
	checker = #{item.checker,jdbcType=INTEGER} ,
	check_date = #{item.check_date,jdbcType=DATE}
where                     
	group_id = #{item.group_id,jdbcType=INTEGER}
	AND hos_id = #{item.hos_id,jdbcType=INTEGER}
	AND copy_code = #{item.copy_code,jdbcType=VARCHAR}
	AND budg_year = #{item.budg_year,jdbcType=VARCHAR}
	AND adj_code = #{item.adj_code,jdbcType=VARCHAR}

mapper.select:
SELECT adj_code  FROM BUDG_MAT_PUR_ADJ
WHERE
	group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=VARCHAR}

	AND adj_code in  (${adj_code})


	and state != #{state,jdbcType=VARCHAR}


select count(0)
from budg_mat_pur_check
WHERE 
	group_id = #{group_id,jdbcType=INTEGER}  
	and hos_id = #{hos_id,jdbcType=INTEGER}   
	and copy_code = #{copy_code,jdbcType=VARCHAR}
	and budg_year = #{budg_year,jdbcType=VARCHAR}   
	and bc_state != '04'


select count(0)
from budg_mat_pur_check
WHERE 
	group_id = #{group_id,jdbcType=INTEGER}  
	and hos_id = #{hos_id,jdbcType=INTEGER}   
	and copy_code = #{copy_code,jdbcType=VARCHAR}
	and budg_year = #{budg_year,jdbcType=VARCHAR}

	with temp as (
     	select max(check_code) as before_adj_code
	    from budg_mat_pur_check
	    WHERE group_id = #{group_id,jdbcType=INTEGER}  
			  and hos_id = #{hos_id,jdbcType=INTEGER}   
			  and copy_code = #{copy_code,jdbcType=VARCHAR}   
	)
	SELECT a.budg_year,a.check_code as before_adj_code,a.issue_data
	FROM budg_mat_pur_check a
	LEFT JOIN temp
	ON a.check_code = temp.before_adj_code
	WHERE a.group_id = #{group_id,jdbcType=INTEGER}  
		  and a.hos_id = #{hos_id,jdbcType=INTEGER}   
		  and a.copy_code = #{copy_code,jdbcType=VARCHAR}   
		  and a.check_code = temp.before_adj_code


SELECT state
FROM BUDG_MAT_PUR_ADJ
WHERE 
	group_id = #{group_id,jdbcType=INTEGER}  
	and hos_id = #{hos_id,jdbcType=INTEGER}   
	and copy_code = #{copy_code,jdbcType=VARCHAR}   
	and adj_code = #{adj_code,jdbcType=VARCHAR}


SELECT max(adj_code) adj_code
FROM BUDG_MAT_PUR_ADJ
WHERE 
	group_id = #{group_id,jdbcType=INTEGER}  
	and hos_id = #{hos_id,jdbcType=INTEGER}   
	and copy_code = #{copy_code,jdbcType=VARCHAR}


SELECT max(check_code)
FROM budg_mat_pur_check
WHERE 
	group_id = #{group_id,jdbcType=INTEGER}  
	and hos_id = #{hos_id,jdbcType=INTEGER}   
	and copy_code = #{copy_code,jdbcType=VARCHAR}

mapper.update:
update   budg_no_manage set max_no = max_no - 1
where
	group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=VARCHAR}
	AND table_code = #{table_code,jdbcType=VARCHAR}
	AND year = #{budg_year,jdbcType=VARCHAR}
