mapper.insert:
INSERT INTO BUDG_WORK_HOS_MONTH ( group_id , hos_id , copy_code , year , index_code , month , count_value , budg_value , remark , grow_rate , resolve_rate , last_year_workload , last_month_carried , carried_next_month
) select BUDG_WORK_HOS_MONTH_SEQ.nextval , t.* from(
select #{item.group_id,jdbcType=INTEGER} , #{item.hos_id,jdbcType=INTEGER} , #{item.copy_code,jdbcType=VARCHAR} , #{item.year,jdbcType=VARCHAR} , #{item.index_code,jdbcType=VARCHAR} , #{item.month,jdbcType=VARCHAR} , #{item.count_value,jdbcType=INTEGER} , #{item.budg_value,jdbcType=INTEGER} , #{item.remark,jdbcType=VARCHAR} , #{item.grow_rate,jdbcType=INTEGER} , #{item.resolve_rate,jdbcType=INTEGER} , #{item.last_year_workload,jdbcType=INTEGER} , #{item.last_month_carried,jdbcType=INTEGER} , #{item.carried_next_month,jdbcType=INTEGER}
from dual
)t

