mapper.select:
select t.* from ( select a.group_id, a.year, a.index_code, b.index_name, '1' as b_no, '上月结转' as item, '' as sum_year, sum(case when month = '01' then nvl(a.last_month_carried,0) else 0 end) as month1, sum(case when month = '02' then nvl(a.last_month_carried,0) else 0 end) as month2, sum(case when month = '03' then nvl(a.last_month_carried,0) else 0 end) as month3, sum(case when month = '04' then nvl(a.last_month_carried,0) else 0 end) as month4, sum(case when month = '05' then nvl(a.last_month_carried,0) else 0 end) as month5, sum(case when month = '06' then nvl(a.last_month_carried,0) else 0 end) as month6, sum(case when month = '07' then nvl(a.last_month_carried,0) else 0 end) as month7, sum(case when month = '08' then nvl(a.last_month_carried,0) else 0 end) as month8, sum(case when month = '09' then nvl(a.last_month_carried,0) else 0 end) as month9, sum(case when month = '10' then nvl(a.last_month_carried,0) else 0 end) as month10, sum(case when month = '11' then nvl(a.last_month_carried,0) else 0 end) as month11, sum(case when month = '12' then nvl(a.last_month_carried,0) else 0 end) as month12 from BUDG_WORK_HOS_month a left join budg_index_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.index_code = b.index_code where a.group_id = ${group_id} AND a.year = ${year} group by a.group_id, a.year, a.index_code, b.index_name
union all
select a.group_id, a.year, a.index_code, b.index_name, '2' as b_no, '预算值' as item, to_char(sum(nvl(budg_value,0))) as sum_year, sum(case when month = '01' then nvl(budg_value,0) else 0 end) as month1, sum(case when month = '02' then nvl(budg_value,0) else 0 end) as month2, sum(case when month = '03' then nvl(budg_value,0) else 0 end) as month3, sum(case when month = '04' then nvl(budg_value,0) else 0 end) as month4, sum(case when month = '05' then nvl(budg_value,0) else 0 end) as month5, sum(case when month = '06' then nvl(budg_value,0) else 0 end) as month6, sum(case when month = '07' then nvl(budg_value,0) else 0 end) as month7, sum(case when month = '08' then nvl(budg_value,0) else 0 end) as month8, sum(case when month = '09' then nvl(budg_value,0) else 0 end) as month9, sum(case when month = '10' then nvl(budg_value,0) else 0 end) as month10, sum(case when month = '11' then nvl(budg_value,0) else 0 end) as month11, sum(case when month = '12' then nvl(budg_value,0) else 0 end) as month12 from BUDG_WORK_HOS_month a left join budg_index_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.index_code = b.index_code where a.group_id = ${group_id} AND a.year = ${year} group by a.group_id, a.year, a.index_code, b.index_name
union all
select a.group_id, a.year, a.index_code, b.index_name, '3' as b_no, '结转下月' as item, '' as sum_year, sum(case when month = '01' then nvl(carried_next_month,0) else 0 end) as month1, sum(case when month = '02' then nvl(carried_next_month,0) else 0 end) as month2, sum(case when month = '03' then nvl(carried_next_month,0) else 0 end) as month3, sum(case when month = '04' then nvl(carried_next_month,0) else 0 end) as month4, sum(case when month = '05' then nvl(carried_next_month,0) else 0 end) as month5, sum(case when month = '06' then nvl(carried_next_month,0) else 0 end) as month6, sum(case when month = '07' then nvl(carried_next_month,0) else 0 end) as month7, sum(case when month = '08' then nvl(carried_next_month,0) else 0 end) as month8, sum(case when month = '09' then nvl(carried_next_month,0) else 0 end) as month9, sum(case when month = '10' then nvl(carried_next_month,0) else 0 end) as month10, sum(case when month = '11' then nvl(carried_next_month,0) else 0 end) as month11, sum(case when month = '12' then nvl(carried_next_month,0) else 0 end) as month12 from BUDG_WORK_HOS_month a left join budg_index_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.index_code = b.index_code where a.group_id = ${group_id} AND a.year = ${year} group by a.group_id, a.year, a.index_code, b.index_name ) t where group_id = #{group_id,jdbcType=INTEGER}
AND year = #{year,jdbcType=VARCHAR}
AND index_code =#{index_code,jdbcType=VARCHAR}
AND month = #{month,jdbcType=VARCHAR}
order by group_id , index_code , b_no


SELECT 
group_id,
hos_id,
copy_code,
year,
index_code,
month,
count_value,
budg_value,
remark,
grow_rate,
resolve_rate,
last_year_workload,
last_month_carried,
carried_next_month
FROM BUDG_WORK_HOS_MONTH 


AND group_id = #{group_id,jdbcType=INTEGER}


AND hos_id = #{hos_id,jdbcType=INTEGER}


AND copy_code = #{copy_code,jdbcType=VARCHAR}


AND year = #{year,jdbcType=VARCHAR}


AND index_code = #{index_code,jdbcType=VARCHAR}


AND month = #{month,jdbcType=VARCHAR}


AND count_value = #{count_value,jdbcType=INTEGER}


AND budg_value = #{budg_value,jdbcType=INTEGER}


AND remark = #{remark,jdbcType=VARCHAR}


AND grow_rate = #{grow_rate,jdbcType=INTEGER}


AND resolve_rate = #{resolve_rate,jdbcType=INTEGER}


AND last_year_workload = #{last_year_workload,jdbcType=INTEGER}


AND last_month_carried = #{last_month_carried,jdbcType=INTEGER}


AND carried_next_month = #{carried_next_month,jdbcType=INTEGER}


order by group_id asc


