mapper.insert:
INSERT INTO COST_PARA_MED_SET ( group_id , hos_id , copy_code , acc_year , acc_month , dept_id , dept_no , server_dept_id , server_dept_no , cost_item_id , cost_item_no , para_code
)
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.acc_year} , #{item.acc_month} , #{item.dept_id} , #{item.dept_no} , #{item.server_dept_id} , #{item.server_dept_no} , #{item.cost_item_id} , #{item.cost_item_no} , #{item.para_code} from dual

mapper.update:

        UPDATE cost_para_med_set SET
			 para_code = #{para_code}

        WHERE 
		 group_id = #{group_id}
			  and 
		 hos_id = #{hos_id}
			  and 
		 copy_code = #{copy_code}
			  and 
		 dept_id = #{dept_id}
			  and 
		 server_dept_id = #{server_dept_id}
			  and 
		 cost_item_id = #{cost_item_id}
		 and 
		 acc_year = #{acc_year}
		 and 
		 acc_month = #{acc_month}

update.foreach:
        UPDATE cost_para_med_set SET
			 para_code = #{para_code}

        WHERE 
		 group_id = #{item.group_id}
			  and 
		 hos_id = #{item.hos_id}
			  and 
		 copy_code = #{item.copy_code}
			  and 
		 dept_id = #{item.dept_id}
			  and 
		 server_dept_id = #{item.server_dept_id}
			  and 
		 cost_item_id = #{item.cost_item_id}
		 and 
		 year_month = #{item.year_month}

mapper.delete:
        DELETE FROM cost_para_med_set WHERE

		 group_id = #{item.group_id}
			  and 
		 hos_id = #{item.hos_id}
			  and 
		 copy_code = #{item.copy_code}
			  and 
		 dept_id = #{item.dept_id}
			  and 
		 server_dept_id = #{item.server_dept_id}
			  and 
		 cost_item_id = #{item.cost_item_id}
		 and 
		 acc_year = #{item.acc_year}
		  and 
		 acc_month = #{item.acc_month}

mapper.select:

SELECT 
           cpms.group_id,
            cpms.hos_id,
            cpms.copy_code,
            cpms.acc_year,
            cpms.acc_month,
            cpms.dept_id,
            cpms.dept_no,
            cpms.server_dept_id,
            cpms.server_dept_no,
            cpms.cost_item_id,
            cpms.cost_item_no,
            cpms.para_code,
            hdd.dept_code as dept_code,
            hdd.dept_name as dept_name,
            shdd.dept_name as server_dept_name,
            shdd.dept_code as server_dept_code,
            cidn.cost_item_code as cost_item_code,
            cidn.cost_item_name as cost_item_name,
            cdpd.para_name
FROM COST_DEPT_PARA_DICT cdpd 
left join  COST_PARA_MED_SET cpms  on cpms.para_code = cdpd.para_code and cpms.group_id = cdpd.group_id and cpms.hos_id = cdpd.hos_id and cpms.copy_code = cpms.copy_code
left join hos_dept_dict hdd on cpms.dept_id = hdd.dept_id 

		and cpms.dept_no = hdd.dept_no 


		and hdd.is_stop=0

and cpms.group_id = hdd.group_id and cpms.hos_id = hdd.hos_id 
left join hos_dept_dict shdd  on cpms.server_dept_id = shdd.dept_id 

		and cpms.server_dept_no = shdd.dept_no


		and shdd.is_stop=0

and cpms.group_id = shdd.group_id and cpms.hos_id = shdd.hos_id 
left join COST_ITEM_DICT_NO cidn on cpms.cost_item_id = cidn.cost_item_id 

		and cpms.cost_item_no = cidn.cost_item_no


		and cidn.is_stop=0

and cpms.group_id = cidn.group_id and cpms.hos_id = cidn.hos_id and cpms.copy_code = cidn.copy_code




                    AND cdpd.group_id = #{group_id}


                    AND cdpd.hos_id = #{hos_id}


                    AND cdpd.copy_code = #{copy_code}


                    AND cpms.dept_id = #{dept_id}


                    AND cpms.dept_no = #{dept_no}


                    AND cpms.server_dept_id = #{server_dept_id}


                    AND cpms.server_dept_no = #{server_dept_no}


                    AND cpms.cost_item_id = #{cost_item_id}


                    AND cpms.cost_item_no = #{cost_item_no}


                    AND cpms.para_code = #{para_code}


                    AND hdd.dept_code = #{dept_code}


                    AND shdd.server_dept_code = #{server_dept_code}


                    AND cidn.cost_item_code = #{cost_item_code}


                    AND cpms.acc_year = #{acc_year}


                    AND cpms.acc_month = #{acc_month}

SELECT cpms.group_id, cpms.hos_id, cpms.copy_code, cpms.acc_year, cpms.acc_month, cpms.dept_id, cpms.dept_no, cpms.server_dept_id, cpms.server_dept_no, cpms.cost_item_id, cpms.cost_item_no, cpms.para_code, hdd.dept_code as dept_code, hdd.dept_name as dept_name, shdd.dept_name as server_dept_name, shdd.dept_code as server_dept_code, cidn.cost_item_code as cost_item_code, cidn.cost_item_name as cost_item_name, cdpd.para_name FROM COST_PARA_MED_SET cpms left join hos_dept_dict hdd on cpms.dept_id = hdd.dept_id
and cpms.dept_no = hdd.dept_no
and hdd.is_stop=0
and cpms.group_id = hdd.group_id and cpms.hos_id = hdd.hos_id left join hos_dept_dict shdd on cpms.server_dept_id = shdd.dept_id
and cpms.server_dept_no = shdd.dept_no
and shdd.is_stop=0
and cpms.group_id = shdd.group_id and cpms.hos_id = shdd.hos_id left join COST_ITEM_DICT_NO cidn on cpms.cost_item_id = cidn.cost_item_id
and cpms.cost_item_no = cidn.cost_item_no
and cidn.is_stop=0
and cpms.group_id = cidn.group_id and cpms.hos_id = cidn.hos_id and cpms.copy_code = cidn.copy_code left join COST_DEPT_PARA_DICT cdpd on cpms.para_code = cdpd.para_code and cpms.group_id = cdpd.group_id and cpms.hos_id = cdpd.hos_id and cpms.copy_code = cdpd.copy_code
AND cpms.group_id = #{group_id}
AND cpms.hos_id = #{hos_id}
AND cpms.copy_code = #{copy_code}
AND cpms.dept_id = #{dept_id}
AND cpms.dept_no = #{dept_no}
AND cpms.server_dept_id = #{server_dept_id}
AND cpms.server_dept_no = #{server_dept_no}
AND cpms.cost_item_id = #{cost_item_id}
AND cpms.cost_item_no = #{cost_item_no}
AND cpms.para_code = #{para_code}
AND hdd.dept_code = #{dept_code}
AND shdd.server_dept_code = #{server_dept_code}
AND cidn.cost_item_code = #{cost_item_code}
AND cpms.acc_year ||''|| cpms.acc_month between #{year_month} and #{e_year_month}
order by cpms.group_id asc
