SELECT crd.group_id, crd.hos_id, crd.copy_code, crd.acc_year, crd.acc_month, crd.dept_id, crd.dept_no, hdd.dept_code, hdd.dept_name, crd.cost_item_id, crd.cost_item_no, cidn.cost_item_code, cidn.cost_item_name, crd.source_id, hs.source_code, hs.source_name, crd.amount FROM COST_RISK_DETAIL crd left join hos_dept_dict hdd on crd.group_id = hdd.group_id and crd.hos_id = hdd.hos_id and crd.dept_id = hdd.dept_id
and crd.dept_no = hdd.dept_no
left join COST_ITEM_DICT_NO cidn on crd.cost_item_id = cidn.cost_item_id
and crd.cost_item_no = cidn.cost_item_no
and crd.group_id = cidn.group_id and crd.hos_id = cidn.hos_id and crd.copy_code = cidn.copy_code left join HOS_SOURCE hs on crd.group_id = hs.group_id and crd.hos_id = hs.hos_id and crd.source_id = hs.source_id
and hdd.is_stop = '0' and cidn.is_stop = '0'
and hs.is_stop='0'
AND crd.group_id = #{group_id}
AND crd.hos_id = #{hos_id}
AND crd.copy_code = #{copy_code}
AND crd.acc_year || '' || crd.acc_month = #{year_month}
AND crd.acc_year = #{acc_year}
AND crd.acc_month = #{acc_month}
AND crd.dept_id = #{dept_id}
AND crd.dept_no = #{dept_no}
AND crd.cost_item_id = #{cost_item_id}
AND crd.cost_item_no = #{cost_item_no}
AND crd.source_id = #{source_id}
AND crd.amount = #{amount}

SELECT crd.group_id, crd.hos_id, crd.copy_code, crd.acc_year, crd.acc_month, crd.acc_year||crd.acc_month as year_month, crd.dept_id, crd.dept_no, hdd.dept_code, hdd.dept_name, crd.cost_item_id, crd.cost_item_no, cidn.cost_item_code, cidn.cost_item_name, crd.source_id, hs.source_code, hs.source_name, crd.amount FROM COST_RISK_DETAIL crd left join hos_dept_dict hdd on crd.group_id = hdd.group_id and crd.hos_id = hdd.hos_id and crd.dept_id = hdd.dept_id
and crd.dept_no = hdd.dept_no
and hdd.is_last=${is_last}
left join COST_ITEM_DICT_NO cidn on crd.cost_item_id = cidn.cost_item_id
and crd.cost_item_no = cidn.cost_item_no
and crd.group_id = cidn.group_id and crd.hos_id = cidn.hos_id and crd.copy_code = cidn.copy_code left join HOS_SOURCE hs on crd.group_id = hs.group_id and crd.hos_id = hs.hos_id and crd.source_id = hs.source_id
and hdd.is_stop = '0' and cidn.is_stop = '0'
and hs.is_stop='0'
AND crd.group_id = #{group_id}
AND crd.hos_id = #{hos_id}
AND crd.copy_code = #{copy_code}
AND crd.acc_year ||''|| crd.acc_month between #{b_year_month} and #{e_year_month}
AND crd.dept_id = #{dept_id}
AND crd.dept_no = #{dept_no}
AND crd.cost_item_id = #{cost_item_id}
AND crd.cost_item_no = #{cost_item_no}
AND crd.source_id = #{source_id}
AND crd.amount = #{amount}
order by crd.acc_year,crd.acc_month,hdd.dept_code,cidn.cost_item_code

SELECT crd.group_id, crd.hos_id, crd.copy_code, crd.acc_year, crd.acc_month, crd.acc_year||crd.acc_month as year_month, crd.dept_id, crd.dept_no, hdd.dept_code, hdd.dept_name, crd.cost_item_id, crd.cost_item_no, cidn.cost_item_code, cidn.cost_item_name, crd.source_id, hs.source_code, hs.source_name, crd.amount FROM COST_RISK_DETAIL crd left join hos_dept_dict hdd on crd.group_id = hdd.group_id and crd.hos_id = hdd.hos_id and crd.dept_id = hdd.dept_id
and crd.dept_no = hdd.dept_no
and hdd.is_last=${is_last}
left join COST_ITEM_DICT_NO cidn on crd.cost_item_id = cidn.cost_item_id
and crd.cost_item_no = cidn.cost_item_no
and crd.group_id = cidn.group_id and crd.hos_id = cidn.hos_id and crd.copy_code = cidn.copy_code left join HOS_SOURCE hs on crd.group_id = hs.group_id and crd.hos_id = hs.hos_id and crd.source_id = hs.source_id
and hdd.is_stop = '0' and cidn.is_stop = '0'
and hs.is_stop='0'
AND crd.group_id = #{group_id}
AND crd.hos_id = #{hos_id}
AND crd.copy_code = #{copy_code}
AND crd.acc_year ||''|| crd.acc_month between #{b_year_month} and #{e_year_month}
AND crd.dept_id = #{dept_id}
AND crd.dept_no = #{dept_no}
AND crd.cost_item_id = #{cost_item_id}
AND crd.cost_item_no = #{cost_item_no}
AND crd.source_id = #{source_id}
AND crd.amount = #{amount}
order by crd.acc_year,crd.acc_month,hdd.dept_code,cidn.cost_item_code


        SELECT 
            group_id,
            hos_id,
            source_id,
            source_code,
            source_name
        FROM HOS_SOURCE 


                    AND group_id = #{group_id}


                    AND hos_id = #{hos_id}


