mapper.insert:
INSERT INTO ASS_OEQ_USED_RESOURCE ( group_id , hos_id , copy_code , year , month , analysis_code , oresource_code , price , unit_code , quantity , amount , remark , is_input_flag , sale_amount , add_date , add_time , add_user , status
) select t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.year} , #{item.month} , #{item.analysis_code} , #{item.oresource_code} , #{item.price} , #{item.unit_code} , #{item.quantity} , #{item.amount} , #{item.remark} , #{item.is_input_flag} ,
#{item.sale_amount} ,
null ,
#{item.add_date} , #{item.add_time} , #{item.add_user} , #{item.status}
from dual
)t

mapper.update:

UPDATE ASS_OEQ_USED_RESOURCE SET
	price = #{price}
	,
	unit_code = #{unit_code}
	,
	quantity = #{quantity}
	,
	amount = #{amount}
	,
	remark = #{remark}
	,
	is_input_flag = #{is_input_flag}
	,

	sale_amount = #{sale_amount}
	,


	add_date = #{add_date}
	,



	add_time = #{add_time}
	,


	add_user = #{add_user}
	,


	update_date = #{update_date}
	,


	update_time = #{update_time}
	,


	update_user = #{update_user}
	,

	status = #{status}

WHERE 
	group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=VARCHAR}
	AND year = #{year,jdbcType=VARCHAR}
	AND month = #{month,jdbcType=VARCHAR}
	AND analysis_code = #{analysis_code,jdbcType=VARCHAR}
	AND oresource_code = #{oresource_code,jdbcType=VARCHAR}

