		AND analysis_code = #{item.analysis_code,jdbcType=VARCHAR}
		AND oresource_code = #{item.oresource_code,jdbcType=VARCHAR}

mapper.select:
SELECT a.group_id , a.hos_id , a.copy_code , a.year , a.month , a.month||'月' month_name , a.analysis_code , t.analysis_name , a.oresource_code, d.oresource_desc oresource_code_name , a.price, a.unit_code, e.unit_name , a.quantity, a.amount, a.remark, a.is_input_flag, a.sale_amount, a.add_date, a.add_time, a.add_user, a.update_date, a.update_time, a.update_user, a.status, case a.status when '0' then '新建' when '1' then '提交' when '2' then '审核' else '作废' end status_name , a.submit_date, a.submit_time, a.submit_user, a.audit_date, a.audit_time, a.audit_user, a.cancel_date, a.cancel_time, a.cancel_user FROM ASS_OEQ_USED_RESOURCE a left join ASS_BENEFIT_ANALYSIS_OBJECT t on a.group_id = t.group_id and a.hos_id = t.hos_id and a.copy_code = t.copy_code and a.analysis_code = t.analysis_code left join ASS_OTHER_RESOURCE_TYPE d on a.group_id = d.group_id and a.hos_id = d.hos_id and a.copy_code = d.copy_code and a.oresource_code = d.oresource_code left join hos_unit e on a.group_id = e.group_id and a.hos_id = e.hos_id and a.unit_code = e.unit_code and e.is_stop = 0 where a.group_id = #{group_id} AND a.hos_id = #{hos_id} AND a.copy_code = #{copy_code}
AND a.year = #{year}
AND a.month = #{month}
AND a.year||a.month between #{startyear} and #{endyear}
AND a.analysis_code = #{analysis_code}
AND a.oresource_code = #{oresource_code}
AND a.price = #{price}
AND a.unit_code = #{unit_code}
AND a.quantity = #{quantity}
AND a.amount = #{amount}
AND a.remark like '%' || #{remark} || '%'
order by a.year,a.month asc

select.foreach:
SELECT to_number(${item.rowNo})+ 1 rowNo
FROM ASS_OEQ_USED_RESOURCE
where
	group_id = #{item.group_id,jdbcType=INTEGER}
	AND hos_id = #{item.hos_id,jdbcType=INTEGER}
	AND copy_code = #{item.copy_code,jdbcType=VARCHAR}
	AND year = #{item.year,jdbcType=VARCHAR}
	AND month = #{item.month,jdbcType=VARCHAR}
	AND analysis_code = #{item.analysis_code,jdbcType=VARCHAR}
	AND oresource_code = #{item.oresource_code,jdbcType=VARCHAR}
