mapper.insert:
INSERT INTO aphi_TARGET_NATURE ( group_id , hos_id , copy_code , nature_code , nature_name , spell_code , wbx_code , is_stop
) select aphi_TARGET_NATURE_SEQ.nextval , t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.nature_code} , #{item.nature_name} , #{item.spell_code} , #{item.wbx_code} , #{item.is_stop}
)t

mapper.update:

UPDATE aphi_target_nature SET
nature_name = #{nature_name}
,
spell_code = #{spell_code}
,
wbx_code = #{wbx_code}
,
is_stop = #{is_stop}

WHERE 
group_id = #{group_id}
 and 
hos_id = #{hos_id}
 and 
copy_code = #{copy_code}
 and 
nature_code = #{nature_code}

update.foreach:
UPDATE aphi_target_nature SET
nature_name = #{nature_name}
,
spell_code = #{spell_code}
,
wbx_code = #{wbx_code}
,
is_stop = #{is_stop}

WHERE 
group_id = #{item.group_id}
 and 
hos_id = #{item.hos_id}
 and 
copy_code = #{item.copy_code}
 and 
nature_code = #{item.nature_code}

mapper.delete:
DELETE FROM aphi_target_nature WHERE

		group_id = #{item.group_id}
		 and 
		hos_id = #{item.hos_id}
		 and 
		copy_code = #{item.copy_code}
		 and 
		nature_code = #{item.nature_code}

mapper.select:

SELECT 
group_id,
hos_id,
copy_code,
nature_code,
nature_name,
spell_code,
wbx_code,
is_stop
FROM aphi_TARGET_NATURE 


AND group_id = #{group_id}


AND hos_id = #{hos_id}


AND copy_code = #{copy_code}


AND nature_code = #{nature_code}


AND nature_name = #{nature_name}


AND spell_code = #{spell_code}


AND wbx_code = #{wbx_code}


AND is_stop = #{is_stop}


order by group_id asc
