mapper.insert:
INSERT INTO Hr_FUN_TYPE ( group_id , hos_id , copy_code , type_code , type_name , spell_code , wbx_code , is_stop
) select Hr_FUN_TYPE_SEQ.nextval , t.* from(
select #{item.group_id} , #{item.hos_id} , #{item.copy_code} , #{item.type_code} , #{item.type_name} , #{item.spell_code} , #{item.wbx_code} , #{item.is_stop}
)t

mapper.update:

UPDATE Hr_fun_type SET
type_name = #{type_name}
,
spell_code = #{spell_code}
,
wbx_code = #{wbx_code}
,
is_stop = #{is_stop}

WHERE 
group_id = #{group_id}
 and 
hos_id = #{hos_id}
 and 
copy_code = #{copy_code}
 and 
type_code = #{type_code}

update.foreach:
UPDATE Hr_fun_type SET
type_name = #{type_name}
,
spell_code = #{spell_code}
,
wbx_code = #{wbx_code}
,
is_stop = #{is_stop}

WHERE 
group_id = #{item.group_id}
 and 
hos_id = #{item.hos_id}
 and 
copy_code = #{item.copy_code}
 and 
type_code = #{item.type_code}

mapper.delete:
DELETE FROM Hr_fun_type WHERE

		group_id = #{item.group_id}
		and hos_id = #{item.hos_id}
		and copy_code = #{item.copy_code}
		and type_code = #{item.type_code}

mapper.select:

select group_id, hos_id, copy_code, type_code, type_name
, spell_code, wbx_code, is_stop ,case when is_stop = 0 then '否'
	when is_stop = 1 then '是'
	else null
end as is_stop_name
	FROM Hr_FUN_TYPE 


			AND group_id = #{group_id}


			AND hos_id = #{hos_id}


			AND copy_code = #{copy_code}


			AND type_code = #{type_code}


			AND type_name = #{type_name}


			AND spell_code = #{spell_code}


			AND wbx_code = #{wbx_code}


			AND is_stop = #{is_stop}


	order by type_code

SELECT group_id, hos_id, copy_code, type_code, type_name, spell_code, wbx_code, is_stop FROM Hr_fun_type WHERE group_id = #{group_id} and hos_id = #{hos_id} and copy_code = #{copy_code}
and type_name = #{type_name}
and (type_code = #{type_code} or type_name = #{type_name})
