mapper.update:
update htc_dept_rela set 
proj_dept_id = #{proj_dept_id} 
where group_id = #{group_id}
and hos_id = #{hos_id} 
and copy_code = #{copy_code}
and acc_year = #{acc_year}
and plan_code = #{plan_code}
and dept_id = #{dept_id}

mapper.delete:

        delete from htc_dept_rela where 

		(
			    group_id = #{item.group_id}
	        and hos_id = #{item.hos_id} 
	        and copy_code = #{item.copy_code}
	        and acc_year = #{item.acc_year}
	        and plan_code = #{item.plan_code}
	        and dept_id = #{item.dept_id} 
		)

mapper.select:
select hdr.group_id, hdr.hos_id, hdr.copy_code, hdr.acc_year, hdr.plan_code, hps.plan_name, hdr.dept_id, hdd.dept_code, hdd.dept_name, hdr.proj_dept_id, hpdd.proj_dept_code, hpdd.proj_dept_name from htc_dept_rela hdr left join htc_plan_set hps on hdr.group_id = hps.group_id and hdr.hos_id = hps.hos_id and hdr.copy_code = hps.copy_code and hdr.acc_year = hps.acc_year and hdr.plan_code = hps.plan_code left join hos_dept_dict hdd on hdd.group_id = hdr.group_id and hdd.hos_id = hdr.hos_id and hdd.dept_id = hdr.dept_id left join htc_proj_dept_dict hpdd on hpdd.group_id = hdr.group_id and hpdd.hos_id = hdr.hos_id and hpdd.proj_dept_id = hdr.proj_dept_id where hdr.group_id = #{group_id} and hdr.hos_id = #{hos_id} and hdr.copy_code = #{copy_code} and hdr.acc_year = #{acc_year} and hdd.is_stop = 0 and hpdd.is_disable = 0
and hdr.plan_code = #{plan_code}
and hdr.dept_id = #{dept_id}
and hdr.proj_dept_id = #{proj_dept_id}
order by hdr.group_id, hdr.hos_id,hdr.copy_code, hdr.acc_year,hdr.plan_code,hdd.dept_code,hpdd.proj_dept_code
