mapper.select:

		    {call pkg_htc_app.prc_relative_charge_hos_cost(
			#{group_id,jdbcType=INTEGER, mode=IN},  
	        #{hos_id,jdbcType=INTEGER, mode=IN},  
	        #{copy_code,jdbcType=VARCHAR, mode=IN},  
	        #{acc_year,jdbcType=VARCHAR, mode=IN},
	        #{plan_code,jdbcType=VARCHAR, mode=IN},
	        #{err_txt,jdbcType=VARCHAR,mode=OUT}
           )}

select hrcch.group_id, hrcch.hos_id, hrcch.copy_code, hrcch.acc_year, hrcch.plan_code, hps.plan_name, hrcch.charge_item_id, ccia.charge_item_code, ccia.charge_item_name, hrcch.charge_num, hrcch.charge_money, hrcch.dir_cost, hrcch.pub_cost, hrcch.man_cost, hrcch.mea_cost, hrcch.source_id, hs.source_code, hs.source_name from htc_relative_charge_cost_hos hrcch left join htc_plan_set hps on hrcch.group_id = hps.group_id and hrcch.hos_id = hps.hos_id and hrcch.copy_code = hps.copy_code and hrcch.acc_year = hps.acc_year and hrcch.plan_code = hps.plan_code left join cost_charge_item_arrt ccia on ccia.group_id = hrcch.group_id and ccia.hos_id = hrcch.hos_id and ccia.copy_code = hrcch.copy_code and ccia.charge_item_id = hrcch.charge_item_id left join hos_source hs on hrcch.group_id = hs.group_id and hrcch.hos_id = hs.hos_id and hrcch.source_id = hs.source_id
and hrcch.plan_code = #{plan_code}
order by hrcch.group_id, hrcch.hos_id, hrcch.copy_code, hrcch.acc_year, hrcch.plan_code, ccia.charge_item_code
