mapper.delete:
     delete from htc_plan_set where

	       group_id = #{item.group_id}
	   and hos_id = #{item.hos_id}
	   and copy_code = #{item.copy_code}
	   and acc_year = #{item.acc_year}
	   and plan_code = #{item.plan_code}

mapper.update:
     update htc_plan_set set 
           plan_name = #{plan_name},
	       start_month = #{start_month},
	       end_month = #{end_month},
	       method = #{method}
	 where group_id = #{group_id}
	   and hos_id = #{hos_id}
	   and copy_code = #{copy_code}
	   and acc_year = #{acc_year}
	   and plan_code = #{plan_code}

mapper.select:
select group_id, hos_id, copy_code, acc_year, plan_code, plan_name, start_month, end_month, is_check, method, is_current from htc_plan_set where group_id = #{group_id} and hos_id = #{hos_id} and copy_code = #{copy_code} and acc_year = #{acc_year}
and plan_code like '%'||#{plan_code}||'%'
order by group_id,hos_id,copy_code,acc_year,plan_code

select group_id, hos_id, copy_code, acc_year, plan_code, plan_name, start_month, end_month, is_check, method, is_current from htc_plan_set where group_id = #{group_id} and hos_id = #{hos_id} and copy_code = #{copy_code} and acc_year = #{acc_year}
and plan_code like '%'||#{plan_code}||'%'
order by group_id,hos_id,copy_code,acc_year,plan_code

update.foreach:
update htc_plan_set set
	is_check = 1
where  group_id = #{item.group_id}
   and hos_id = #{item.hos_id}
   and copy_code = #{item.copy_code}
   and acc_year = #{item.acc_year}
   and plan_code = #{item.plan_code}

update htc_plan_set set
	is_check = 0
where  group_id = #{item.group_id}
   and hos_id = #{item.hos_id}
   and copy_code = #{item.copy_code}
   and acc_year = #{item.acc_year}
   and plan_code = #{item.plan_code}
