mapper.select:
select hdi.group_id, hdi.hos_id, hdi.copy_code, decode(grouping(hdi.acc_year), 1, '合', hdi.acc_year) acc_year, decode(grouping(hdi.acc_month), 1, '计', hdi.acc_month) acc_month, hdi.plan_code, hps.plan_name, hdi.appl_dept_id, hdi.appl_dept_no, appl_hpdd.proj_dept_code appl_dept_code, appl_hpdd.proj_dept_name appl_dept_name, hdi.exec_dept_id, hdi.exec_dept_no, exec_hpdd.proj_dept_code exec_dept_code, exec_hpdd.proj_dept_name exec_dept_name, hdi.charge_item_id, ccia.charge_item_code, ccia.charge_item_name, sum(hdi.num) num, sum(hdi.money) money from htc_dept_income hdi left join htc_plan_set hps on hdi.group_id = hps.group_id and hdi.hos_id = hps.hos_id and hdi.copy_code = hps.copy_code and hdi.plan_code = hps.plan_code and hdi.acc_year = hps.acc_year left join htc_proj_dept_dict appl_hpdd on appl_hpdd.group_id = hdi.group_id and appl_hpdd.hos_id = hdi.hos_id and appl_hpdd.copy_code = hdi.copy_code and appl_hpdd.proj_dept_id = hdi.appl_dept_id left join htc_proj_dept_dict exec_hpdd on exec_hpdd.group_id = hdi.group_id and exec_hpdd.hos_id = hdi.hos_id and exec_hpdd.copy_code = hdi.copy_code and exec_hpdd.proj_dept_id = hdi.exec_dept_id left join cost_charge_item_arrt ccia on ccia.group_id = hdi.group_id and ccia.hos_id = hdi.hos_id and ccia.copy_code = hdi.copy_code and ccia.charge_item_id = hdi.charge_item_id where hdi.group_id = #{group_id} and hdi.hos_id = #{hos_id} and hdi.copy_code = #{copy_code} and appl_hpdd.is_disable = 0 and exec_hpdd.is_disable = 0 and hdi.acc_year || hdi.acc_month between #{start_month} and #{end_month}
and hdi.appl_dept_id = #{appl_dept_id}
and hdi.exec_dept_id = #{exec_dept_id}
and hdi.charge_item_id = #{charge_item_id}
group by rollup((hdi.group_id, hdi.hos_id, hdi.copy_code, hdi.acc_year, hdi.acc_month, hdi.plan_code, hps.plan_name, hdi.appl_dept_id, hdi.appl_dept_no, appl_hpdd.proj_dept_code, appl_hpdd.proj_dept_name, hdi.exec_dept_id, hdi.exec_dept_no, exec_hpdd.proj_dept_code, exec_hpdd.proj_dept_name, hdi.charge_item_id, ccia.charge_item_code, ccia.charge_item_name)) order by hdi.group_id,hdi.hos_id,hdi.copy_code,hdi.acc_year,hdi.acc_month,hdi.plan_code,appl_hpdd.proj_dept_code,exec_hpdd.proj_dept_code
