mapper.select:
		SELECT 
			group_id, 
			hos_id, 
			copy_code,
			year, 
			month, 
			inv_id, 
			store_id, 
			location_id,
			batch_sn, 
			batch_no, 
			bar_code,  
      		nvl(price,0) price, 
      		nvl(begin_amount,0) begin_amount, 
      		nvl(begin_money,0) begin_money, 
      		nvl(in_amount,0) in_amount,  
      		nvl(in_money,0) in_money, 
      		nvl(out_amount,0) out_amount, 
      		nvl(out_money,0) out_money, 
      		nvl(y_in_amount,0) y_in_amount, 
      		nvl(y_in_money,0) y_in_money, 
      		nvl(y_out_amount,0) y_out_amount, 
      		nvl(y_out_money,0) y_out_money, 
      		nvl(remove_zero_error,0)  remove_zero_error
		FROM mat_affi_batch


				and group_id = #{group_id,jdbcType=INTEGER}


				and copy_code = #{copy_code,jdbcType=VARCHAR}


				and hos_id = #{hos_id,jdbcType=INTEGER}


				and year = #{year,jdbcType=VARCHAR}


				and month = #{month,jdbcType=VARCHAR}	


				and inv_id = #{inv_id,jdbcType=INTEGER}


				and store_id = #{store_id,jdbcType=INTEGER}	


				and location_id = #{location_id,jdbcType=INTEGER}	


				and batch_sn = #{batch_sn,jdbcType=INTEGER}


				and batch_no = #{batch_no,jdbcType=VARCHAR}	


				and bar_code = #{bar_code,jdbcType=VARCHAR}

with list_temp as (
select #{item.group_id,jdbcType=INTEGER} as group_id, #{item.hos_id,jdbcType=INTEGER} as hos_id, #{item.copy_code,jdbcType=VARCHAR} as copy_code, #{item.year,jdbcType=VARCHAR} as year, #{item.month,jdbcType=VARCHAR} as month, #{item.store_id,jdbcType=INTEGER} as store_id, #{item.inv_id,jdbcType=INTEGER} as inv_id, #{item.price,jdbcType=FLOAT} as price, #{item.batch_no,jdbcType=INTEGER} as batch_no, #{item.batch_sn,jdbcType=INTEGER} as batch_sn, #{item.bar_code,jdbcType=INTEGER} as bar_code, #{item.location_id,jdbcType=INTEGER} as location_id from dual
) select a.group_id, a.hos_id, a.copy_code, a.year, a.month, a.store_id, a.inv_id, a.batch_no, a.batch_sn, a.bar_code, nvl(b.price, 0) as price, nvl(b.sale_price, 0) as sale_price, nvl(b.begin_amount, 0) as begin_amount, nvl(b.begin_money, 0) as begin_money, nvl(b.begin_sale_money, 0) as begin_sale_money, nvl(b.in_amount, 0) as in_amount, nvl(b.in_money, 0) as in_money, nvl(b.in_sale_money, 0) as in_sale_money, nvl(b.out_amount, 0) as out_amount, nvl(b.out_money, 0) as out_money, nvl(b.out_sale_money, 0) as out_sale_money, nvl(b.y_in_amount, 0) as y_in_amount, nvl(b.y_in_money, 0) as y_in_money, nvl(b.y_in_sale_money, 0) as y_in_sale_money, nvl(b.y_out_amount, 0) as y_out_amount, nvl(b.y_out_money, 0) as y_out_money, nvl(b.y_out_sale_money, 0) as y_out_sale_money, nvl(sale_zero_error, 0) as sale_zero_error, nvl(b.location_id, 0) as location_id from list_temp a inner join mat_affi_batch b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.year = b.year and a.month = b.month and a.store_id = b.store_id and a.inv_id = b.inv_id and a.price = b.price and a.batch_no = b.batch_no and a.batch_sn = b.batch_sn and a.bar_code = b.bar_code and nvl(a.location_id, 0) = nvl(b.location_id, 0)
