mapper.select:
SELECT a.group_id, a.hos_id, a.copy_code, a.in_id, a.in_no, a.in_date, a.brief, a.store_id, a.store_no, c.store_name, a.bus_type_code, d.bus_type_name, e.user_name as maker_name, f.user_name as confirmer_name, a.confirm_date, sum(b.amount_money) as amount_money, a.state, g.field_desc as state_name, a.sup_id, a.sup_no, h.sup_name FROM MAT_AFFI_IN a LEFT JOIN MAT_AFFI_IN_DETAIL b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.in_id = b.in_id LEFT JOIN hos_store_dict c on a.group_id = c.group_id and a.hos_id = c.hos_id and a.store_id = c.store_id
and a.store_no = c.store_no
and c.is_stop = 0
LEFT JOIN mat_bus_type d on a.bus_type_code = d.bus_type_code and d.is_stop = 0 LEFT JOIN sys_user e on a.group_id = e.group_id and a.hos_id = e.hos_id and a.maker = e.user_id LEFT JOIN sys_user f on a.group_id = f.group_id and a.hos_id = f.hos_id and a.confirmer = f.user_id LEFT JOIN mat_sys_list g on g.table_code = 'MAT_IN_MAIN' and g.field_code = 'STATE' and a.state = g.field_value LEFT JOIN hos_sup_dict h on a.group_id = h.group_id and a.hos_id = h.hos_id and a.sup_id = h.sup_id
and a.sup_no = h.sup_no
and h.is_stop = 0
a.group_id = #{group_id,jdbcType=INTEGER} AND a.hos_id = #{hos_id,jdbcType=INTEGER} AND a.copy_code = #{copy_code,jdbcType=VARCHAR} and a.bus_type_code = 1
AND a.in_date >= #{begin_date,jdbcType=DATE}
AND a.in_date <= #{end_date,jdbcType=DATE}
AND a.store_id = #{store_id,jdbcType=VARCHAR}
AND a.sup_id = #{sup_id,jdbcType=VARCHAR}
AND a.state = #{state,jdbcType=VARCHAR}
group by a.group_id, a.hos_id, a.copy_code, a.in_id, a.in_no, a.in_date, brief, a.store_id, a.store_no, c.store_name, a. bus_type_code, d.bus_type_name, e.user_name, f.user_name, a.state, g.field_desc, a.sup_id, a.sup_no, h.sup_name, a.confirm_date order by in_id desc

SELECT 
	inv_id, inv_no, inv_code, inv_name, is_bar, is_quality, is_disinfect
FROM mat_inv_dict
WHERE group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=INTEGER}
	AND is_stop = 0 AND is_com = 1

SELECT 
	location_id, location_code, location_name
FROM mat_location_dict
WHERE group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=INTEGER}
	AND is_stop = 0

select d.in_id as id, i.INV_CODE,i.INV_NAME,i.INV_MODEL,u.UNIT_NAME,d.PRICE,d.AMOUNT,d.AMOUNT_MONEY,d.ALLOT_MONEY, f.FAC_CODE FAC_CODE,f.FAC_NAME,d.NOTE, p.PACK_NAME,d.NUM_EXCHANGE,d.NUM,d.BATCH_SN,d.BATCH_NO,d.SN,d.BAR_CODE, to_char(d.INVA_DATE, 'yyyy-MM-dd') inva_date, ld.LOCATION_CODE,ld.LOCATION_NAME, to_char(d.DISINFECT_DATE, 'yyyy-MM-dd') disinfect_date,d.SALE_PRICE,d.SALE_MONEY,d.SELL_PRICE, d.SELL_MONEY,d.ALLOT_PRICE,d.FAC_DATE,d.serial_no,d.note from mat_affi_in_detail d left join mat_inv_dict i on d.group_id=i.group_id and d.hos_id=i.hos_id and d.copy_code=i.copy_code and d.inv_id=i.inv_id and d.inv_no=i.inv_no left JOIN mat_location_dict ld ON d.group_id = ld.group_id AND d.hos_id = ld.hos_id AND d.copy_code = ld.copy_code AND d.location_id = ld.location_id left join hos_fac_dict f on d.group_id = f.group_id AND d.hos_id = f.hos_id and i.fac_id=f.fac_id and i.fac_no=f.fac_no left join hos_unit u on d.group_id=u.group_id and d.hos_id=u.hos_id and i.unit_code=u.unit_code left join hos_package p on d.group_id=p.group_id and d.hos_id=p.hos_id and d.pack_code=p.pack_code where d.group_id = #{group_id,jdbcType=NUMERIC} and d.hos_id = #{hos_id,jdbcType=NUMERIC} and d.copy_code = #{copy_code,jdbcType=VARCHAR}
and d.in_id in (${paraId})
and d.in_id=#{${paraId},jdbcType=NUMERIC}
