mapper.select:
SELECT mpm.group_id, mpm.hos_id, mpm.copy_code, mpm.pur_id, mpm.pur_code, hdd.dept_name, mpm.brif, msl.field_desc as plan_type, pur.hos_name as pur_hos_name, req.hos_name as req_hos_name, pay.hos_name as pay_hos_name, su.user_name as maker, mpm.make_date, sus.user_name as checker, mpm.check_date, mpm.state, mpm.is_dir, mpm.arrive_date, mpm.dir_dept_id, mpm.dir_dept_no, hddd.dept_code dir_dept_code, hddd.dept_name dir_dept_name FROM MAT_PUR_MAIN mpm LEFT JOIN HOS_DEPT_DICT hdd ON mpm.group_id = hdd.group_id and mpm.hos_id = hdd.hos_id and mpm.dept_id = hdd.dept_id
and hdd.dept_no = mpm.dept_no
and hdd.is_stop = 0
LEFT JOIN MAT_SYS_LIST msl ON mpm.pur_type = msl.field_value AND msl.field_code = 'PUR_TYPE' left join v_hos_dict pur on pur.hos_id = mpm.pur_hos_id and pur.group_id = mpm.group_id and pur.is_stop = 0 left join v_hos_dict req on req.group_id = mpm.group_id and mpm.req_hos_id = req.hos_id and req.is_stop = 0 left join v_hos_dict pay on mpm.group_id = pay.group_id and mpm.pay_hos_id = pay.hos_id and pay.is_stop = 0 LEFT JOIN SYS_USER su ON mpm.group_id = su.group_id AND mpm.hos_id = su.hos_id AND mpm.copy_code = su.copy_code AND mpm.maker = su.user_id LEFT JOIN SYS_USER sus ON mpm.group_id = sus.group_id AND mpm.hos_id = sus.hos_id AND mpm.copy_code = sus.copy_code AND mpm.checker = sus.user_id left join hos_dept_dict hddd on hddd.dept_id = mpm.dir_dept_id and hddd.group_id = mpm.group_id and hddd.hos_id = mpm.hos_id
and hddd.dept_no = mpm.dir_dept_no
and hddd.is_stop = 0
WHERE mpm.group_id = #{group_id} AND mpm.hos_id = #{hos_id} AND mpm.copy_code = #{copy_code}
AND mpm.make_date >= to_date(#{begin_date},'yyyy/MM/dd')
AND mpm.make_date <= to_date(#{end_date},'yyyy/MM/dd')
AND mpm.dept_id = #{dept_id}
AND mpm.state = #{state}
AND mpm.pur_type = #{pur_type}
AND mpm.pur_code like '${pur_code}%' or Upper( mpm.pur_code) like '${pur_code}%' or lower( mpm.pur_code) like '${pur_code}%'
AND mpm.pur_id = #{pur_id}
AND mpm.brif like '%${brif}%'
AND mpm.is_dir = #{is_dir}
AND mpm.req_hos_id = #{req_hos_id}
AND mpm.pur_hos_id = #{pur_hos_id}
AND mpm.dir_dept_id = #{dir_dept_id}
AND mpm.dir_dept_no = #{dir_dept_no}
AND mpm.is_collect=0 order by mpm.pur_id desc

update.foreach:
UPDATE MAT_PUR_MAIN

	state = 2,
	checker = #{item.checker},
	check_date =
	to_date(#{item.check_date},'yyyy/MM/dd')


	group_id = #{item.group_id}
	and hos_id = #{item.hos_id}
	and
	copy_code = #{item.copy_code}
	and pur_id = #{item.pur_id}
	and state =
	#{item.state}

mapper.update:
UPDATE MAT_PUR_MAIN SET
state = 1,
checker='',
check_date = ''
where

	group_id = #{item.group_id}
	and hos_id = #{item.hos_id}
	and copy_code = #{item.copy_code}
	and pur_id = #{item.pur_id}
	and state = #{item.state}
