mapper.insert:
INSERT INTO MAT_OUT_MAIN (
group_id, hos_id, copy_code, out_id, out_no, year, month, bus_type_code, store_id, store_no, brief, dept_id, dept_no, dept_emp, use_code, proj_id, maker, out_date, checker, check_date, confirmer, confirm_date, state, is_dir, his_flag, app_date, amount_money,
) VALUES (
#{group_id,jdbcType=INTEGER}, #{hos_id,jdbcType=INTEGER}, #{copy_code,jdbcType=VARCHAR}, #{out_id,jdbcType=INTEGER}, #{out_no,jdbcType=VARCHAR}, #{year,jdbcType=VARCHAR}, #{month,jdbcType=VARCHAR}, #{bus_type_code,jdbcType=VARCHAR}, #{store_id,jdbcType=INTEGER}, #{store_no,jdbcType=INTEGER}, #{brief,jdbcType=VARCHAR}, #{dept_id,jdbcType=INTEGER}, #{dept_no,jdbcType=INTEGER}, #{dept_emp,jdbcType=INTEGER}, #{use_code,jdbcType=VARCHAR}, #{proj_id,jdbcType=INTEGER}, #{maker,jdbcType=INTEGER}, to_date(#{out_date},'yyyy/MM/dd'), #{checker,jdbcType=INTEGER}, to_date(#{check_date},'yyyy/MM/dd'), #{confirmer,jdbcType=INTEGER}, to_date(#{confirm_date},'yyyy/MM/dd'), #{state,jdbcType=INTEGER}, #{is_dir,jdbcType=INTEGER}, #{his_flag,jdbcType=VARCHAR}, #{app_date,jdbcType=TIMESTAMP}, #{amount_money,jdbcType=NUMERIC},
)

mapper.update:
UPDATE mat_out_main
year = #{year,jdbcType=VARCHAR}, month = #{month,jdbcType=VARCHAR}, bus_type_code = #{bus_type_code,jdbcType=VARCHAR}, store_id = #{store_id,jdbcType=INTEGER}, store_no = #{store_no,jdbcType=INTEGER}, brief = #{brief,jdbcType=VARCHAR}, dept_id = #{dept_id,jdbcType=INTEGER}, dept_no = #{dept_no,jdbcType=INTEGER}, dept_emp = #{dept_emp,jdbcType=INTEGER}, use_code = #{use_code,jdbcType=VARCHAR}, proj_id = #{proj_id,jdbcType=INTEGER}, maker = #{maker,jdbcType=INTEGER}, out_date =to_date(#{out_date,jdbcType=DATE},'yyyy/MM/dd'), checker = #{checker,jdbcType=INTEGER}, check_date = to_date(#{check_date,jdbcType=DATE},'yyyy/MM/dd'), confirmer = #{confirmer,jdbcType=INTEGER}, confirm_date = to_date(#{confirm_date,jdbcType=DATE},'yyyy/MM/dd'), state = #{state,jdbcType=INTEGER}, is_dir = #{is_dir,jdbcType=INTEGER}, his_flag = #{his_flag,jdbcType=VARCHAR}, app_date = #{app_date,jdbcType=TIMESTAMP}, amount_money = #{amount_money,jdbcType=NUMERIC},
AND group_id = #{group_id,jdbcType=INTEGER} AND hos_id = #{hos_id,jdbcType=INTEGER} AND copy_code = #{copy_code,jdbcType=VARCHAR} AND out_id = #{out_id,jdbcType=INTEGER}

