	checker = #{item.checker,jdbcType=INTEGER},
	state = #{item.state,jdbcType=INTEGER}

	hos_id = #{item.hos_id,jdbcType=INTEGER}   
	AND group_id = #{item.group_id,jdbcType=INTEGER} 
	AND copy_code = #{item.copy_code,jdbcType=VARCHAR}  
	AND out_id = #{item.out_id,jdbcType=INTEGER}

UPDATE mat_out_main set   
	year = #{item.year,jdbcType=VARCHAR}, 
	month = #{item.month,jdbcType=VARCHAR}, 
	confirm_date = fun_date_same_year_month(out_date, sysdate),
	confirmer = #{item.confirmer,jdbcType=INTEGER},
	state = #{item.state,jdbcType=INTEGER}

	hos_id = #{item.hos_id,jdbcType=INTEGER}   
	AND group_id = #{item.group_id,jdbcType=INTEGER} 
	AND copy_code = #{item.copy_code,jdbcType=VARCHAR}  
	AND out_id = #{item.out_id,jdbcType=INTEGER}

UPDATE mat_out_main
out_no = #{item.out_no,jdbcType=VARCHAR}, year = #{item.year,jdbcType=VARCHAR}, month = #{item.month,jdbcType=VARCHAR}, bus_type_code = #{item.bus_type_code,jdbcType=VARCHAR}, store_id = #{item.store_id,jdbcType=INTEGER}, store_no = #{item.store_no,jdbcType=INTEGER}, brief = #{item.brief,jdbcType=VARCHAR}, dept_id = #{item.dept_id,jdbcType=INTEGER}, dept_no = #{item.dept_no,jdbcType=INTEGER}, dept_emp = #{item.dept_emp,jdbcType=INTEGER}, use_code = #{item.use_code,jdbcType=VARCHAR}, proj_id = #{item.proj_id,jdbcType=INTEGER}, maker = #{item.maker,jdbcType=INTEGER}, out_date = to_date(#{item.out_date,jdbcType=VARCHAR},'yyyy/MM/dd'), checker = #{item.checker,jdbcType=INTEGER}, check_date = to_date(#{item.check_date,jdbcType=VARCHAR},'yyyy/MM/dd'), confirmer = #{item.confirmer,jdbcType=INTEGER}, confirm_date = to_date(#{item.confirm_date,jdbcType=VARCHAR},'yyyy/MM/dd'), state = #{item.state,jdbcType=INTEGER}, is_dir = #{item.is_dir,jdbcType=INTEGER}, his_flag = #{item.his_flag,jdbcType=VARCHAR}, is_dir = #{item.amount_money,jdbcType=NUMERIC},
