mapper.select:
with tmp_out as( select a.group_id, a.hos_id, a.copy_code, a.out_id, to_char(a.out_no) as out_no, a.out_date, a.confirm_date, a.brief, a.state, a.dept_id, a.dept_no, c.dept_name, case when a.state = 1 then '未审核' when a.state = 2 then '审核' else '已确认' end state_name, a.dept_emp, d.emp_code, d.emp_name, a.app_date, a.bus_type_code, b.bus_type_name, e.user_name as user_name_make, f.user_name as user_name_confirmer, a.store_id, g.store_code, g.store_name, a.amount_money, case when nvl(spc.print_count, 0) > 0 then 1 else 0 end as print_state, case when nvl(spc.print_count, 0) > 0 then '是' else '否' end as print_state_name from mat_out_main a left join mat_bus_type b on a.bus_type_code = b.bus_type_code left join hos_dept_dict c on a.group_id = c.group_id and a.hos_id = c.hos_id and a.dept_id = c.dept_id and a.dept_no = c.dept_no left join hos_emp d on a.group_id = d.group_id and a.hos_id = d.hos_id and a.dept_emp = d.emp_id left join sys_user e on a.group_id = e.group_id and a.hos_id = e.hos_id and a.maker = e.user_id left join sys_user f on a.group_id = f.group_id and a.hos_id = f.hos_id and a.confirmer = f.user_id left join hos_store_dict g on a.group_id = g.group_id and a.hos_id = g.hos_id and a.store_id = g.store_id and a.store_no = g.store_no left join sys_print_count spc on a.group_id = spc.group_id and a.hos_id = spc.hos_id and a.copy_code = spc.copy_code and a.out_no = spc.business_no where a.group_id = #{group_id,jdbcType=INTEGER} and a.hos_id = #{hos_id,jdbcType=INTEGER} and a.copy_code = #{copy_code,jdbcType=VARCHAR}
AND to_char(a.out_date,'yyyy-MM-dd') >= #{out_date_start}
AND to_char(a.out_date,'yyyy-MM-dd') <=#{out_date_end}
AND to_char(a.confirm_date,'yyyy-MM-dd') >= #{confirm_date_start}
AND to_char(a.confirm_date,'yyyy-MM-dd') <=#{confirm_date_end}
AND a.store_id = #{store_id,jdbcType=INTEGER}
and exists ( select 1 from v_user_data_perm where group_id = #{group_id,jdbcType=INTEGER} and hos_id = #{hos_id,jdbcType=INTEGER} and user_id = #{user_id,jdbcType=INTEGER} and table_code = 'HOS_STORE_DICT' and is_write = 1 and a.store_id = perm_code )
AND a.proj_id = #{proj_id,jdbcType=INTEGER}
AND a.maker = #{maker_code,jdbcType=VARCHAR}
AND a.state = #{state,jdbcType=INTEGER}
AND a.bus_type_code = #{bus_type_code,jdbcType=INTEGER}
AND a.bus_type_code in (3, 5, 7, 9, 11, 17, 19, 23, 24, 25) AND exists ( select perm_code from v_user_data_perm where group_id = #{group_id,jdbcType=INTEGER} and hos_id = #{hos_id,jdbcType=INTEGER} and user_id = #{user_id,jdbcType=INTEGER} and table_code = 'MAT_BUS_TYPE' and (is_read = 1 or is_write = 1) and a.bus_type_code = perm_code )
AND a.dept_id = #{dept_id,jdbcType=INTEGER}
AND a.out_no like '%${out_no}%'
AND a.brief like '%${brief}%'
AND a.DEPT_EMP =#{emp_id}
AND case when nvl(spc.print_count,0) > 0 then 1 else 0 end = #{print_state}
AND a.confirmer = #{affirm}
AND exists ( select 1 from mat_out_detail tem1 left join mat_inv_dict tem2 on tem1.group_id = tem2.group_id and tem1.hos_id = tem2.hos_id and tem1.copy_code = tem2.copy_code and tem1.inv_id = tem2.inv_id and tem1.inv_no = tem2.inv_no left join mat_type_dict tem3 on tem2.group_id = tem3.group_id and tem2.hos_id = tem3.hos_id and tem2.copy_code = tem3.copy_code and tem2.mat_type_id = tem3.mat_type_id and tem2.mat_type_no = tem3.mat_type_no where a.out_id = tem1.out_id and a.group_id = tem1.group_id and a.hos_id = tem1.hos_id and a.copy_code = tem1.copy_code
AND ( tem2.inv_code like '%${inv_code}%' or upper(tem2.inv_name) like '%${inv_code}%' or upper(tem2.inv_model) like '%${inv_code}%' or upper(tem2.spell_code) like '%${inv_code}%' or upper(tem2.wbx_code) like '%${inv_code}%' or upper(tem2.bid_code) like '%${inv_code}%' or lower(tem2.spell_code) like '%${inv_code}%' or lower(tem2.bid_code) like '%${inv_code}%' or lower(tem2.wbx_code) like '%${inv_code}%' or lower(tem2.inv_name) like '%${inv_code}%' or tem2.alias like '%${inv_code}%' )
AND tem2.inv_model like '%${inv_model}%'
AND tem1.batch_no like '%${batch_no}%'
AND tem1.bar_code like '%${bar_code}%'
AND tem3.mat_type_code like '${mat_type_code}%'
)
order by a.out_no desc ) SELECT null as group_id, null as hos_id, null as copy_code, null as out_id, '合计' as out_no, null as out_date, null as confirm_date, null as brief, null as state, null as state_name, null as dept_id, null as dept_no, null as dept_name, null as dept_emp, --领料人 null as emp_code, null as emp_name, null as app_date, null as bus_type_code, null as bus_type_name, null as user_name_make, null as user_name_confirmer, null as store_id, null as store_code, null as store_name, sum(amount_money) as amount_money, null as print_state, null as print_state_name FROM tmp_out UNION ALL SELECT group_id, hos_id, copy_code, out_id, out_no, out_date, confirm_date, brief, state, state_name, dept_id, dept_no, dept_name, dept_emp, --领料人 emp_code, emp_name, app_date, bus_type_code, bus_type_name, user_name_make, user_name_confirmer, store_id, store_code, store_name, amount_money, print_state, print_state_name from tmp_out

SELECT 
	group_id,
	hos_id,
	copy_code,
	out_id,
	out_no,
	year,
	month,
	bus_type_code,
	store_id,
	store_no,
	brief,
	out_date,
	dept_id,
	dept_no,
	dept_emp,
	use_code,
	proj_id,
	maker,
	checker,
	check_date,
	confirmer,
	confirm_date,
	state,
	is_dir,
	his_flag
FROM MAT_OUT_MAIN 


AND group_id = #{group_id,jdbcType=INTEGER}


AND hos_id = #{hos_id,jdbcType=INTEGER}


