SELECT 
group_id,
hos_id,
copy_code,
out_id,
out_no,
year,
month,
bus_type_code,
store_id,
store_no,
brief,
out_date,
dept_id,
dept_no,
dept_emp,
use_code,
proj_id,
maker,
checker,
check_date,
confirmer,
confirm_date,
state,
is_dir,
his_flag
FROM MAT_OUT_MAIN 


AND group_id = #{group_id,jdbcType=INTEGER}


AND hos_id = #{hos_id,jdbcType=INTEGER}


AND copy_code = #{copy_code,jdbcType=VARCHAR}


AND out_id = #{out_id,jdbcType=INTEGER}


AND out_no = #{out_no,jdbcType=VARCHAR}


AND year = #{year,jdbcType=VARCHAR}


AND month = #{month,jdbcType=VARCHAR}


AND bus_type_code = #{bus_type_code,jdbcType=VARCHAR}


AND store_id = #{store_id,jdbcType=INTEGER}


AND store_no = #{store_no,jdbcType=INTEGER}


AND brief = #{brief,jdbcType=VARCHAR}


AND out_date = #{out_date,jdbcType=DATE}


AND dept_id = #{dept_id,jdbcType=INTEGER}


AND dept_no = #{dept_no,jdbcType=INTEGER}


AND dept_emp = #{dept_emp,jdbcType=INTEGER}


AND use_code = #{use_code,jdbcType=VARCHAR}


AND proj_id = #{proj_id,jdbcType=INTEGER}


AND maker = #{maker,jdbcType=INTEGER}


AND checker = #{checker,jdbcType=INTEGER}


AND check_date = #{check_date,jdbcType=DATE}


AND confirmer = #{confirmer,jdbcType=INTEGER}


AND confirm_date = #{confirm_date,jdbcType=DATE}


AND state = #{state,jdbcType=INTEGER}


AND is_dir = #{is_dir,jdbcType=INTEGER}


AND his_flag = #{his_flag,jdbcType=VARCHAR}


order by group_id asc

with w_mih as (
select mih.group_id as group_id, mih.hos_id as hos_id, mih.copy_code as copy_code, mih.cur_amount as cur_amount, mih.cur_money as cur_money, mih.inv_id as inv_id, mid.inv_no as inv_no, mid.inv_code as inv_code, mid.inv_name as inv_name, mid.inv_model as inv_model, mid.plan_price as plan_price, mid.is_charge as is_charge, mid.fac_id as fac_id, mid.fac_no as fac_no, mid.unit_code as unit_code, hu.unit_name as unit_name, hfd.fac_code as fac_code, hfd.fac_name as fac_name from mat_inv_hold mih
left join mat_inv_dict mid on mih.group_id = mid.group_id and mih.hos_id = mid.hos_id and mih.copy_code = mid.copy_code and mih.inv_id = mid.inv_id and mid.is_stop=0 left join hos_unit hu on mid.group_id = hu.group_id and mid.hos_id = hu.hos_id and mid.unit_code = hu.unit_code and hu.is_stop=0 left join hos_fac_dict hfd on mid.group_id = hfd.group_id and mid.hos_id = hfd.hos_id and mid.fac_id = hfd.fac_id and mid.fac_no = hfd.fac_no
mih.cur_amount > 0
AND mih.group_id = #{group_id}
AND mih.hos_id = #{hos_id}
AND mih.copy_code = #{copy_code}
AND mih.inv_id = #{inv_id}
AND mih.store_id = #{store_id}
order by mih.inv_id asc ),
w_mod as ( select inv_id as inv_id, sum(amount) as imme_amount , sum(amount_money) as imme_amount_money, mom.store_id as store_id, mom.store_no as store_no
from mat_out_detail matod left join mat_out_main mom on matod.group_id = mom.group_id and matod.hos_id = mom.hos_id and matod.copy_code = mom.copy_code and matod.out_id = mom.out_id
mom.state < 3
AND mom.out_id != #{out_id}
AND matod.group_id = #{group_id}
AND matod.hos_id = #{hos_id}
AND matod.copy_code = #{copy_code}
AND matod.inv_id = #{inv_id}
AND mom.store_id = #{store_id}
AND mom.store_no = #{store_no}
group by inv_id,store_id,store_no order by matod.inv_id asc ) select
w_mih.group_id as group_id, w_mih.hos_id as hos_id, w_mih.copy_code as copy_code, w_mih.cur_amount as cur_amount, w_mih.cur_money as cur_money, w_mih.inv_no as inv_no, w_mih.inv_code as inv_code, w_mih.inv_name as inv_name, w_mih.inv_model as inv_model, w_mih.plan_price as plan_price, w_mih.is_charge as is_charge, w_mih.fac_id as fac_id, w_mih.fac_no as fac_no, w_mih.unit_code as unit_code, w_mih.unit_name as unit_name, w_mih.fac_code as fac_code, w_mih.fac_name as fac_name, nvl(w_mih.cur_amount,0) - nvl(w_mod.imme_amount,0) AS imme_amount, nvl(w_mih.cur_money,0) - nvl(w_mod.imme_amount_money,0) AS imme_amount_money, w_mih.inv_id as inv_id, w_mod.store_id as store_id, w_mod.store_no as store_no
from w_mih w_mih left join w_mod w_mod on w_mih.inv_id = w_mod.inv_id order by w_mih.inv_id

with w_mfb as (
select mfb.group_id as group_id, mfb.hos_id as hos_id, mfb.copy_code as copy_code, mfb.left_amount as left_amount, mfb.left_money as left_money, mfb.batch_sn as batch_sn, mfb.batch_no as batch_no, mfb.bar_code as bar_code, mfb.price as price, mfb.inv_id as inv_id, mfb.location_id as location_id, mld.location_code as location_code, mld.location_name as location_name, mid.inv_no as inv_no, mid.inv_code as inv_code, mid.inv_name as inv_name, mid.inv_model as inv_model, mid.unit_code as unit_code, mid.sell_price as sell_price, hu.unit_name as unit_name, mbv.inva_date as inva_date
from mat_fifo_balance mfb left join mat_inv_dict mid on mfb.group_id = mid.group_id and mfb.hos_id = mid.hos_id and mfb.copy_code = mid.copy_code and mfb.inv_id = mid.inv_id and mid.is_stop=0 left join mat_location_dict mld on mfb.group_id = mld.group_id and mfb.hos_id = mld.hos_id and mfb.copy_code = mld.copy_code and mfb.location_id = mld.location_id and mld.is_stop=0 left join mat_batch_validity mbv on mfb.group_id = mbv.group_id and mfb.hos_id = mbv.hos_id and mfb.copy_code = mbv.copy_code and mfb.inv_id = mbv.inv_id and mfb.batch_no = mbv.batch_no left join hos_unit hu on mid.group_id = hu.group_id and mid.hos_id = hu.hos_id and mid.unit_code = hu.unit_code and hu.is_stop=0
mfb.left_amount > 0
AND mfb.group_id = #{group_id}
AND mfb.hos_id = #{hos_id}
AND mfb.copy_code = #{copy_code}
AND mfb.inv_id = #{inv_id}
AND mfb.batch_sn = #{batch_sn}
AND mfb.batch_no = #{batch_no}
AND mfb.bar_code = #{bar_code}
AND mfb.store_id = #{store_id}
${sql}
order by mfb.inv_id asc
),
w_mod as ( select inv_id as inv_id, batch_sn as batch_sn, batch_no as batch_no, bar_code as bar_code, sum(amount) as imme_amount , sum(amount_money) as imme_amount_money, mom.store_id as store_id, mom.store_no as store_no from mat_out_detail matod left join mat_out_main mom on matod.group_id = mom.group_id and matod.hos_id = mom.hos_id and matod.copy_code = mom.copy_code and matod.out_id = mom.out_id
mom.state < 3
AND mom.out_id != #{out_id}
AND matod.group_id = #{group_id}
AND matod.hos_id = #{hos_id}
AND matod.copy_code = #{copy_code}
AND matod.inv_id = #{inv_id}
AND matod.batch_sn = #{batch_sn}
AND matod.batch_no = #{batch_no}
AND matod.bar_code = #{bar_code}
AND mom.store_id = #{store_id}
${sql}
group by inv_id,batch_sn,batch_no,bar_code,store_id,store_no order by matod.inv_id asc
)
select
w_mfb.group_id as group_id, w_mfb.hos_id as hos_id, w_mfb.copy_code as copy_code, w_mfb.left_amount as left_amount, w_mfb.left_money as left_money, w_mfb.batch_sn as batch_sn, w_mfb.batch_no as batch_no, w_mfb.bar_code as bar_code, w_mfb.price as price, w_mfb.inv_id as inv_id, w_mfb.location_id as location_id, w_mfb.location_code as location_code, w_mfb.location_name as location_name, w_mfb.inv_no as inv_no, w_mfb.inv_code as inv_code, w_mfb.inv_name as inv_name, w_mfb.inv_model as inv_model, w_mfb.unit_code as unit_code, w_mfb.sell_price as sell_price, w_mfb.unit_name as unit_name, w_mfb.inva_date as inva_date, w_mod.store_id as store_id, w_mod.store_no as store_no, nvl(w_mfb.left_amount,0) - nvl(w_mod.imme_amount,0) AS imme_amount, nvl(w_mfb.left_money,0) - nvl(w_mod.imme_amount_money,0) AS imme_amount_money, nvl(w_mfb.left_amount,0) as cur_amount
from w_mfb w_mfb left join w_mod w_mod on w_mfb.inv_id = w_mod.inv_id and w_mfb.batch_sn= w_mod.batch_sn and w_mfb.batch_no= w_mod.batch_no and w_mfb.bar_code= w_mod.bar_code
where nvl(w_mfb.left_amount, 0) - nvl(w_mod.imme_amount, 0) > 0 order by w_mfb.inv_id, w_mfb.inva_date, w_mfb.batch_no, w_mfb.batch_sn

select mim.group_id, mim.hos_id, mim.copy_code, mim.in_id, mim.in_no, mim.brief, hsd.sup_name, su.user_name as maker_name, mim.in_date, suc.user_name as confirmer_name, mim.confirm_date, mim.app_dept dept_id, hdd.dept_no, hdd.dept_code, hdd.dept_name, sum(nvl(mfb.left_amount, 0) - nvl(modet.amount, 0)) as imme_amount,dm.amount_money from mat_in_main mim left join mat_in_detail mid on mim.group_id = mid.group_id and mim.hos_id = mid.hos_id and mim.copy_code = mid.copy_code and mim.in_id = mid.in_id left join ( select group_id, hos_id, copy_code,in_id,sum(amount_money) as amount_money from mat_in_detail
group by group_id, hos_id, copy_code,in_id )dm on mim.group_id = dm.group_id and mim.hos_id = dm.hos_id and mim.copy_code = dm.copy_code and dm.in_id=mim.in_id left join mat_fifo_balance mfb on mim.group_id = mfb.group_id and mim.hos_id = mfb.hos_id and mim.copy_code = mfb.copy_code and mim.store_id = mfb.store_id and mid.inv_id = mfb.inv_id and mid.batch_no = mfb.batch_no and mid.batch_sn = mfb.batch_sn and mid.bar_code = mfb.bar_code left join mat_out_main mom on mim.group_id = mom.group_id and mim.hos_id = mom.hos_id and mim.copy_code = mom.copy_code and mim.store_id = mom.store_id and mom.state < 3 left join mat_out_detail modet on mom.group_id = modet.group_id and mom.hos_id = modet.hos_id and mom.copy_code = modet.copy_code and mom.out_id = modet.out_id and mid.inv_id = modet.inv_id and mid.batch_no = modet.batch_no and mid.batch_sn = modet.batch_sn and mid.bar_code = modet.bar_code left join hos_sup_dict hsd on mim.group_id = hsd.group_id and mim.hos_id = hsd.hos_id and mim.sup_id = hsd.sup_id and hsd.is_stop = 0 left join hos_dept_dict hdd on mim.group_id = hdd.group_id and mim.hos_id = hdd.hos_id and mim.app_dept = hdd.dept_id and hdd.is_stop = 0 left join sys_user su on mim.group_id = su.group_id and su.hos_id = su.hos_id and mim.maker = su.user_id left join sys_user suc on mim.group_id = suc.group_id and mim.hos_id = suc.hos_id and mim.confirmer = suc.user_id where mim.group_id = #{group_id,jdbcType=INTEGER} and mim.hos_id = #{hos_id,jdbcType=INTEGER} and mim.copy_code = #{copy_code,jdbcType=VARCHAR}
and mim.store_id = #{store_id,jdbcType=INTEGER}
and mim.app_dept = #{dept_id,jdbcType=INTEGER}
AND mim.sup_id = #{sup_id,jdbcType=INTEGER}
AND mim.in_no = #{in_no,jdbcType=VARCHAR}
AND mim.in_date >= #{in_date_start,jdbcType=DATE}
AND mim.in_date <= #{in_date_end,jdbcType=DATE}
and mim.state = 3 group by mim.group_id, mim.hos_id, mim.copy_code, mim.in_id, mim.in_no, mim.brief, hsd.sup_name, su.user_name, mim.in_date, suc.user_name, mim.app_dept, hdd.dept_no, hdd.dept_code, hdd.dept_name, mim.confirm_date,dm.amount_money having sum(nvl(mfb.left_amount, 0) - nvl(modet.amount, 0)) > 0 order by mim.in_id

select mdm.group_id as group_id, mdm.hos_id as hos_id, mdm.copy_code as copy_code, mdm.dept_match_id as dept_match_id, mdm.dept_match_name as dept_match_name, mdmd.inv_id as inv_id, mid.inv_code as inv_code, mid.inv_name as inv_name, mid.inv_model as inv_model, mid.unit_code as unit_code, hu.unit_name as unit_name, mdm.store_id as store_id, hsd.store_no as store_no, hsd.store_code as store_code, hsd.store_name as store_name, nvl(mdmd.amount,0) as amount ,mfb.cur_amount from mat_dept_match_detail mdmd left join mat_dept_match mdm on mdmd.group_id = mdm.group_id and mdmd.hos_id = mdm.hos_id and mdmd.copy_code = mdm.copy_code and mdmd.dept_match_id = mdm.dept_match_id left join mat_inv_dict mid on mdmd.group_id = mid.group_id and mdmd.hos_id = mid.hos_id and mdmd.copy_code = mid.copy_code and mdmd.inv_id = mid.inv_id and mid.is_stop=0 left join mat_inv_hold mfb on mdmd.group_id=mfb.group_id and mdmd.hos_id=mfb.hos_id and mdmd.copy_code=mfb.copy_code and mdmd.inv_id=mfb.inv_id and mfb.store_id=mdm.store_id left join hos_unit hu on mid.group_id = hu.group_id and mid.hos_id = hu.hos_id and mid.unit_code = hu.unit_code left join hos_store_dict hsd on mdm.group_id = hsd.group_id and mdm.hos_id = hsd.hos_id and mdm.store_id =hsd.store_id and hsd.is_stop=0
AND mdmd.group_id = #{group_id}
AND mdmd.hos_id = #{hos_id}
AND mdmd.copy_code = #{copy_code}
AND mdmd.inv_id = #{inv_id}
AND mdm.store_id = #{store_id}
AND mdm.dept_id = #{dept_id}
AND mdmd.dept_match_id = #{dept_match_id}
${sql}
group by mdm.group_id , mdm.hos_id , mdm.copy_code , mdm.dept_match_id , mdm.dept_match_name , mdmd.inv_id , mid.inv_code , mid.inv_name, mid.inv_model , mid.unit_code , hu.unit_name , mdm.store_id , hsd.store_no , hsd.store_code , hsd.store_name ,mdmd.amount , mfb.cur_amount order by mdmd.inv_id

SELECT 
*
FROM (
  SELECT NVL(MIN(out_id),'0') from MAT_OUT_MAIN where 
 group_id = #{group_id,jdbcType=INTEGER}
AND hos_id = #{hos_id,jdbcType=INTEGER}
AND copy_code = #{copy_code,jdbcType=VARCHAR}
AND bus_type_code = #{bus_type_code,jdbcType=VARCHAR}
AND store_id = #{store_id,jdbcType=INTEGER}
AND   out_id>#{out_id,jdbcType=INTEGER} 
order by out_id
)WHERE rownum = 1

SELECT 
*
FROM (
  SELECT NVL(MAX(out_id),'0') from MAT_OUT_MAIN where 
 group_id = #{group_id,jdbcType=INTEGER}
AND hos_id = #{hos_id,jdbcType=INTEGER}
AND copy_code = #{copy_code,jdbcType=VARCHAR}
AND bus_type_code = #{bus_type_code,jdbcType=VARCHAR}
AND store_id = #{store_id,jdbcType=INTEGER}
AND   out_id<#{out_id,jdbcType=INTEGER} 
order by out_id desc
)WHERE rownum = 1

with inv_fifi as ( select a.store_id,a.inv_id,nvl(a.cur_amount,0)-nvl(b.amount,0) as amount
from mat_inv_hold a left join ( select group_id,hos_id,copy_code,store_id,inv_id,sum(nvl(amount,0)) amount from v_mat_stock_imme where
store_id in (select store_id from mat_out_main where out_id in (${paraId}))
store_id = #{store_id,jdbcType=INTEGER}
group by group_id,hos_id,copy_code,store_id,inv_id ) b on a.group_id=b.group_id and a.hos_id=b.hos_id and a.copy_code=b.copy_code and a.inv_id=b.inv_id and a.store_id=b.store_id where a.cur_amount>0
and a.store_id in (select store_id from mat_out_main where out_id in (${paraId}))
and a.store_id = #{store_id,jdbcType=INTEGER}
and a.group_id = #{group_id,jdbcType=NUMERIC} and a.hos_id = #{hos_id,jdbcType=NUMERIC} and a.copy_code = #{copy_code,jdbcType=VARCHAR} )
select a.out_id as id, a.out_detail_id, b.inv_code as inv_code, b.inv_name as inv_name, b.inv_model as inv_model, c.unit_name as unit_code, a.price as price, a.amount as amount, a.amount_money as money, d.fac_name as fac_name, a.note as note, e.pack_name as pack_code, a.num_exchange as num_exchange, a.num as num , a.BATCH_NO as BATCH_NO, a.BAR_CODE as BAR_CODE, to_char(a.INVA_DATE,'yyyy-MM-dd') as INVA_DATE, ld.LOCATION_NAME as LOCATION_NAME, to_char(a.DISINFECT_DATE,'yyyy-MM-dd') as DISINFECT_DATE, a.SALE_PRICE as SALE_PRICE, a.SALE_MONEY as SALE_MONEY, a.SELL_PRICE as SELL_PRICE, a.SELL_MONEY as SELL_MONEY, a.ALLOT_PRICE as ALLOT_PRICE, a.ALLOT_MONEY as ALLOT_MONEY, b.BiD_CODE as BID_CODE, inf.amount as IMME_AMOUNT, m.memory_encoding as memory_encoding,mtd.mat_type_name as MAT_TYPE_NAME, RANK () OVER (ORDER BY a.out_id,ld.LOCATION_NAME desc) AS rowno from ( select b.store_id,a.group_id,a.hos_id,a.copy_code,a.out_id,a.inv_id,a.inv_no,a.batch_no,a.price, a.sell_price,a.allot_price,a.num_exchange,a.num,a.bar_code,a.inva_date,a.disinfect_date, a.location_id,a.note,a.pack_code,a.out_no,a.sale_price,min(a.out_detail_id) out_detail_id, sum(a.amount) amount,sum(a.amount_money) amount_money,sum(a.sell_money) sell_money,sum(a.allot_money) allot_money,sum(a.sale_money) sale_money from mat_out_detail a left join mat_out_main b on a.out_id=b.out_id where a.group_id = #{group_id,jdbcType=NUMERIC} and a.hos_id = #{hos_id,jdbcType=NUMERIC} and a.copy_code = #{copy_code,jdbcType=VARCHAR}
and a.out_id in (${paraId})
and a.out_id = #{out_id,jdbcType=NUMERIC}
group by b.store_id,a.group_id,a.hos_id,a.copy_code,a.out_id,a.inv_id,a.inv_no,a.batch_no,a.price,a.sell_price,a.allot_price,a.num_exchange,a.num,a.bar_code,a.inva_date,a.disinfect_date, a.location_id,a.note,a.pack_code,a.out_no,a.sale_price ) a left join mat_inv_dict b on a.group_id=b.group_id and a.hos_id =b.hos_id and a.copy_code=b.copy_code and a.inv_id=b.inv_id and a.inv_no=b.inv_no left join hos_unit c on b.unit_code=c.unit_code and a.group_id=c.group_id and a.hos_id =c.hos_id left join hos_fac_dict d on b.fac_id=d.fac_id and b.group_id=d.group_id and b.hos_id =d.hos_id and b.fac_no=d.fac_no left join hos_package e on a.pack_code=e.pack_code and a.group_id=e.group_id and a.hos_id =e.hos_id left join mat_store_inv msi on a.group_id = msi.group_id and a.hos_id = msi.hos_id and a.copy_code = msi.copy_code and a.inv_id = msi.inv_id and a.store_id = msi.store_id
and msi.store_id in (select store_id from mat_out_main where out_id in (${paraId}))
and msi.store_id = #{store_id,jdbcType=INTEGER}
left join mat_location_dict ld on msi.group_id = ld.group_id and msi.hos_id = ld.hos_id and msi.copy_code = ld.copy_code and msi.location_id = ld.location_id and ld.is_stop = 0 left join mat_inv m on a.group_id = m.group_id and a.hos_id = m.hos_id and a.inv_id = m.inv_id left join inv_fifi inf on a.inv_id=inf.inv_id left join mat_type_dict mtd on m.group_id = mtd.group_id and m.hos_id = mtd.hos_id and m.copy_code = mtd.copy_code and m.mat_type_id = mtd.mat_type_id and mtd.is_stop = 0 order by a.out_id,ld.LOCATION_NAME,b.spell_code,a.out_detail_id desc

with inv_fifi as ( select a.store_id,a.inv_id,nvl(a.cur_amount,0)-nvl(b.amount,0) as amount
from mat_inv_hold a left join ( select group_id,hos_id,copy_code,store_id,inv_id,sum(nvl(amount,0)) amount from v_mat_stock_imme where
store_id in (select store_id from mat_out_main where out_id in (${paraId}))
store_id = #{store_id,jdbcType=INTEGER}
group by group_id,hos_id,copy_code,store_id,inv_id ) b on a.group_id=b.group_id and a.hos_id=b.hos_id and a.copy_code=b.copy_code and a.inv_id=b.inv_id and a.store_id=b.store_id where a.cur_amount>0
and a.store_id in (select store_id from mat_out_main where out_id in (${paraId}))
and a.store_id = #{store_id,jdbcType=INTEGER}
and a.group_id = #{group_id,jdbcType=NUMERIC} and a.hos_id = #{hos_id,jdbcType=NUMERIC} and a.copy_code = #{copy_code,jdbcType=VARCHAR} )
SELECT tmp.*, RANK () OVER (ORDER BY id, fim_type_code, LOCATION_NAME, spell_code, out_detail_id desc) AS rowno FROM( select a.out_id as id, to_char(b.inv_code) as inv_code, b.inv_name as inv_name, b.spell_code as spell_code, b.inv_model as inv_model, c.unit_name as unit_code, a.price as price, a.amount as amount, a.amount_money as money, d.fac_name as fac_name, a.note as note, e.pack_name as pack_code, a.num_exchange as num_exchange, a.num as num , a.BATCH_NO as BATCH_NO, a.BAR_CODE as BAR_CODE, to_char(a.INVA_DATE,'yyyy-MM-dd') as INVA_DATE, ld.LOCATION_NAME as LOCATION_NAME, to_char(a.DISINFECT_DATE,'yyyy-MM-dd') as DISINFECT_DATE, a.SALE_PRICE as SALE_PRICE, a.SALE_MONEY as SALE_MONEY, a.SELL_PRICE as SELL_PRICE, a.SELL_MONEY as SELL_MONEY, a.ALLOT_PRICE as ALLOT_PRICE, a.ALLOT_MONEY as ALLOT_MONEY, b.BiD_CODE as BID_CODE, inf.amount as IMME_AMOUNT, m.memory_encoding as memory_encoding, mtd.mat_type_name as MAT_TYPE_NAME, mft.fim_type_code AS FIM_TYPE_CODE, mft.fim_type_name AS FIM_TYPE_NAME, a.out_detail_id AS OUT_DETAIL_ID from ( select b.store_id,a.group_id,a.hos_id,a.copy_code,a.out_id,a.inv_id,a.inv_no,a.batch_no,a.price, a.sell_price,a.allot_price,a.num_exchange,a.num,a.bar_code,a.inva_date,a.disinfect_date, a.location_id,a.note,a.pack_code,a.out_no,a.sale_price,min(a.out_detail_id) out_detail_id, sum(a.amount) amount,sum(a.amount_money) amount_money,sum(a.sell_money) sell_money,sum(a.allot_money) allot_money,sum(a.sale_money) sale_money from mat_out_detail a left join mat_out_main b on a.out_id=b.out_id where a.group_id = #{group_id,jdbcType=NUMERIC} and a.hos_id = #{hos_id,jdbcType=NUMERIC} and a.copy_code = #{copy_code,jdbcType=VARCHAR}
and a.out_id in (${paraId})
and a.out_id = #{out_id,jdbcType=NUMERIC}
group by b.store_id,a.group_id,a.hos_id,a.copy_code,a.out_id,a.inv_id,a.inv_no,a.batch_no,a.price,a.sell_price,a.allot_price,a.num_exchange,a.num,a.bar_code,a.inva_date,a.disinfect_date, a.location_id,a.note,a.pack_code,a.out_no,a.sale_price ) a left join mat_inv_dict b on a.group_id=b.group_id and a.hos_id =b.hos_id and a.copy_code=b.copy_code and a.inv_id=b.inv_id and a.inv_no=b.inv_no left join hos_unit c on b.unit_code=c.unit_code and a.group_id=c.group_id and a.hos_id =c.hos_id left join hos_fac_dict d on b.fac_id=d.fac_id and b.group_id=d.group_id and b.hos_id =d.hos_id and b.fac_no=d.fac_no left join hos_package e on a.pack_code=e.pack_code and a.group_id=e.group_id and a.hos_id =e.hos_id left join mat_store_inv msi on a.group_id = msi.group_id and a.hos_id = msi.hos_id and a.copy_code = msi.copy_code and a.inv_id = msi.inv_id and a.store_id = msi.store_id
and msi.store_id in (select store_id from mat_out_main where out_id in (${paraId}))
and msi.store_id = #{store_id,jdbcType=INTEGER}
left join mat_location_dict ld on msi.group_id = ld.group_id and msi.hos_id = ld.hos_id and msi.copy_code = ld.copy_code and msi.location_id = ld.location_id and ld.is_stop = 0 left join mat_inv m on a.group_id = m.group_id and a.hos_id = m.hos_id and a.inv_id = m.inv_id left join inv_fifi inf on a.inv_id=inf.inv_id left join mat_type_dict mtd on m.group_id = mtd.group_id and m.hos_id = mtd.hos_id and m.copy_code = mtd.copy_code and m.mat_type_id = mtd.mat_type_id and mtd.is_stop = 0 left join mat_fim_type mft on mtd.group_id = mft.group_id and mtd.hos_id = mft.hos_id and mtd.copy_code = mft.copy_code and mtd.fim_type_code = mft.fim_type_code union all select a.out_id as id, to_char('小计：') as inv_code, NULL as inv_name, NULL as spell_code, NULL as inv_model, NULL as unit_code, NULL as price, NULL as amount, SUM(a.amount_money) as money, NULL as fac_name, NULL as note, NULL as pack_code, NULL as num_exchange, NULL as num , NULL as BATCH_NO, NULL as BAR_CODE, NULL as INVA_DATE, NULL as LOCATION_NAME, NULL as DISINFECT_DATE, NULL as SALE_PRICE, SUM(a.SALE_MONEY) as SALE_MONEY, NULL as SELL_PRICE, SUM(a.SELL_MONEY) as SELL_MONEY, NULL as ALLOT_PRICE, SUM(a.ALLOT_MONEY) as ALLOT_MONEY, NULL as BID_CODE, NULL as IMME_AMOUNT, NULL as memory_encoding, NULL as MAT_TYPE_NAME, mft.fim_type_code AS FIM_TYPE_CODE, NULL AS FIM_TYPE_NAME, 0 AS OUT_DETAIL_ID from ( select b.store_id,a.group_id,a.hos_id,a.copy_code,a.out_id,a.inv_id,a.inv_no,a.batch_no,a.price, a.sell_price,a.allot_price,a.num_exchange,a.num,a.bar_code,a.inva_date,a.disinfect_date, a.location_id,a.note,a.pack_code,a.out_no,a.sale_price,min(a.out_detail_id) out_detail_id, sum(a.amount) amount,sum(a.amount_money) amount_money,sum(a.sell_money) sell_money,sum(a.allot_money) allot_money,sum(a.sale_money) sale_money from mat_out_detail a left join mat_out_main b on a.out_id=b.out_id where a.group_id = #{group_id,jdbcType=NUMERIC} and a.hos_id = #{hos_id,jdbcType=NUMERIC} and a.copy_code = #{copy_code,jdbcType=VARCHAR}
and a.out_id in (${paraId})
and a.out_id = #{out_id,jdbcType=NUMERIC}
group by b.store_id,a.group_id,a.hos_id,a.copy_code,a.out_id,a.inv_id,a.inv_no,a.batch_no,a.price,a.sell_price,a.allot_price,a.num_exchange,a.num,a.bar_code,a.inva_date,a.disinfect_date, a.location_id,a.note,a.pack_code,a.out_no,a.sale_price ) a left join mat_inv_dict b on a.group_id=b.group_id and a.hos_id =b.hos_id and a.copy_code=b.copy_code and a.inv_id=b.inv_id and a.inv_no=b.inv_no left join mat_inv m on a.group_id = m.group_id and a.hos_id = m.hos_id and a.copy_code=m.copy_code and a.inv_id = m.inv_id left join mat_type_dict mtd on m.group_id = mtd.group_id and m.hos_id = mtd.hos_id and m.copy_code = mtd.copy_code and m.mat_type_id = mtd.mat_type_id and mtd.is_stop = 0 left join mat_fim_type mft on mtd.group_id = mft.group_id and mtd.hos_id = mft.hos_id and mtd.copy_code = mft.copy_code and mtd.fim_type_code = mft.fim_type_code group by a.out_id, mft.fim_type_code ) tmp order by id, LOCATION_NAME, spell_code, out_detail_id desc

with tmp_imme as ( select inv_id, store_id, sum(amount) as amount from ( --出库未确认单据 select inv_id, mom.store_id, sum(nvl(amount, 0)) as amount from mat_out_main mom left join mat_out_detail matod on mom.group_id = matod.group_id and mom.hos_id = matod.hos_id and mom.copy_code = matod.copy_code and mom.out_id = matod.out_id where matod.group_id = #{group_id,jdbcType=INTEGER} AND matod.hos_id = #{hos_id,jdbcType=INTEGER} AND matod.copy_code = #{copy_code,jdbcType=VARCHAR}
AND mom.state < 3 AND mom.bus_type_code <> '21' AND matod.amount_money > 0
group by inv_id, mom.store_id --退货未确认单据 union all select inv_id, mim.store_id, -1 * sum(nvl(amount, 0)) as amount from mat_in_main mim left join mat_in_detail matid on mim.group_id = matid.group_id and mim.hos_id = matid.hos_id and mim.copy_code = matid.copy_code and mim.in_id = matid.in_id where matid.group_id = #{group_id,jdbcType=INTEGER} AND matid.hos_id = #{hos_id,jdbcType=INTEGER} AND matid.copy_code = #{copy_code,jdbcType=VARCHAR}
AND mim.state < 3 AND mim.bus_type_code in ('10','12','16','22') and matid.amount_money < 0
group by inv_id, mim.store_id ) group by inv_id, store_id ), tmp_affi_imme as ( select inv_id, store_id, sum(amount) as amount from ( --出库未确认单据 select inv_id, mom.store_id, sum(nvl(amount, 0)) as amount from mat_affi_out mom left join mat_affi_out_detail matod on mom.group_id = matod.group_id and mom.hos_id = matod.hos_id and mom.copy_code = matod.copy_code and mom.out_id = matod.out_id where matod.group_id = #{group_id,jdbcType=INTEGER} AND matod.hos_id = #{hos_id,jdbcType=INTEGER} AND matod.copy_code = #{copy_code,jdbcType=VARCHAR}
AND mom.state < 3 AND mom.bus_type_code <> '30'
group by inv_id, mom.store_id --退货未确认单据 union all select inv_id, mim.store_id, -1 * sum(nvl(amount, 0)) as amount from mat_affi_in mim left join mat_affi_in_detail matid on mim.group_id = matid.group_id and mim.hos_id = matid.hos_id and mim.copy_code = matid.copy_code and mim.in_id = matid.in_id where matid.group_id = #{group_id,jdbcType=INTEGER} AND matid.hos_id = #{hos_id,jdbcType=INTEGER} AND matid.copy_code = #{copy_code,jdbcType=VARCHAR}
AND mim.state < 3 AND mim.bus_type_code = '29'
group by inv_id, mim.store_id ) group by inv_id, store_id ), tmp_rela as ( --申领单总处理数量 select mad.apply_id, mad.detail_id, mad.app_amount, sum(nvl(maor.rela_amount, 0)) rela_amount from mat_apply_detail mad left join mat_apply_out_rela maor on mad.group_id = maor.group_id and mad.hos_id = maor.hos_id and mad.copy_code = maor.copy_code and mad.apply_id = maor.apply_id and mad.detail_id = maor.app_detail_id and maor.rela_type <> 5 where mad.group_id = #{group_id,jdbcType=INTEGER} and mad.hos_id = #{hos_id,jdbcType=INTEGER} and mad.copy_code = #{copy_code,jdbcType=VARCHAR} and nvl(mad.is_closed, 0) = 0 group by mad.apply_id, mad.detail_id, mad.app_amount ), tmp_app_left as( --申请单材料总库存 select mad.apply_id, sum(nvl(hold.cur_amount, 0) + nvl(afhold.cur_amount, 0) - nvl(imme.amount, 0) - nvl(afimme.amount, 0)) left_amount from mat_apply_main mam left join mat_apply_detail mad on mam.group_id = mad.group_id and mam.hos_id = mad.hos_id and mam.copy_code = mad.copy_code and mam.apply_id = mad.apply_id left join tmp_rela tr on mad.apply_id = tr.apply_id and mad.detail_id = tr.detail_id left join mat_inv_hold hold on mam.group_id = hold.group_id and mam.hos_id = hold.hos_id and mam.copy_code = hold.copy_code and mam.store_id = hold.store_id and mad.inv_id = hold.inv_id left join mat_affi_inv_hold afhold on mam.group_id = afhold.group_id and mam.hos_id = afhold.hos_id and mam.copy_code = afhold.copy_code and mam.store_id = afhold.store_id and mad.inv_id = afhold.inv_id left join tmp_imme imme on mam.store_id = imme.store_id and mad.inv_id = imme.inv_id left join tmp_affi_imme afimme on mam.store_id = afimme.store_id and mad.inv_id = afimme.inv_id where mad.group_id = #{group_id,jdbcType=INTEGER} and mad.hos_id = #{hos_id,jdbcType=INTEGER} and mad.copy_code = #{copy_code,jdbcType=VARCHAR} and tr.app_amount <> tr.rela_amount group by mad.apply_id ) select a.group_id, a.hos_id, a.copy_code, a.apply_id, a.apply_no, a.app_date, a.brief, a.store_id, hsd.store_no, hsd.store_code, hsd.store_name, a.dept_id, b.dept_no, b.dept_code, b.dept_name, c.emp_name, a.sender, d.user_name sender_name, a.send_date, case when e.rela_amount = e.app_amount then 3 when e.rela_amount <> e.app_amount and e.rela_amount <> 0 then 2 else 1 end rela_state from mat_apply_main a left join hos_store_dict hsd on a.group_id = hsd.group_id and a.hos_id = hsd.hos_id and a.store_id = hsd.store_id
and a.store_no = hsd.store_no
and hsd.is_stop = 0
left join hos_dept_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.dept_id = b.dept_id
and a.dept_no = b.dept_no
and b.is_stop = 0
left join hos_emp_dict c on a.group_id = c.group_id and a.hos_id = c.hos_id and a.app_emp = c.emp_id and c.is_stop = 0 left join sys_user d on a.group_id = d.group_id and a.hos_id = d.hos_id and a.sender = d.user_id left join ( select apply_id, sum(nvl(app_amount, 0)) app_amount, sum(nvl(rela_amount, 0)) rela_amount from tmp_rela group by apply_id ) e on a.apply_id = e.apply_id left join tmp_app_left g on a.apply_id = g.apply_id where a.group_id = #{group_id,jdbcType=INTEGER} and a.hos_id = #{hos_id,jdbcType=INTEGER} and a.copy_code = #{copy_code,jdbcType=VARCHAR}
and a.app_date >= #{begin_app_date,jdbcType=DATE}
and a.app_date <= #{end_app_date,jdbcType=DATE}
and a.dept_id = #{dept_id,jdbcType=INTEGER}
and a.store_id = #{store_id,jdbcType=INTEGER}
AND a.apply_no like '${apply_no}%'
and a.dept_id in ( select perm_code from v_user_data_perm where group_id = ${group_id} and hos_id = ${hos_id} and user_id = ${user_id} and table_code = 'HOS_DEPT_DICT' and is_read = 1 and is_write = 1)
and a.store_id in (select perm_code from v_user_data_perm where group_id = ${group_id} and hos_id = ${hos_id} and user_id = ${user_id} and table_code = 'HOS_STORE_DICT' and is_read = 1 and is_write = 1 )
and a.state = 3
and (#{is_zero,jdbcType=INTEGER} = 1 or (#{is_zero,jdbcType=INTEGER} = 0 and nvl(g.left_amount, 0) != 0))
and e.app_amount > 0 and e.rela_amount != e.app_amount order by a.app_date, b.dept_name, a.store_id, a.dept_id

with tmp_imme as ( select inv_id, sum(amount) as amount from ( --出库未确认单据 select inv_id, sum(nvl(amount, 0)) as amount from mat_out_main mom left join mat_out_detail matod on mom.group_id = matod.group_id and mom.hos_id = matod.hos_id and mom.copy_code = matod.copy_code and mom.out_id = matod.out_id where matod.group_id = #{group_id,jdbcType=INTEGER} AND matod.hos_id = #{hos_id,jdbcType=INTEGER} AND matod.copy_code = #{copy_code,jdbcType=VARCHAR} AND mom.store_id = #{store_id,jdbcType=INTEGER}
AND mom.state < 3 AND mom.bus_type_code <> '21' AND matod.amount_money > 0
group by inv_id --退货未确认单据 union all select inv_id, -1 * sum(nvl(amount, 0)) as amount from mat_in_main mim left join mat_in_detail matid on mim.group_id = matid.group_id and mim.hos_id = matid.hos_id and mim.copy_code = matid.copy_code and mim.in_id = matid.in_id where matid.group_id = #{group_id,jdbcType=INTEGER} AND matid.hos_id = #{hos_id,jdbcType=INTEGER} AND matid.copy_code = #{copy_code,jdbcType=VARCHAR} AND mim.store_id = #{store_id,jdbcType=INTEGER}
AND mim.state < 3 AND mim.bus_type_code in ('10','12','16','22') and matid.amount_money < 0
group by inv_id ) group by inv_id ), tmp_affi_imme as ( select inv_id, sum(amount) as amount from ( --出库未确认单据 select inv_id, sum(nvl(amount, 0)) as amount from mat_affi_out mom left join mat_affi_out_detail matod on mom.group_id = matod.group_id and mom.hos_id = matod.hos_id and mom.copy_code = matod.copy_code and mom.out_id = matod.out_id where matod.group_id = #{group_id,jdbcType=INTEGER} AND matod.hos_id = #{hos_id,jdbcType=INTEGER} AND matod.copy_code = #{copy_code,jdbcType=VARCHAR} AND mom.store_id = #{store_id,jdbcType=INTEGER}
AND mom.state < 3 AND mom.bus_type_code <> '30'
group by inv_id --退货未确认单据 union all select inv_id, -1 * sum(nvl(amount, 0)) as amount from mat_affi_in mim left join mat_affi_in_detail matid on mim.group_id = matid.group_id and mim.hos_id = matid.hos_id and mim.copy_code = matid.copy_code and mim.in_id = matid.in_id where matid.group_id = #{group_id,jdbcType=INTEGER} AND matid.hos_id = #{hos_id,jdbcType=INTEGER} AND matid.copy_code = #{copy_code,jdbcType=VARCHAR} AND mim.store_id = #{store_id,jdbcType=INTEGER}
AND mim.state < 3 AND mim.bus_type_code = '29'
group by inv_id ) group by inv_id ) SELECT a.group_id, a.hos_id, a.copy_code, a.apply_id, a.detail_id, a.inv_id, a.inv_no, b.inv_code, b.inv_name, b.inv_model, c.unit_name, d.fac_name, b.is_com, a.app_amount, nvl(g.rela_amount, 0) as rela_amount, nvl(e.cur_amount, 0) common_amount, nvl(f.cur_amount, 0) affi_amount, nvl(e.cur_amount, 0) - nvl(h.amount, 0) imme_amount, nvl(f.cur_amount, 0) - nvl(i.amount, 0) affi_imme_amount, nvl(b.plan_price, 0) price, a.app_amount*nvl(b.plan_price, 0) amount_money, a.note, a.is_closed FROM mat_apply_detail a left join mat_inv_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.inv_id = b.inv_id
and a.inv_no = b.inv_no
and b.is_stop = 0
left join hos_unit c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.unit_code = c.unit_code left join hos_fac_dict d on b.group_id = d.group_id and b.hos_id = d.hos_id and b.fac_id = d.fac_id and b.fac_no = d.fac_no left join mat_inv_hold e on a.group_id = e.group_id and a.hos_id = e.hos_id and a.copy_code = e.copy_code and a.inv_id = e.inv_id and e.store_id = #{store_id,jdbcType=INTEGER} left join mat_affi_inv_hold f on a.group_id = f.group_id and a.hos_id = f.hos_id and a.copy_code = f.copy_code and a.inv_id = f.inv_id and f.store_id = #{store_id,jdbcType=INTEGER} LEFT JOIN ( select app_detail_id, sum(nvl(rela_amount, 0)) rela_amount from mat_apply_out_rela where group_id = #{group_id,jdbcType=INTEGER} and hos_id = #{hos_id,jdbcType=INTEGER} and copy_code = #{copy_code,jdbcType=VARCHAR} and apply_id = #{apply_id,jdbcType=INTEGER} and rela_type <> 5 group by app_detail_id ) g on a.detail_id = g.app_detail_id left join tmp_imme h on a.inv_id = h.inv_id left join tmp_affi_imme i on a.inv_id = i.inv_id WHERE a.group_id = #{group_id,jdbcType=INTEGER} and a.hos_id = #{hos_id,jdbcType=INTEGER} and a.copy_code = #{copy_code,jdbcType=VARCHAR} and a.apply_id = #{apply_id,jdbcType=INTEGER} and nvl(a.is_closed, 0) = 0 and a.app_amount - nvl(g.rela_amount, 0) > 0 ORDER BY a.detail_id

with inv_temp as ( select a.group_id, a.hos_id, a.copy_code, a.inv_id, --材料ID e.inv_no, -- 材料变更号 e.inv_code, --材料编码 e.inv_name, --材料名称 e.inv_model, --规格型号 h.unit_name, --计量单位 f.mat_type_name, --材料类别名称 g.fac_name, --生成产商名称 e.spell_code, --拼音码 e.wbx_code, --五笔码 a.batch_no, --批号 sum(nvl(a.left_amount, 0)) as left_amount, --当前库存数量 sum(nvl(a.left_money, 0)) as left_money, --当前库存数量 a.price, --单价 nvl(a.sale_price, 0) as sale_price, --批发单价 nvl(e.sell_price, 0) as sell_price, --零售单价 e.alias from mat_fifo_balance a left join mat_inv_dict e on a.group_id = e.group_id and a.hos_id = e.hos_id and a.copy_code = e.copy_code and a.inv_id = e.inv_id and e.is_stop = 0 and e.use_state = 1 left join mat_type_dict f on a.group_id = f.group_id and a.hos_id = f.hos_id and a.copy_code = f.copy_code and e.mat_type_id = f.mat_type_id and f.is_stop = 0 left join hos_fac_dict g on a.group_id = g.group_id and a.hos_id = g.hos_id and e.fac_id = g.fac_id and e.fac_no=g.fac_no left join hos_unit h on a.group_id = h.group_id and a.hos_id = h.hos_id and e.unit_code = h.unit_code where a.group_id = #{group_id,jdbcType=INTEGER} AND a.hos_id = #{hos_id,jdbcType=INTEGER} AND a.copy_code = #{copy_code,jdbcType=VARCHAR} AND a.store_id = #{store_id,jdbcType=INTEGER}
and a.price >= #{begin_price}
and a.price <= #{end_price}
AND a.bar_code <> '-'
AND a.bar_code = '-'
and f.mat_type_code like '${mat_type_code}%'
group by a.group_id, a.hos_id, a.copy_code, a.inv_id, e.inv_no, e.inv_code, e.inv_name, e.spell_code, e.alias, e.wbx_code, a.batch_no, a.price, a.sale_price, e.sell_price, e.inv_model, f.mat_type_name, g.fac_name, h.unit_name ), out_temp as ( select inv_id, batch_no, price, sum(amount) amount from( --出库未确认 select b.inv_id, b.batch_no, --批号 b.price, --单价 sum(nvl(b.amount, 0)) as amount from mat_out_main a left join mat_out_detail b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.out_id = b.out_id where a.group_id = #{group_id,jdbcType=INTEGER} AND a.hos_id = #{hos_id,jdbcType=INTEGER} AND a.copy_code = #{copy_code,jdbcType=VARCHAR} AND a.store_id = #{store_id,jdbcType=INTEGER} AND a.state < 3 AND a.bus_type_code <> '21' AND b.amount_money > 0 group by b.inv_id, b.batch_no, b.price --退货未确认 union all select b.inv_id, b.batch_no, --批号 b.price, --单价 -1 * sum(nvl(b.amount, 0)) as amount from mat_in_main a left join mat_in_detail b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.in_id = b.in_id where a.group_id = #{group_id,jdbcType=INTEGER} AND a.hos_id = #{hos_id,jdbcType=INTEGER} AND a.copy_code = #{copy_code,jdbcType=VARCHAR} AND a.store_id = #{store_id,jdbcType=INTEGER} AND a.state < 3 AND a.bus_type_code in ('10','12','16','22') and b.amount_money < 0 group by b.inv_id, b.batch_no, b.price ) group by inv_id, batch_no, price ),inv_cert_tmp as( select a.inv_id,a.group_id,a.hos_id,a.copy_code,min(a.cert_id) cert_id from mat_prod_cert_inv a where a.group_id = #{group_id,jdbcType=INTEGER} and a.hos_id = #{hos_id,jdbcType=INTEGER} and a.copy_code = #{copy_code,jdbcType=VARCHAR} group by a.inv_id,a.group_id,a.hos_id,a.copy_code ) select a.group_id, a.hos_id, a.copy_code, a.inv_id, --材料ID a.inv_no, --材料变更号 a.inv_code, --材料编码 a.inv_name, --材料名称 a.inv_model, --规格型号 a.unit_name, --计量单位 a.mat_type_name, --材料类别名称 a.fac_name, --生成产商名称 a.batch_no, --批号 a.left_amount as cur_amount, --当前库存数量 a.left_money as cur_money, --当前库存金额 a.price, --单价 a.sale_price, --批发单价 a.sell_price, --零售单价 nvl(a.left_amount, 0) - nvl(b.amount, 0) AS imme_amount, mic.cert_id, mic.cert_code from inv_temp a left join out_temp b on a.inv_id = b.inv_id and a.batch_no= b.batch_no and a.price = b.price left join inv_cert_tmp ict on ict.group_id = a.group_id and ict.hos_id = a.hos_id and ict.copy_code = a.copy_code and ict.inv_id = a.inv_id left join mat_prod_cert mic on mic.group_id = ict.group_id and mic.hos_id = ict.hos_id and mic.copy_code = ict.copy_code and mic.cert_id = ict.cert_id and mic.check_state = 2
and nvl(a.left_amount, 0) - nvl(b.amount, 0) > 0
AND ( a.inv_code like '%${inv_code}%' or upper(a.inv_name) like '%${inv_code}%' or upper(a.inv_model) like '%${inv_code}%' or upper(a.spell_code) like '%${inv_code}%' or upper(a.wbx_code) like '%${inv_code}%'
or lower(a.spell_code) like '%${inv_code}%' or lower(a.wbx_code) like '%${inv_code}%' or lower(a.inv_code) like '%${inv_code}%' or lower(a.inv_name) like '%${inv_code}%'
)
order by a.inv_code, a.batch_no

select null as group_id, null as hos_id, null as copy_code, null as out_id, '合计' as out_no, null as brief, null as store_id, null as store_name, null as dept_name, null as inv_code, null as inv_name, null as unit_name, null as inv_model, null as price, sum(a.amount) as amount, sum(a.amount_money) as amount_money, null as batch_no, null as bar_code, null as fac_name, null as state, null as state_name, null as out_date, null as confirm_date, null as user_name, null as bus_type_name, null as mat_type_name, null as inva_date, null as print_state, null as print_state_name from mat_out_detail a left join mat_out_main b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.out_id = b.out_id left join mat_inv_dict c on a.group_id=c.group_id and a.hos_id=c.hos_id and a.copy_code=c.copy_code and a.inv_id=c.inv_id and a.inv_no=c.inv_no left join mat_out_resource h on b.group_id = h.group_id and b.hos_id = h.hos_id and b.copy_code = h.copy_code and b.out_id = h.out_id left join sys_print_count spc on b.group_id = h.group_id and b.hos_id = h.hos_id and b.copy_code = h.copy_code and b.out_no = spc.business_no where a.group_id=#{group_id,jdbcType=INTEGER} and a.hos_id=#{hos_id,jdbcType=INTEGER} and a.copy_code=#{copy_code,jdbcType=VARCHAR} and b.bus_type_code in (3, 5, 7, 9, 11, 17, 19, 23, 24, 25) and exists ( select 1 from v_user_data_perm where group_id = #{group_id} and hos_id = #{hos_id} and user_id = #{user_id} and table_code = 'HOS_STORE_DICT' and is_read = 1 and is_write = 1 and b.store_id=perm_code )
AND b.proj_id = #{proj_id,jdbcType=INTEGER}
AND to_char(b.out_date,'yyyy-MM-dd') >= #{out_date_start}
AND to_char(b.out_date,'yyyy-MM-dd') <= #{out_date_end}
AND b.bus_type_code = #{bus_type_code,jdbcType=VARCHAR}
AND b.maker = #{maker_code,jdbcType=VARCHAR}
AND b.confirmer = #{affirm,jdbcType=VARCHAR}
AND b.state = #{state,jdbcType=INTEGER}
AND to_char(b.confirm_date,'yyyy-MM-dd') >= #{confirm_date_start}
AND to_char(b.confirm_date,'yyyy-MM-dd') <= #{confirm_date_end}
AND b.dept_id = #{dept_id,jdbcType=INTEGER}
and b.DEPT_EMP =#{emp_id}
AND b.store_id = #{store_id,jdbcType=INTEGER}
AND b.out_no like '${out_no}%'
and b.brief like '%${brief}%'
AND c.inv_model like '%${inv_model}%'
AND a.bar_code like '%${bar_code}%'
and a.batch_no like '%${batch_no}%'
and case when nvl(spc.print_count,0) > 0 then 1 else 0 end = #{print_state}
AND ( c.inv_code like '%${inv_code}%' or upper(c.inv_name) like '%${inv_code}%' or upper(c.inv_model) like '%${inv_code}%' or upper(c.spell_code) like '%${inv_code}%' or upper(c.wbx_code) like '%${inv_code}%' or upper(c.bid_code) like '%${inv_code}%' or lower(c.spell_code) like '%${inv_code}%' or lower(c.bid_code) like '%${inv_code}%' or lower(c.wbx_code) like '%${inv_code}%' or lower(c.inv_name) like '%${inv_code}%'
)
union all select b.group_id, b.hos_id, b.copy_code, a.out_id, to_char(b.out_no) as out_no, b.brief, b.store_id, g.store_name, h.dept_name, c.inv_code, c.inv_name, d.unit_name, c.inv_model, a.price, a.amount, a.amount_money, a.batch_no, a.bar_code, f.fac_name, b.state, case when b.state =1 then '未审核' when b.state =2 then '已审核' else '已确认' end state_name, b.out_date, b.confirm_date, i.user_name, j.bus_type_name, k.mat_type_name, a.inva_date, case when nvl(spc.print_count, 0) > 0 then 1 else 0 end as print_state, case when nvl(spc.print_count, 0) > 0 then '是' else '否' end as print_state_name from mat_out_detail a left join mat_out_main b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.out_id = b.out_id left join mat_inv_dict c on a.group_id=c.group_id and a.hos_id=c.hos_id and a.copy_code=c.copy_code and a.inv_id=c.inv_id and a.inv_no=c.inv_no left join hos_unit d on c.group_id=d.group_id and c.hos_id=d.hos_id and c.unit_code=d.unit_code left join hos_fac_dict f on c.group_id=f.group_id and c.hos_id=f.hos_id and c.fac_id=f.fac_id and c.fac_no=f.fac_no left join hos_store_dict g on b.group_id=g.group_id and b.hos_id=g.hos_id and b.store_id=g.store_id and b.store_no=g.store_no left join hos_dept_dict h on b.group_id=h.group_id and b.hos_id=h.hos_id and b.dept_id=h.dept_id and b.dept_no=h.dept_no left join sys_user i on b.group_id=i.group_id and b.hos_id=i.hos_id and b.confirmer=i.user_id left join mat_bus_type j on b.bus_type_code=j.bus_type_code left join mat_type_dict k on c.group_id=k.group_id and c.hos_id=k.hos_id and c.copy_code=k.copy_code and c.mat_type_id=k.mat_type_id and c.mat_type_no=k.mat_type_no left join mat_out_resource mor on b.group_id = mor.group_id and b.hos_id = mor.hos_id and b.copy_code = mor.copy_code and b.out_id = mor.out_id left join sys_print_count spc on b.group_id = mor.group_id and b.hos_id = mor.hos_id and b.copy_code = mor.copy_code and b.out_no = spc.business_no where a.group_id=#{group_id,jdbcType=INTEGER} and a.hos_id=#{hos_id,jdbcType=INTEGER} and a.copy_code=#{copy_code,jdbcType=VARCHAR} and b.bus_type_code in (3, 5, 7, 9, 11, 17, 19, 23, 24, 25) and k.mat_type_code like '${mat_type_code}%' and exists ( select 1 from v_user_data_perm where group_id = #{group_id} and hos_id = #{hos_id} and user_id = #{user_id} and table_code = 'HOS_STORE_DICT' and is_read = 1 and is_write = 1 and b.store_id=perm_code )
AND b.proj_id = #{proj_id,jdbcType=INTEGER}
AND to_char(b.out_date,'yyyy-MM-dd') >= #{out_date_start}
AND to_char(b.out_date,'yyyy-MM-dd') <= #{out_date_end}
AND b.bus_type_code = #{bus_type_code,jdbcType=VARCHAR}
AND b.maker = #{maker_code,jdbcType=VARCHAR}
AND b.confirmer = #{affirm,jdbcType=VARCHAR}
AND b.state = #{state,jdbcType=INTEGER}
AND to_char(b.confirm_date,'yyyy-MM-dd') >= #{confirm_date_start}
AND to_char(b.confirm_date,'yyyy-MM-dd') <= #{confirm_date_end}
AND b.dept_id = #{dept_id,jdbcType=INTEGER}
and b.DEPT_EMP =#{emp_id}
AND b.store_id = #{store_id,jdbcType=INTEGER}
AND b.out_no like '${out_no}%'
and b.brief like '%${brief}%'
AND c.inv_model like '%${inv_model}%'
AND a.bar_code like '%${bar_code}%'
and a.batch_no like '%${batch_no}%'
and case when nvl(spc.print_count,0) > 0 then 1 else 0 end = #{print_state}
AND ( c.inv_code like '%${inv_code}%' or upper(c.inv_name) like '%${inv_code}%' or upper(c.inv_model) like '%${inv_code}%' or upper(c.spell_code) like '%${inv_code}%' or upper(c.wbx_code) like '%${inv_code}%' or upper(c.bid_code) like '%${inv_code}%' or lower(c.spell_code) like '%${inv_code}%' or lower(c.bid_code) like '%${inv_code}%' or lower(c.wbx_code) like '%${inv_code}%' or lower(c.inv_name) like '%${inv_code}%'
)

SELECT count(1)
FROM MAT_OUT_MAIN
WHERE

	group_id = #{item.group_id,jdbcType=INTEGER}
	and hos_id = #{item.hos_id,jdbcType=INTEGER}
	and copy_code = #{item.copy_code,jdbcType=VARCHAR}
	and out_id = #{item.out_id,jdbcType=INTEGER}
	and state = 3

	select a.group_id,a.hos_id,a.copy_code,a.req_id,a.req_code,a.make_date,a.brif,a.maker,a.stock_id store_id,a.stock_no store_no,b.store_code,b.store_name,a.dept_id,a.dept_no,
		c.dept_code,c.dept_name,d.user_name maker_name
	from mat_require_main a
	left join hos_store_dict b on a.group_id=b.group_id and a.hos_id=b.hos_id 
	     and a.stock_id=b.store_id and a.stock_no = b.store_no
	left join hos_dept_dict c on b.group_id=c.group_id and b.hos_id=c.hos_id 
	     and a.dept_id=c.dept_id and a.dept_no=c.dept_no
	left join sys_user d on b.group_id=d.group_id and b.hos_id=d.hos_id and a.maker=d.user_id and d.is_stop = 0
	left join (
         select group_id,hos_id,copy_code,req_id,sum(nvl(amount,0)) amount
         from mat_require_detail


				and group_id = #{group_id,jdbcType=INTEGER}


				and hos_id = #{hos_id,jdbcType=INTEGER}


				and copy_code = #{copy_code,jdbcType=VARCHAR}

			and nvl(is_closed, 0) = 0

         group by group_id,hos_id,copy_code,req_id
    ) aa on a.group_id=aa.group_id and a.hos_id=aa.hos_id and a.copy_code=aa.copy_code and a.req_id=aa.req_id
    left join (
         select group_id,hos_id,copy_code,req_id,sum(nvl(rela_amount,0)) rela_amount
          from mat_dept_req_out_rela


				and group_id = #{group_id,jdbcType=INTEGER}


				and hos_id = #{hos_id,jdbcType=INTEGER}


				and copy_code = #{copy_code,jdbcType=VARCHAR}


          group by group_id,hos_id,copy_code,req_id
    ) bb on a.group_id=bb.group_id and a.hos_id=bb.hos_id and a.copy_code=bb.copy_code and a.req_id=bb.req_id


			and a.group_id = #{group_id,jdbcType=INTEGER}


			and a.hos_id = #{hos_id,jdbcType=INTEGER}


			and a.copy_code = #{copy_code,jdbcType=VARCHAR}


			and a.make_date >= #{begin_date,jdbcType=DATE}


			and a.make_date <= #{end_date,jdbcType=DATE}


			and a.dept_id = #{dept_id,jdbcType=INTEGER}


			and a.stock_id = #{store_id,jdbcType=INTEGER}


			AND a.req_code like '${req_code}%'


			and a.dept_id in ( 
				select perm_code from v_user_data_perm
				where group_id = ${group_id} and hos_id = ${hos_id} and user_id = ${user_id} 
					and table_code = 'HOS_DEPT_DICT' and is_read = 1 and is_write = 1
			)


			and a.stock_id in (
				select perm_code from v_user_data_perm
				where group_id = ${group_id} and hos_id = ${hos_id} and user_id = ${user_id} 
					and table_code ='HOS_STORE_DICT' and is_read = 1 and is_write = 1 
			)

		and a.state = 2
		and nvl(aa.amount,0) != nvl(bb.rela_amount,0)

	with tmp_imme as (
		select inv_id, sum(amount) as amount
		from (
			select inv_id, sum(nvl(amount, 0)) as amount
			from mat_out_main mom
			left join mat_out_detail matod
				on mom.group_id = matod.group_id and mom.hos_id = matod.hos_id
				and mom.copy_code = matod.copy_code and mom.out_id = matod.out_id
			where matod.group_id = #{group_id,jdbcType=INTEGER}
				AND matod.hos_id = #{hos_id,jdbcType=INTEGER}
				AND matod.copy_code = #{copy_code,jdbcType=VARCHAR}
				AND mom.store_id = #{store_id,jdbcType=INTEGER}

				AND mom.state < 3 
				AND mom.bus_type_code <> '21'
				AND matod.amount_money > 0

			group by inv_id
			union all
			select inv_id, -1 * sum(nvl(amount, 0)) as amount
			from mat_in_main mim
			left join mat_in_detail matid
				on mim.group_id = matid.group_id and mim.hos_id = matid.hos_id
				and mim.copy_code = matid.copy_code and mim.in_id = matid.in_id
			where matid.group_id = #{group_id,jdbcType=INTEGER}
				AND matid.hos_id = #{hos_id,jdbcType=INTEGER} 
				AND matid.copy_code = #{copy_code,jdbcType=VARCHAR} 
				AND mim.store_id = #{store_id,jdbcType=INTEGER} 

				AND mim.state < 3 
				AND mim.bus_type_code in ('10','12','16','22') 
				and matid.amount_money < 0

			group by inv_id
		)
		group by inv_id
	), 
	tmp_affi_imme as (
		select inv_id, sum(amount) as amount
		from (
			select inv_id, sum(nvl(amount, 0)) as amount
			from mat_affi_out mom
			left join mat_affi_out_detail matod
				on mom.group_id = matod.group_id and mom.hos_id = matod.hos_id
				and mom.copy_code = matod.copy_code and mom.out_id = matod.out_id
			where matod.group_id = #{group_id,jdbcType=INTEGER}
				AND matod.hos_id = #{hos_id,jdbcType=INTEGER}
				AND matod.copy_code = #{copy_code,jdbcType=VARCHAR}
				AND mom.store_id = #{store_id,jdbcType=INTEGER}

				AND mom.state < 3 
				AND mom.bus_type_code <> '30'

			group by inv_id
			union all
			select inv_id, -1 * sum(nvl(amount, 0)) as amount
			from mat_affi_in mim
			left join mat_affi_in_detail matid
				on mim.group_id = matid.group_id and mim.hos_id = matid.hos_id
				and mim.copy_code = matid.copy_code and mim.in_id = matid.in_id
			where matid.group_id = #{group_id,jdbcType=INTEGER}
				AND matid.hos_id = #{hos_id,jdbcType=INTEGER} 
				AND matid.copy_code = #{copy_code,jdbcType=VARCHAR} 
				AND mim.store_id = #{store_id,jdbcType=INTEGER} 

				AND mim.state < 3 
				AND mim.bus_type_code = '29' 

			group by inv_id
		)
		group by inv_id
	)
	SELECT 
		a.group_id, a.hos_id, a.copy_code, a.req_id, a.req_detail_id, a.inv_id, a.inv_no, b.inv_code, b.inv_name, 
		b.inv_model, c.unit_name, d.fac_name, b.is_com, a.amount, nvl(g.rela_amount, 0) as rela_amount, 
		nvl(e.cur_amount, 0) common_amount, nvl(f.cur_amount, 0) affi_amount, 
		nvl(e.cur_amount, 0) - nvl(h.amount, 0) imme_amount, nvl(f.cur_amount, 0) - nvl(i.amount, 0) affi_imme_amount, 
		nvl(b.plan_price, 0) price, a.amount*nvl(b.plan_price, 0) amount_money, a.is_closed
	FROM mat_require_detail a
	left join mat_inv_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id
		and a.copy_code = b.copy_code and a.inv_id = b.inv_id

		and a.inv_no = b.inv_no


			and b.is_stop = 0

	left join hos_unit c on b.group_id = c.group_id and b.hos_id = c.hos_id
		and b.unit_code = c.unit_code and c.is_stop = 0
    left join hos_fac_dict d on b.group_id = d.group_id and b.hos_id = d.hos_id
    	and b.fac_id = d.fac_id and b.fac_no = d.fac_no
	left join mat_inv_hold e on a.group_id = e.group_id and a.hos_id = e.hos_id
		and a.copy_code = e.copy_code and a.inv_id = e.inv_id
		and e.store_id = #{store_id,jdbcType=INTEGER} 
	left join mat_affi_inv_hold f on a.group_id = f.group_id and a.hos_id = f.hos_id
		and a.copy_code = f.copy_code and a.inv_id = f.inv_id
		and f.store_id = #{store_id,jdbcType=INTEGER} 
	LEFT JOIN (
		select req_detail_id, sum(nvl(rela_amount, 0)) rela_amount 
		from mat_dept_req_out_rela
		where group_id = #{group_id,jdbcType=INTEGER} 
			and hos_id = #{hos_id,jdbcType=INTEGER} 
			and copy_code = #{copy_code,jdbcType=VARCHAR} 
			and req_id = #{req_id,jdbcType=INTEGER} 
		group by req_detail_id
	) g on a.req_detail_id = g.req_detail_id
	left join tmp_imme h on a.inv_id = h.inv_id
	left join tmp_affi_imme i on a.inv_id = i.inv_id
	WHERE a.group_id = #{group_id,jdbcType=INTEGER} 
		and a.hos_id = #{hos_id,jdbcType=INTEGER} 
		and a.copy_code = #{copy_code,jdbcType=VARCHAR} 
		and a.req_id = #{req_id,jdbcType=INTEGER} 
		and nvl(a.is_closed, 0) = 0
		and a.amount - nvl(g.rela_amount, 0) > 0
	ORDER BY a.req_detail_id

mapper.update:
update mat_require_detail set 
	is_closed = 1
WHERE

	group_id = #{item.group_id,jdbcType=INTEGER}
	and hos_id = #{item.hos_id,jdbcType=INTEGER}
	and copy_code = #{item.copy_code,jdbcType=VARCHAR}
	and req_id = #{item.req_id,jdbcType=INTEGER}
	and req_detail_id = #{item.req_detail_id,jdbcType=INTEGER}
