		           group by mim.group_id,mim.hos_id,mim.copy_code,hsd.sup_code,hsd.sup_name,hsdd.set_name,hsdd.store_name,mim.in_id
		           union all 
		           select  mai.group_id,mai.hos_id,mai.copy_code,hd.sup_code,hd.sup_name,hdd.set_name,hdd.store_name,sum(nvl(maid.amount_money,0)) in_money,mai.in_id
		           from mat_affi_in mai
		           left join mat_affi_in_detail maid on mai.group_id=maid.group_id and mai.hos_id=maid.hos_id and mai.copy_code=maid.copy_code
		                and mai.in_id = maid.in_id
		           left join hos_sup_dict hd on mai.group_id=hd.group_id and mai.hos_id=hd.hos_id and mai.sup_id=hd.sup_id
		                and mai.sup_no=hd.sup_no
		           join store_tmp hdd on mai.group_id=hdd.group_id and mai.hos_id=hdd.hos_id and mai.store_id=hdd.store_id
		                and mai.store_no=hdd.store_no


		           			and mai.group_id = #{group_id,jdbcType=INTEGER}


							and mai.hos_id = #{hos_id,jdbcType=INTEGER}


							and mai.copy_code = #{copy_code,jdbcType=VARCHAR}

						and mai.state= 3 

							and mai.sup_id = #{sup_id,jdbcType=INTEGER}


	 					AND mai.confirm_date >= to_date(#{begin_date,jdbcType=DATE},'yyyy/mm/dd')


						AND mai.confirm_date <= to_date(#{end_date,jdbcType=DATE},'yyyy/mm/dd')


							and mai.bus_type_code in ${in_bus_type} 


						and to_char(mai.store_id) in (
							select perm_code from v_user_data_perm  
							where group_id = #{group_id,jdbcType=INTEGER}
								and hos_id = #{hos_id,jdbcType=INTEGER}
								and table_code = 'HOS_STORE_DICT'
								and user_id = #{user_id,jdbcType=INTEGER}
								and is_read = 1 and is_write = 1 
						)

		           group by mai.group_id,mai.hos_id,mai.copy_code,hd.sup_code,hd.sup_name,hdd.set_name,hdd.store_name,mai.in_id
		     ) a
		     group by a.group_id,a.hos_id,a.copy_code,a.sup_code,a.sup_name,a.set_name,a.store_name    
		),back_tmp as (
		    select b.group_id,b.hos_id,b.copy_code,b.sup_code,b.sup_name,b.set_name,b.store_name,sum(nvl(in_money,0)) back_money ,0 in_money,count(1) as bus_amount
		    from (
		           select  mim.group_id,mim.hos_id,to_char(mim.copy_code) copy_code,hsd.sup_code,hsd.sup_name,hsdd.set_name,hsdd.store_name,
		           		sum(nvl(mid.amount_money,0)) in_money,mim.in_id
		           from mat_in_main mim
		           left join mat_in_detail mid on mim.group_id=mid.group_id and mim.hos_id=mid.hos_id and mim.copy_code=mid.copy_code
		                and mim.in_id = mid.in_id
		           left join hos_sup_dict hsd on mim.group_id=hsd.group_id and mim.hos_id=hsd.hos_id and mim.sup_id=hsd.sup_id
		                and mim.sup_no=hsd.sup_no
		           join store_tmp hsdd on mim.group_id=hsdd.group_id and mim.hos_id=hsdd.hos_id and mim.store_id=hsdd.store_id
		                and mim.store_no=hsdd.store_no


		           			and mim.group_id = #{group_id,jdbcType=INTEGER}


							and mim.hos_id = #{hos_id,jdbcType=INTEGER}


							and mim.copy_code = #{copy_code,jdbcType=VARCHAR}


							and mim.sup_id = #{sup_id,jdbcType=INTEGER}

						and mim.state= 3 

	 					AND mim.confirm_date >= to_date(#{begin_date,jdbcType=DATE},'yyyy/mm/dd')


						AND mim.confirm_date <= to_date(#{end_date,jdbcType=DATE},'yyyy/mm/dd')


							and mim.bus_type_code in ${back_bus_type} 


						and to_char(mim.store_id) in (
							select perm_code from v_user_data_perm  
							where group_id = #{group_id,jdbcType=INTEGER}
								and hos_id = #{hos_id,jdbcType=INTEGER}
								and table_code = 'HOS_STORE_DICT'
								and user_id = #{user_id,jdbcType=INTEGER}
								and is_read = 1 and is_write = 1 
						)

		           group by mim.group_id,mim.hos_id,mim.copy_code,hsd.sup_code,hsd.sup_name,hsdd.set_name,hsdd.store_name,mim.in_id
		           union all 
		           select  mai.group_id,mai.hos_id,mai.copy_code,hd.sup_code,hd.sup_name,hdd.set_name,hdd.store_name,
		           		sum(nvl(maid.amount_money,0)) in_money,mai.in_id
		           from mat_affi_in mai
		           left join mat_affi_in_detail maid on mai.group_id=maid.group_id and mai.hos_id=maid.hos_id and mai.copy_code=maid.copy_code
		                and mai.in_id = maid.in_id
		           left join hos_sup_dict hd on mai.group_id=hd.group_id and mai.hos_id=hd.hos_id and mai.sup_id=hd.sup_id
		                and mai.sup_no=hd.sup_no
		           join store_tmp hdd on mai.group_id=hdd.group_id and mai.hos_id=hdd.hos_id and mai.store_id=hdd.store_id
		                and mai.store_no=hdd.store_no


		           			and mai.group_id = #{group_id,jdbcType=INTEGER}


							and mai.hos_id = #{hos_id,jdbcType=INTEGER}


							and mai.copy_code = #{copy_code,jdbcType=VARCHAR}

						and mai.state= 3

							and mai.sup_id = #{sup_id,jdbcType=INTEGER}


	 					AND mai.confirm_date >= to_date(#{begin_date,jdbcType=DATE},'yyyy/mm/dd')


						AND mai.confirm_date <= to_date(#{end_date,jdbcType=DATE},'yyyy/mm/dd')


							and mai.bus_type_code in ${back_bus_type}


						and to_char(mai.store_id) in (
							select perm_code from v_user_data_perm  
							where group_id = #{group_id,jdbcType=INTEGER}
								and hos_id = #{hos_id,jdbcType=INTEGER}
								and table_code = 'HOS_STORE_DICT'
								and user_id = #{user_id,jdbcType=INTEGER}
								and is_read = 1 and is_write = 1 
						)

		           group by mai.group_id,mai.hos_id,mai.copy_code,hd.sup_code,hd.sup_name,hdd.set_name,hdd.store_name,mai.in_id
		     ) b
		     group by b.group_id,b.hos_id,b.copy_code,b.sup_code,b.sup_name,b.set_name,b.store_name       

		  ) ,sum_tmp as (
                select sup_code, sup_name, set_name, store_name, sum(nvl(in_money, 0)) as in_money
              		, sum(nvl(back_money, 0)) as back_money, sum(nvl(in_money, 0) + nvl(back_money, 0)) as sum_money,sum(bus_amount) bus_amount
            	from (
              		select group_id, hos_id, copy_code, sup_code, sup_name, set_name, store_name, in_money, back_money,bus_amount
              		from in_tmp
              		union all
              		select group_id, hos_id, copy_code, sup_code, sup_name, set_name, store_name, in_money, back_money,bus_amount
              		from back_tmp
            	) c
            	group by sup_code, sup_name, set_name, store_name
          )

      		select to_char(set_name) set_name,to_char(store_name) store_name,sup_code,sup_name,in_money,back_money,sum_money,bus_amount
      		from sum_tmp
      		union all
      		select '合计' set_name,'合计' store_name,null sup_code,null sup_name,sum(in_money) in_money,sum(back_money) back_money,sum(sum_money) sum_money,sum(bus_amount) bus_amount
      		from sum_tmp
    		order by  set_name, store_name,sup_code, sup_name
