mapper.insert:
INSERT INTO MED_IN_RESOURCE(group_id, hos_id, copy_code, in_id, source_id, source_money) SELECT a.group_id, a.hos_id, a.copy_code, a.in_id, b.source_id, a.source_money FROM(
SELECT #{item.group_id,jdbcType=INTEGER} as group_id, #{item.hos_id,jdbcType=INTEGER} as hos_id, #{item.copy_code,jdbcType=VARCHAR} as copy_code, #{item.in_id,jdbcType=INTEGER} as in_id, #{item.source_money,jdbcType=FLOAT} as source_money FROM dual
) a LEFT JOIN hos_source b ON a.group_id = b.group_id AND a.hos_id = b.hos_id and b.source_attr = 1 and rownum=1 order by b.source_code

