mapper.select:
with in_detail as( select mim.in_no,mim.confirm_date,mim.in_date, midd.inv_code,midd.inv_name,midd.inv_model, hu.unit_name,sum(nvl(mid.amount,0)) amount, mid.price,sum(nvl(mid.amount_money,0)) amount_money, mid.batch_no,mid.batch_sn,mid.bar_code,hsd.sup_name, hfd.fac_name,m.user_name maker_name,hstd.store_name, s.emp_name stocker_name, mbt.bus_type_name
from med_in_detail mid left join med_in_main mim on mim.group_id=mid.group_id and mim.hos_id=mid.hos_id and mim.copy_code=mid.copy_code and mim.in_id=mid.in_id left join med_inv_dict midd on midd.group_id=mid.group_id and midd.hos_id=mid.hos_id and midd.copy_code=mid.copy_code and midd.inv_id=mid.inv_id and midd.inv_no=mid.inv_no
left join hos_store_dict hstd on hstd.group_id=mim.group_id and hstd.hos_id=mim.hos_id and hstd.store_id=mim.store_id and hstd.store_no=mim.store_no left join hos_unit hu on hu.group_id=midd.group_id and hu.hos_id=midd.hos_id and hu.unit_code=midd.unit_code and hu.is_stop=0 left join hos_sup_dict hsd on hsd.group_id=mim.group_id and hsd.hos_id=mim.hos_id and hsd.sup_id=mim.sup_id and hsd.sup_no=mim.sup_no left join hos_fac_dict hfd on hfd.group_id=midd.group_id and hfd.hos_id=midd.hos_id and hfd.fac_id=midd.fac_id and hfd.fac_no=midd.fac_no left join sys_user m on m.group_id=mim.group_id and m.hos_id=mim.hos_id and m.copy_code=mim.copy_code and m.user_id=mim.maker and m.is_stop=0 left join hos_emp s on s.group_id=mim.group_id and s.hos_id=mim.hos_id and s.emp_id=mim.stocker left join med_bus_type mbt on mbt.bus_type_code=mim.bus_type_code and mbt.is_stop=0
mim.bus_type_code <> 14 and mim.state = 3
and mim.group_id = #{group_id}
and mim.hos_id = #{hos_id}
and mim.copy_code = #{copy_code}
and mid.inv_id=#{inv_id}
and mim.confirm_date >= to_date(#{startDate},'yyyy-MM-dd')
and mim.confirm_date <=to_date( #{endDate},'yyyy-MM-dd')
and mim.store_id = #{store_id}
and mim.store_no = #{store_no}
and mim.sup_id = #{sup_id}
and mim.sup_id is null
and midd.fac_id = #{fac_id}
and mim.bus_type_code=#{bus_type_code}
and midd.med_type_id=#{med_type_code}
and mid.batch_no like '%${batch_no}%'
and to_char(mim.bus_type_code) in ( select perm_code from v_user_data_perm where group_id = #{group_id,jdbcType=INTEGER} and hos_id = #{hos_id,jdbcType=INTEGER} and table_code = 'MED_BUS_TYPE' and user_id = #{user_id,jdbcType=INTEGER} and (is_read = 1 or is_write = 1) )
and to_char(midd.med_type_id) in ( select perm_code from v_user_data_perm where group_id = #{group_id,jdbcType=INTEGER} and hos_id = #{hos_id,jdbcType=INTEGER} and table_code = 'MED_TYPE_DICT' and user_id = #{user_id,jdbcType=INTEGER} and (is_read = 1 or is_write = 1) )
group by mim.in_no,mim.confirm_date,mim.in_date, midd.inv_code,midd.inv_name,midd.inv_model, hu.unit_name,mid.price, mid.batch_no,mid.batch_sn,mid.bar_code,hsd.sup_name, hfd.fac_name,m.user_name ,hstd.store_name, s.emp_name , mbt.bus_type_name order by mim.confirm_date,hstd.store_name, hsd.sup_name,hfd.fac_name ) select in_no,confirm_date,in_date,inv_code, inv_name,inv_model, unit_name,amount, price,amount_money,batch_no,batch_sn, bar_code,sup_name,fac_name,maker_name, store_name,stocker_name,bus_type_name from in_detail union all select null in_no,null confirm_date,null in_date, null inv_code,'合计' inv_name,null inv_model, null unit_name,sum(nvl(amount, 0)) as amount, null price,sum(nvl(amount_money, 0)) as amount_money, null batch_no,null batch_sn,null bar_code,null sup_name, null fac_name,null maker_name,null store_name, null stocker_name,null bus_type_name from in_detail
