mapper.insert:
INSERT INTO MED_LOCATION_TYPE ( location_type_id , group_id , hos_id , copy_code , location_type_code , location_type_name , spell_code , wbx_code , is_stop , note ) select MED_LOCATION_TYPE_SEQ.nextval , t.* from(
select #{item.group_id,jdbcType=INTEGER} , #{item.hos_id,jdbcType=INTEGER} , #{item.copy_code,jdbcType=VARCHAR} , #{item.location_type_code,jdbcType=VARCHAR} , #{item.location_type_name,jdbcType=VARCHAR} , #{item.spell_code,jdbcType=VARCHAR} , #{item.wbx_code,jdbcType=VARCHAR} , #{item.is_stop,jdbcType=INTEGER} , #{item.note,jdbcType=VARCHAR} from dual
)t

mapper.update:

UPDATE med_location_type  SET
	location_type_code = #{location_type_code},
	location_type_name = #{location_type_name},
	spell_code = #{spell_code},
	wbx_code = #{wbx_code},
	is_stop = #{is_stop},
	note = #{note}		
WHERE group_id = #{group_id}
 	and  hos_id = #{hos_id}
 	and  copy_code = #{copy_code}
 	and  location_type_id = #{location_type_id}

update.foreach:
UPDATE med_location_type SET
location_type_code = #{item.location_type_code}			,
location_type_name = #{item.location_type_name}			,
spell_code = #{item.spell_code}			,
wbx_code = #{item.wbx_code}			,
is_stop = #{item.is_stop}			,
note = #{item.note}			
WHERE  group_id = #{item.group_id}
 and  hos_id = #{item.hos_id}
 and  copy_code = #{item.copy_code}
 and  location_type_id = #{item.location_type_id}

mapper.delete:

DELETE FROM med_location_type 
WHERE group_id = #{group_id}   
	and  hos_id = #{hos_id}   
	and  copy_code = #{copy_code}   
	and  location_type_id = #{location_type_id}

DELETE FROM med_location_type WHERE

		group_id = #{item.group_id}
		 and hos_id = #{item.hos_id}
		 and copy_code = #{item.copy_code}
		 and location_type_id = #{item.location_type_id}

mapper.select:
select count(*) from med_location_dict
where copy_code = #{copy_code}
	and hos_id = #{hos_id}
	and group_id = #{group_id}
	and location_type_id = #{location_type_id}


SELECT 
	location_type_id,
	location_type_code,
	location_type_name,
	is_stop,
	note
FROM MED_LOCATION_TYPE 


		AND group_id = #{group_id}


		AND hos_id = #{hos_id}


		AND copy_code = #{copy_code}


		AND location_type_id = #{location_type_id}


		and location_type_code like #{location_type_code}||'%'



		    and (location_type_name like '%'||#{location_type_name}||'%'
		     or upper(spell_code) like '%'||#{location_type_name}||'%'
		     or upper(wbx_code) like '%'||#{location_type_name}||'%'
		     or lower(spell_code) like '%'||#{location_type_name}||'%'
		     or lower(wbx_code) like '%'||#{location_type_name}||'%')


		AND spell_code = #{spell_code}


		AND wbx_code = #{wbx_code}


		AND is_stop = #{is_stop}


		AND note = #{note}


order by location_type_code asc


SELECT 
	group_id,
	hos_id,
	copy_code,
	location_type_id,
	location_type_code,
	location_type_name,
	spell_code,
	wbx_code,
	is_stop,
	note
FROM MED_LOCATION_TYPE 


		AND group_id = #{group_id,jdbcType=INTEGER}


		AND hos_id = #{hos_id,jdbcType=INTEGER}


		AND copy_code = #{copy_code,jdbcType=VARCHAR}


		AND location_type_id = #{location_type_id,jdbcType=INTEGER}


		AND location_type_code = #{location_type_code,jdbcType=VARCHAR}


		AND location_type_name = #{location_type_name,jdbcType=VARCHAR}


		AND spell_code = #{spell_code,jdbcType=VARCHAR}


		AND wbx_code = #{wbx_code,jdbcType=VARCHAR}


		AND is_stop = #{is_stop,jdbcType=INTEGER}


		AND note = #{note,jdbcType=VARCHAR}


order by group_id asc
