mapper.select:
	select group_id,
       hos_id,
       copy_code,
       fim_type_name,
       fim_type_code,
       spell_code,
       wbx_code,
       is_stop
  from med_fim_type
  where  group_id=#{group_id}
  and hos_id=#{hos_id}
  and copy_code=#{copy_code}

  	and fim_type_code=#{fim_type_code} 


   and fim_type_name = #{fim_type_name} or spell_code = #{fim_type_name} or wbx_code= #{fim_type_name}


  	and is_stop=#{is_stop}

  order by fim_type_code

	select group_id,
       hos_id,
       copy_code,
       fim_type_name,
       fim_type_code,
       spell_code,
       wbx_code,
       is_stop
  from med_fim_type
  where fim_type_code=#{fim_type_code}
  and group_id=#{group_id}
  and hos_id=#{hos_id}
  and copy_code=#{copy_code}

mapper.update:
update med_fim_type
   set  
       fim_type_name = #{fim_type_name,jdbcType=VARCHAR},
       spell_code =#{spell_code,jdbcType=VARCHAR},
       wbx_code = #{wbx_code,jdbcType=VARCHAR},
       is_stop = #{is_stop,jdbcType=VARCHAR}
 where group_id = #{group_id,jdbcType=INTEGER}
   and hos_id = #{hos_id,jdbcType=INTEGER}
   and copy_code = #{copy_code,jdbcType=VARCHAR}
   and fim_type_code = #{fim_type_code,jdbcType=VARCHAR}

mapper.select:
select  count(*)  from med_type
where group_id = #{group_id,jdbcType=INTEGER}
   and hos_id = #{hos_id,jdbcType=INTEGER}
   and copy_code = #{copy_code,jdbcType=VARCHAR}
   and fim_type_code = #{fim_type_code,jdbcType=VARCHAR}

mapper.delete:
delete med_fim_type where

  group_id = #{item.group_id}
   and hos_id = #{item.hos_id}
   and copy_code = #{item.copy_code}
   and fim_type_code = #{item.fim_type_code}
