mapper.select:
SELECT mnd.group_id, mnd.hos_id, mnd.copy_code, mnd.deliver_id, mnd.deliver_no, mnd.origin_no , mnd.year, mnd.month , mnd.sup_id, mnd.sup_no, hsd.sup_name, mnd.store_id, mnd.store_no, hsc.store_name, mnd.brief, mnd.stocker, he.emp_name as stocker_name, mnd.stock_type_code , mst.stock_type_name , mnd.confirmer , hed.emp_name as confirmer_name, mnd.confirm_date, mnd.maker, su.user_name as maker_name, mnd.in_date, mnd.checker, syu.user_name as checker_name , mnd.check_date, mnd.state , mnd.protocol_code , mnd.app_dept, hdd.dept_code, hdd.dept_name, mnd.proj_id , t.amount_money
FROM MED_NOPAY_DELIVER mnd LEFT JOIN (select deliver_id , sum(amount_money) as amount_money from med_nopay_deliver_d where group_id = #{group_id} and hos_id = #{hos_id} and copy_code = #{copy_code} group by deliver_id ) t ON mnd.deliver_id = t.deliver_id LEFT JOIN HOS_DEPT_DICT hdd ON mnd.group_id = hdd.group_id and mnd.hos_id = hdd.hos_id and mnd.app_dept = hdd.dept_id and hdd.is_stop = 0 LEFT JOIN hos_store_dict hsc on mnd.group_id = hsc.group_id and mnd.hos_id = hsc.hos_id and mnd.store_id = hsc.store_id and mnd.store_no = hsc.store_no and hsc.is_stop = 0 LEFT JOIN sys_user su on mnd.group_id = su.group_id and mnd.hos_id = su.hos_id and mnd.maker = su.user_id LEFT JOIN sys_user syu on mnd.group_id = syu.group_id and mnd.hos_id = syu.hos_id and mnd.checker = syu.user_id LEFT JOIN hos_emp_dict hed on mnd.group_id = hed.group_id and mnd.hos_id = hed.hos_id and mnd.confirmer = hed.emp_id LEFT JOIN hos_sup_dict hsd on mnd.group_id = hsd.group_id and mnd.hos_id = hsd.hos_id and mnd.sup_id = hsd.sup_id
and hsd.is_stop = 0 LEFT JOIN hos_emp he on mnd.group_id = he.group_id and mnd.hos_id = he.hos_id and mnd.stocker = he.emp_id LEFT JOIN med_stock_type mst ON mnd.group_id = mst.group_id and mnd.hos_id = mst.hos_id and mnd.copy_code = mst.copy_code and mnd.stock_type_code = mst.stock_type_code and hdd.is_stop = 0
mnd.group_id = #{group_id,jdbcType=INTEGER} AND mnd.hos_id = #{hos_id,jdbcType=INTEGER} AND mnd.copy_code = #{copy_code,jdbcType=VARCHAR}
AND mnd.confirm_date between #{begin_confirm_date,jdbcType=DATE} and #{end_confirm_date,jdbcType=DATE}
AND mnd.check_date between #{begin_check_date,jdbcType=DATE} and #{end_check_date,jdbcType=DATE}
AND mnd.in_date between #{begin_in_date,jdbcType=DATE} and #{end_in_date,jdbcType=DATE}
AND mnd.state = #{state,jdbcType=INTEGER}
AND mnd.store_id = #{store_id,jdbcType=INTEGER}
AND mnd.sup_id = #{sup_id,jdbcType=INTEGER}
AND mnd.deliver_no like '%${deliver_no}%'
AND mnd.origin_no like '%${origin_no}%'
order by mnd.deliver_id

SELECT 
	group_id, hos_id, copy_code, deliver_id, deliver_no, origin_no, year, month , sup_id, sup_no, store_id, 
	store_no, brief, stocker, stock_type_code, confirmer, confirm_date, maker, in_date,checker, check_date,
	state, protocol_code , app_dept , proj_id
FROM MED_NOPAY_DELIVER 

	group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=VARCHAR}

		AND deliver_id = #{deliver_id,jdbcType=INTEGER}


		AND deliver_no = #{deliver_no , jdbcType=VARCHAR}


order by deliver_id

