SELECT mndd.group_id, mndd.hos_id, mndd.copy_code, mndd.deliver_id , mndd.deliver_no ,mndd.detail_id, mndd.inv_id, mndd.inv_no, b.inv_code, b.inv_name, b.inv_model, b.unit_code, c.unit_name, mndd.amount, mndd.price, mndd.amount_money, mndd.pack_code, e.pack_name, mndd.num_exchange, mndd.pack_price, mndd.num, mndd.batch_no, mndd.bar_code, mndd.sell_price, mndd.sell_money, mndd.sale_price, mndd.sale_money, mndd.inva_date, mndd.disinfect_date, mndd.location_id, d.location_name, mndd.note, b.is_highvalue, b.is_batch, b.is_bar, b.is_per_bar, b.is_quality, b.is_single_ven, b.is_disinfect , mnd.sup_id , mnd.sup_no , hsd.sup_code ,hsd.sup_name FROM med_nopay_deliver_d mndd left join med_inv_dict b on mndd.group_id = b.group_id and mndd.hos_id = b.hos_id and mndd.copy_code = b.copy_code and mndd.inv_id = b.inv_id and mndd.inv_no = b.inv_no left join hos_unit c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.unit_code = c.unit_code left join med_location_dict d on mndd.group_id = d.group_id and mndd.hos_id = d.hos_id and mndd.copy_code = d.copy_code and mndd.location_id = d.location_id left join hos_package e on mndd.group_id = e.group_id and mndd.hos_id = e.hos_id and mndd.pack_code = e.pack_code left join MED_NOPAY_DELIVER mnd ON mndd.group_id = mnd.group_id and mndd.hos_id = mnd.hos_id and mndd.copy_code = mnd.copy_code and mndd.deliver_id = mnd.deliver_id and mndd.deliver_no = mnd.deliver_no LEFT JOIN hos_sup_dict hsd on mnd.group_id = hsd.group_id and mnd.hos_id = hsd.hos_id and mnd.sup_id = hsd.sup_id
and hsd.is_stop = 0
WHERE mndd.group_id = #{group_id,jdbcType=INTEGER} and mndd.hos_id = #{hos_id,jdbcType=INTEGER} and mndd.copy_code = #{copy_code,jdbcType=VARCHAR} and mndd.deliver_id = #{deliver_id,jdbcType=INTEGER}

