SELECT  deliver_id , deliver_no
FROM MED_NOPAY_DELIVER
WHERE group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=VARCHAR}
	AND bus_type_code = #{bus_type_code,jdbcType=VARCHAR}
	AND deliver_id = (
		SELECT MAX(deliver_id) FROM MED_NOPAY_DELIVER
		WHERE group_id = #{group_id,jdbcType=INTEGER}
			AND hos_id = #{hos_id,jdbcType=INTEGER}
			AND copy_code = #{copy_code,jdbcType=VARCHAR}
			AND bus_type_code = #{bus_type_code,jdbcType=VARCHAR}
			AND deliver_id < #{deliver_id,jdbcType=INTEGER}
	)

SELECT deliver_id , deliver_no 
FROM MED_NOPAY_DELIVER
WHERE group_id = #{group_id,jdbcType=INTEGER}
	AND hos_id = #{hos_id,jdbcType=INTEGER}
	AND copy_code = #{copy_code,jdbcType=VARCHAR}
	AND bus_type_code = #{bus_type_code,jdbcType=VARCHAR}
	AND deliver_id =( 
		SELECT MIN(deliver_id) 	FROM MED_NOPAY_DELIVER
		WHERE group_id = #{group_id,jdbcType=INTEGER}
			AND hos_id = #{hos_id,jdbcType=INTEGER}
			AND copy_code = #{copy_code,jdbcType=VARCHAR}
			AND bus_type_code = #{bus_type_code,jdbcType=VARCHAR}
			AND deliver_id > #{deliver_id,jdbcType=INTEGER}
	)

