SELECT a.deliver_id as id, a.deliver_id, a.inv_id, b.inv_code, b.inv_name, b.inv_model, c.unit_name, d.fac_name, b.bid_code, a.price, a.amount, a.amount_money, a.sell_price, a.sell_money, a.sale_price, a.sale_money, a.batch_no, a.bar_code, a.inva_date, a.disinfect_date, e.location_code, e.location_name, a.note FROM med_nopay_deliver_d a LEFT JOIN med_inv_dict b ON a.group_id = b.group_id AND a.hos_id = b.hos_id AND a.copy_code = b.copy_code AND a.inv_id = b.inv_id AND a.inv_no = b.inv_no LEFT JOIN hos_unit c ON b.group_id = c.group_id AND b.hos_id = c.hos_id AND b.unit_code = c.unit_code LEFT JOIN hos_fac_dict d ON b.group_id = d.group_id AND b.hos_id = d.hos_id AND b.fac_id = d.fac_id AND b.fac_no = d.fac_no LEFT JOIN med_location_dict e ON a.group_id = e.group_id AND a.hos_id = e.hos_id AND a.copy_code = e.copy_code AND a.location_id = e.location_id AND e.is_stop = 0 WHERE a.group_id = #{group_id,jdbcType=INTEGER} AND a.hos_id = #{hos_id,jdbcType=INTEGER} AND a.copy_code = #{copy_code,jdbcType=VARCHAR}
AND a.deliver_id in (${paraId})
AND a.deliver_id = #{paraId,jdbcType=NUMERIC}
