mapper.select:
SELECT mpm.group_id, mpm.hos_id, mpm.copy_code, mpm.pur_id, mpm.pur_code, hdd.dept_name, mpm.brif, mpm.pur_type , pur.hos_name as pur_hos_name, req.hos_name as req_hos_name, pay.hos_name as pay_hos_name, su.user_name as maker, mpm.make_date, mpm.arrive_date, sus.user_name as checker, mpm.check_date, case when (mior.order_amount > 0 and mior.pur_amount != mior.order_amount) then 4 when (mior.order_amount > 0 and mior.pur_amount = mior.order_amount) then 3 else mpm.state end as state, mpm.is_dir, mpm.dir_dept_id, mpm.dir_dept_no, hddd.dept_code dir_dept_code, hddd.dept_name dir_dept_name, mdd.field_desc field_desc, mpm.come_from
FROM MED_PUR_MAIN mpm left join HOS_DEPT_DICT hdd ON mpm.group_id = hdd.group_id and mpm.hos_id = hdd.hos_id and mpm.dept_id = hdd.dept_id
and hdd.dept_no = mpm.dept_no
left join med_data_dict msl ON mpm.pur_type = msl.field_value and msl.field_code = 'PUR_TYPE' left join med_data_dict mdd on mpm.come_from = mdd.field_value and mdd.data_code='08005' and mdd.field_code='PUR_FROM' left join v_hos_dict pur on mpm.group_id = pur.group_id and mpm.pur_hos_id = pur.hos_id and pur.is_stop = 0 left join v_hos_dict req on mpm.group_id = req.group_id and mpm.req_hos_id = req.hos_id and req.is_stop = 0 left join v_hos_dict pay on mpm.group_id = pay.group_id and mpm.pay_hos_id = pay.hos_id and pay.is_stop = 0 left join SYS_USER su ON mpm.group_id = su.group_id and mpm.hos_id = su.hos_id and mpm.copy_code = su.copy_code and mpm.maker = su.user_id left join SYS_USER sus ON mpm.group_id = sus.group_id and mpm.hos_id = sus.hos_id and mpm.copy_code = sus.copy_code and mpm.checker = sus.user_id left join hos_dept_dict hddd on mpm.group_id = hddd.group_id and mpm.hos_id = hddd.hos_id and mpm.dir_dept_id = hddd.dept_id
and hddd.dept_no = mpm.dir_dept_no
left join ( select a.group_id,a.hos_id,a.copy_code,a.pur_id,a.pur_amount,b.order_amount from ( select distinct group_id,hos_id,copy_code,pur_id,sum(nvl(amount,0)) as pur_amount from med_pur_detail
and group_id = #{group_id,jdbcType=INTEGER}
and hos_id = #{hos_id,jdbcType=INTEGER}
and copy_code = #{copy_code,jdbcType=VARCHAR}
group by group_id,hos_id,copy_code,pur_id ) a left join ( select distinct group_id,hos_id,copy_code,pur_id,sum(order_amount) as order_amount from med_pur_order_rela
and group_id = #{group_id,jdbcType=INTEGER}
and hos_id = #{hos_id,jdbcType=INTEGER}
and copy_code = #{copy_code,jdbcType=VARCHAR}
group by group_id,hos_id,copy_code,pur_id ) b on a.group_id=b.group_id and a.hos_id=b.hos_id and a.copy_code=b.copy_code and a.pur_id=b.pur_id ) mior ON mpm.group_id = mior.group_id AND mpm.hos_id = mior.hos_id AND mpm.copy_code = mior.copy_code AND mpm.pur_id = mior.pur_id WHERE mpm.group_id = #{group_id} and mpm.hos_id = #{hos_id} and mpm.copy_code = #{copy_code}
and mpm.make_date >= to_date(#{begin_date},'yyyy/MM/dd')
and mpm.make_date <= to_date(#{end_date},'yyyy/MM/dd')
and mpm.dept_id = #{dept_id}
and mpm.state = #{state}
and mpm.pur_type = #{pur_type}
and mpm.pur_code like '${pur_code}%' or Upper( mpm.pur_code) like '${pur_code}%' or lower( mpm.pur_code) like '${pur_code}%'
and mpm.pur_id = #{pur_id}
and mpm.brif like '%${brif}%'
and mpm.is_dir = #{is_dir}
and mpm.dir_dept_id = #{dir_dept_id}
and mpm.dir_dept_no = #{dir_dept_no}
and mpm.is_collect=0 order by mpm.pur_id desc

