select mid.group_id, mid.hos_id, mid.copy_code, mid.inv_no, mid.inv_id, msi.store_id, hsd.store_no, hsd.store_code, hsd.store_name, mid.inv_code, mid.inv_name, mid.inv_model, hu.unit_code, hu.unit_name, mid.plan_price price, msi.package_code pack_code, hp.pack_name, nvl(msi.num_exchange,0) num_exchange, nvl(msi.period_num,0) period_num, msi.period, nvl(msi.warn_amount,0) warn_amount, nvl(mih.cur_amount,0) cur_amount, nvl(msi.secu_limit,0) secu_limit, nvl(mih.cur_amount,0) - nvl(msi.secu_limit,0) differ_amount, case when nvl(mih.cur_amount,0) - nvl(msi.secu_limit,0) !=0 then nvl(msi.num_exchange,0) /(nvl(mih.cur_amount,0) - nvl(msi.secu_limit,0)) else 0 end differ_num, abs(nvl(mih.cur_amount,0) - nvl(msi.secu_limit,0)) abs_differ, case when nvl(mih.cur_amount,0) - nvl(msi.secu_limit,0) !=0 then abs(nvl(msi.num_exchange,0) /(nvl(mih.cur_amount,0) - nvl(msi.secu_limit,0))) else 0 end abs_num, nvl(hfd.fac_id,0) fac_id, nvl(hfd.fac_no,0) fac_no, nvl(hfd.fac_name,'') fac_name, nvl(hs.sup_id,0) sup_id, nvl(hs.sup_no,0) sup_no, nvl(hs.sup_code,'') sup_code, nvl(hs.sup_name,'') sup_name
from med_store_inv msi left join med_inv_dict mid on msi.group_id = mid.group_id and msi.hos_id = mid.hos_id and msi.copy_code = mid.copy_code and msi.inv_id = mid.inv_id and mid.is_stop = 0 left join hos_unit hu on mid.group_id = hu.group_id and mid.hos_id = hu.hos_id and mid.unit_code = hu.unit_code and hu.is_stop = 0 left join hos_store_dict hsd on hsd.store_id = msi.store_id and hsd.group_id = msi.group_id and hsd.hos_id = msi.hos_id and hsd.is_stop = 0 left join hos_package hp on hp.group_id = msi.group_id and hp.hos_id = msi.hos_id and hp.pack_code = msi.package_code and hp.is_stop = 0 left join med_inv_hold mih on msi.store_id = mih.store_id and msi.group_id = mih.group_id and msi.hos_id = mih.hos_id and msi.copy_code = mih.copy_code and msi.inv_id = mih.inv_id left join hos_fac_dict hfd on hfd.fac_id = mid.fac_id and hfd.group_id = mid.group_id and hfd.hos_id = mid.hos_id and hfd.fac_no = mid.fac_no left join ( select group_id,hos_id,copy_code,inv_id,max(sup_id) sup_id from med_inv_sup group by group_id,hos_id,copy_code,inv_id ) mis on mis.group_id = mid.group_id and mis.hos_id = mid.hos_id and mis.copy_code = mid.copy_code and mis.inv_id = mid.inv_id left join hos_sup_dict hs on hs.sup_id = mis.sup_id and hs.group_id = mis.group_id and hs.hos_id = mis.hos_id and hs.is_stop = 0
msi.group_id = #{group_id,jdbcType=INTEGER}
and msi.hos_id = #{hos_id,jdbcType=INTEGER}
and msi.copy_code = #{copy_code,jdbcType=VARCHAR}
and msi.store_id = #{store_id,jdbcType=INTEGER}
and mid.med_type_id in ( select med_type_id from med_type where med_type_code like '${med_type_code}%' and is_last = 1
and group_id = #{group_id,jdbcType=INTEGER}
and hos_id = #{hos_id,jdbcType=INTEGER}
and copy_code = #{copy_code,jdbcType=VARCHAR}
)
and ( mid.inv_code like '%'||#{inv_code}||'%' or mid.inv_name like '%'||#{inv_code}||'%' or mid.spell_code like '%'||#{inv_code}||'%' or mid.wbx_code like '%'||#{inv_code}||'%' )
and msi.secu_limit > 0 and ((nvl(msi.warn_amount,0) - (nvl(mih.cur_amount,0) - nvl(msi.secu_limit,0)))) > 0
and msi.secu_limit > 0
order by mid.inv_code

