mapper.select:
SELECT mpd.pur_id as id,mpd.group_id, mpd.hos_id, mpd.copy_code, mpd.pur_id, mpd.inv_id, mid.bid_code , mpd.inv_no, mid.inv_code, mid.inv_name, mid.inv_model, mid.unit_code , hu.unit_name, mih.cur_amount, mpd.amount, mpd.price, mpd.amount * mpd.price AS amount_money , mpd.num, mpd.num_exchange, mpd.pack_code, hp.pack_name AS pack_unit_name, mpd.sup_no , mpd.sup_id, hs.sup_code as sup_code, hs.sup_name AS sup_name, mpd.req_amount, hfd.fac_id, hfd.fac_no , hfd.fac_name, mpd.memo, mpm.state FROM MED_PUR_DETAIL mpd left join MED_PUR_MAIN mpm ON mpd.group_id=mpm.group_id and mpd.hos_id=mpm.hos_id and mpd.copy_code = mpm.copy_code and mpd.pur_id = mpm.pur_id left join MED_INV_DICT mid ON mpd.group_id = mid.group_id and mpd.hos_id = mid.hos_id and mpd.copy_code = mid.copy_code and mpd.inv_id = mid.inv_id and mpd.inv_no = mid.inv_no left join MED_INV_UNIT_MAP mium ON mpd.group_id = mium.group_id and mpd.hos_id = mium.hos_id and mpd.copy_code = mium.copy_code and mpd.inv_id = mium.inv_id and mpd.pack_code = mium.pack_code left join HOS_PACKAGE hp ON mium.group_id = hp.group_id and mium.hos_id = hp.hos_id and mium.pack_code = hp.pack_code left join HOS_FAC_DICT hfd ON mid.group_id = hfd.group_id and mid.hos_id = hfd.hos_id and mid.fac_id = hfd.fac_id and mid.fac_no = hfd.fac_no left join hos_sup_dict hs ON mpd.group_id = hs.group_id and mpd.hos_id = hs.hos_id and mpd.sup_id = hs.sup_id and mpd.sup_no = hs.sup_no left join HOS_UNIT hu ON mpd.group_id = hu.group_id and mpd.hos_id = hu.hos_id and mid.unit_code = hu.unit_code left join ( SELECT group_id,hos_id,copy_code,inv_id, SUM(cur_amount) AS cur_amount FROM MED_inv_HOLD GROUP BY group_id,hos_id,copy_code,inv_id ) mih ON mid.group_id=mih.group_id and mid.hos_id=mih.hos_id and mid.copy_code=mih.copy_code and mid.inv_id = mih.inv_id
where mpd.group_id = #{group_id,jdbcType=NUMERIC} and mpd.hos_id = #{hos_id,jdbcType=NUMERIC} and mpd.copy_code = #{copy_code,jdbcType=VARCHAR}
and mpd.pur_id in (${paraId})
and mpd.pur_id=#{pur_id,jdbcType=NUMERIC}
order by pur_detail_id

SELECT to_char(mpm.make_date, 'yyyy-MM-dd') make_date, case when mpm.come_from = 2 then ps.dept_name else case when mpm.come_from = 3 then pd.dept_name else pa.dept_name end end as dept_name, mpd.inv_id, mid.bid_code, mpd.inv_no, mid.inv_code, mid.inv_name, mid.inv_model, mid.unit_code, hu.unit_name, sum(mpd.amount) amount, mpd.price, sum(mpd.amount * mpd.price) AS amount_money, mpd.num, mpd.num_exchange, mpd.pack_code, hp.pack_name AS pack_unit_name, mpd.sup_no, mpd.sup_id, hs.sup_name, sum(mpd.req_amount) req_amount, hfd.fac_id, hfd.fac_no, hfd.fac_name, mpd.memo,mtd.med_type_name FROM MED_PUR_DETAIL mpd left join MED_PUR_MAIN mpm ON mpd.pur_id = mpm.pur_id left join MED_INV_DICT mid ON mpd.group_id = mid.group_id and mpd.hos_id = mid.hos_id and mpd.copy_code = mid.copy_code and mpd.inv_id = mid.inv_id and mpd.inv_no = mid.inv_no left join med_type_dict mtd on mpd.group_id = mtd.group_id and mpd.hos_id = mtd.hos_id and mpd.copy_code = mtd.copy_code and mid.med_type_id=mtd.med_type_id and mid.med_type_no=mtd.med_type_no left join MED_INV_UNIT_MAP mium ON mpd.group_id = mium.group_id and mpd.hos_id = mium.hos_id and mpd.copy_code = mium.copy_code and mpd.inv_id = mium.inv_id and mpd.pack_code = mium.pack_code left join HOS_PACKAGE hp ON mium.group_id = hp.group_id and mium.hos_id = hp.hos_id and mium.pack_code = hp.pack_code left join HOS_FAC_DICT hfd ON mid.group_id = hfd.group_id and mid.hos_id = hfd.hos_id and mid.fac_id = hfd.fac_id and mid.fac_no = hfd.fac_no left join HOS_SUP hs ON mpd.group_id = hs.group_id and mpd.hos_id = hs.hos_id and mpd.sup_id = hs.sup_id left join HOS_UNIT hu ON mpd.group_id = hu.group_id and mpd.hos_id = hu.hos_id and mid.unit_code = hu.unit_code left join hos_dept_dict hdd on mpd.group_id = hdd.group_id and mpd.hos_id = hdd.hos_id and mpd.app_dept_id = hdd.dept_id and mpd.app_dept_no = hdd.dept_no left join ( select a.apply_id, a.rela_id as pur_id, a.rela_detail_id as pur_detail_id, a.app_detail_id, b.dept_id, b.dept_no, c.dept_code, c.dept_name from med_apply_pur_rela a left join med_apply_main b on a.group_id = b.group_id and a.hos_id =b.hos_id and a.copy_code = b.copy_code and a.apply_id = b.apply_id left join hos_dept_dict c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.dept_id = c.dept_id and b.dept_no = c.dept_no
a.group_id = #{group_id,jdbcType=INTEGER} and a.hos_id = #{hos_id,jdbcType=INTEGER} and a.copy_code = #{copy_code,jdbcType=VARCHAR}
) pa on mpm.pur_id = pa.pur_id and mpd.pur_detail_id = pa.pur_detail_id left join( select mrspr.pur_id, mrspr.pur_detail_id, cc.store_id as dept_id, cc.store_no as dept_no, cc.store_code as dept_code, cc.store_name as dept_name from med_req_store_pur_rela mrspr left join med_req_store_main mss on mrspr.group_id = mss.group_id and mrspr.hos_id =mss.hos_id and mrspr.copy_code = mss.copy_code and mrspr.req_id= mss.req_id left join hos_store_dict cc on mss.group_id = cc.group_id and mss.hos_id = cc.hos_id and mss.stock_id = cc.store_id and mss.stock_no = cc.store_no
mrspr.group_id = #{group_id,jdbcType=INTEGER} and mrspr.hos_id = #{hos_id,jdbcType=INTEGER} and mrspr.copy_code = #{copy_code,jdbcType=VARCHAR}
) ps on mpm.pur_id = ps.pur_id and mpd.pur_detail_id=ps.pur_detail_id left join ( select mrdpr.pur_id, mrdpr.pur_detail_id, mrm.dept_id, c1.dept_no, c1.dept_code, c1.dept_name from med_req_dept_pur_rela mrdpr left join med_require_main mrm on mrdpr.group_id = mrm.group_id and mrdpr.hos_id =mrm.hos_id and mrdpr.copy_code = mrm.copy_code and mrdpr.req_id= mrm.req_id left join hos_dept_dict c1 on mrm.group_id = c1.group_id and mrm.hos_id = c1.hos_id and mrm.dept_id = c1.dept_id and mrm.dept_no = c1.dept_no
mrdpr.group_id = #{group_id,jdbcType=INTEGER} and mrdpr.hos_id = #{hos_id,jdbcType=INTEGER} and mrdpr.copy_code = #{copy_code,jdbcType=VARCHAR}
) pd on mpm.pur_id = pd.pur_id and mpd.pur_detail_id = pd.pur_detail_id where mpd.group_id = #{group_id,jdbcType=NUMERIC} and mpd.hos_id = #{hos_id,jdbcType=NUMERIC} and mpd.copy_code = #{copy_code,jdbcType=VARCHAR} and mpd.pur_detail_id in (${paraId}) group by mpd.group_id, mpd.hos_id, mpd.copy_code,mpm.make_date, mpd.inv_id, mid.bid_code, mpd.inv_no, mid.inv_code, mid.inv_name, mid.inv_model, mid.unit_code, hu.unit_name, mpd.price, mpd.num, mpd.num_exchange, mpd.pack_code, hp.pack_name, mpd.sup_no, mpd.sup_id, hs.sup_name, hfd.fac_id, hfd.fac_no, hfd.fac_name, mpd.memo,mtd.med_type_name, case when mpm.come_from = 2 then ps.dept_name else case when mpm.come_from = 3 then pd.dept_name else pa.dept_name end end order by mpd.sup_id

select mrsm.group_id,mrsm.hos_id,mrsm.copy_code,mrsm.req_id,mrsm.req_code, to_char(mrsm.make_date,'yyyy-MM-dd') as make_date,mrsd.req_detail_id, mrsm.dept_id,mrsm.dept_no,hdd.dept_code,hdd.dept_name, mrsm.stock_id as store_id,mrsm.stock_no as store_no,hsd.store_code,hsd.store_name, mid.inv_code,mid.inv_name,mid.inv_model,mid.unit_code,hu.unit_name,mid.inv_id,mid.inv_no, nvl(mrsd.amount,0) as req_amount,nvl(mrsd.amount,0) as pur_amount, mdd.field_desc as come_from --, 0 checkboxstatus from med_req_store_detail mrsd left join med_req_store_main mrsm on mrsd.group_id = mrsm.group_id and mrsd.hos_id = mrsm.hos_id and mrsd.copy_code = mrsm.copy_code and mrsd.req_id = mrsm.req_id and mrsd.req_code = mrsm.req_code left join hos_dept_dict hdd on mrsm.group_id = hdd.group_id and mrsm.hos_id = hdd.hos_id and mrsm.dept_id = hdd.dept_id and mrsm.dept_no = hdd.dept_no
left join hos_store_dict hsd on mrsm.group_id = hsd.group_id and mrsm.hos_id = hsd.hos_id and mrsm.stock_id = hsd.store_id and mrsm.stock_no = hsd.store_no
left join med_inv_dict mid on mrsd.group_id = mid.group_id and mrsd.hos_id = mid.hos_id and mrsd.copy_code = mid.copy_code and mrsd.inv_id = mid.inv_id and mrsd.inv_no = mid.inv_no left join hos_unit hu on mrsd.group_id = hu.group_id and mrsd.hos_id = hu.hos_id and mid.unit_code = hu.unit_code and hu.is_stop = 0 left join med_data_dict mdd on mrsm.come_from = mdd.field_value and mdd.data_code = '08004' where mrsm.group_id = #{group_id} and mrsm.hos_id = #{hos_id} and mrsm.copy_code = #{copy_code} and mrsd.is_closed = 0
and mrsm.make_date between to_date(#{begin_date},'yyyy-MM-dd') and to_date(#{end_date},'yyyy-MM-dd')
and mrsm.stock_id = #{stock_id}
and mrsm.req_code like '%${req_code}%'
and mrsm.is_dir = #{is_dir}
and mrsm.state = #{state}
order by mrsm.req_code,mid.inv_code

