left join hos_store_dict hsd on mrsm.group_id = hsd.group_id and mrsm.hos_id = hsd.hos_id and mrsm.stock_id = hsd.store_id and mrsm.stock_no = hsd.store_no
left join med_inv_dict mid on mrsd.group_id = mid.group_id and mrsd.hos_id = mid.hos_id and mrsd.copy_code = mid.copy_code and mrsd.inv_id = mid.inv_id and mrsd.inv_no = mid.inv_no left join hos_unit hu on mrsd.group_id = hu.group_id and mrsd.hos_id = hu.hos_id and mid.unit_code = hu.unit_code and hu.is_stop = 0 left join med_data_dict mdd on mrsm.come_from = mdd.field_value and mdd.field_code = 'REQ_FROM' where mrsm.group_id = #{group_id} and mrsm.hos_id = #{hos_id} and mrsm.copy_code = #{copy_code} and mrsd.is_closed = 0
and mrsm.make_date between to_date(#{begin_date},'yyyy-MM-dd') and to_date(#{end_date},'yyyy-MM-dd')
and mrsm.stock_id = #{stock_id}
and mrsm.req_code like '%${req_code}%'
and mrsm.is_dir = #{is_dir}
and mrsm.state = #{state}
order by mrsm.req_code,mid.inv_code

