INSERT INTO MED_PUR_DETAIL( pur_detail_id, group_id, hos_id, copy_code, pur_id, pur_code, inv_id, inv_no, price ,sup_id ,sup_no ,amount ,num ,pack_code ,req_amount ,num_exchange ,memo ,is_closed ,app_dept_id ,app_dept_no ,app_date ) select MED_PUR_DETAIL_SEQ.Nextval, mid.group_id, mid.hos_id, mid.copy_code, #{pur_id}, #{pur_code}, mid.inv_id, mid.inv_no, mid.plan_price price, nvl(hs.sup_id,0) sup_id, nvl(hs.sup_no,0) sup_no, abs(nvl(mih.cur_amount,0) - nvl(msi.secu_limit,0)) amount, case when nvl(mih.cur_amount,0) - nvl(msi.secu_limit,0) !=0 then abs(nvl(msi.num_exchange,0) /(nvl(mih.cur_amount,0) - nvl(msi.secu_limit,0))) else 0 end num, msi.package_code pack_code, null, nvl(msi.num_exchange,0) num_exchange, null, 0, null, null, null
from med_store_inv msi left join med_inv_dict mid on msi.group_id = mid.group_id and msi.hos_id = mid.hos_id and msi.copy_code = mid.copy_code and msi.inv_id = mid.inv_id and mid.is_stop = 0 left join hos_unit hu on mid.group_id = hu.group_id and mid.hos_id = hu.hos_id and mid.unit_code = hu.unit_code and hu.is_stop = 0 left join hos_store_dict hsd on hsd.store_id = msi.store_id and hsd.group_id = msi.group_id and hsd.hos_id = msi.hos_id and hsd.is_stop = 0 left join hos_package hp on hp.group_id = msi.group_id and hp.hos_id = msi.hos_id and hp.pack_code = msi.package_code and hp.is_stop = 0 left join med_inv_hold mih on msi.store_id = mih.store_id and msi.group_id = mih.group_id and msi.hos_id = mih.hos_id and msi.copy_code = mih.copy_code and msi.inv_id = mih.inv_id left join hos_fac_dict hfd on hfd.fac_id = mid.fac_id and hfd.group_id = mid.group_id and hfd.hos_id = mid.hos_id and hfd.fac_no = mid.fac_no left join ( select group_id,hos_id,copy_code,inv_id,max(sup_id) sup_id from med_inv_sup group by group_id,hos_id,copy_code,inv_id ) mis on mis.group_id = mid.group_id and mis.hos_id = mid.hos_id and mis.copy_code = mid.copy_code and mis.inv_id = mid.inv_id left join hos_sup_dict hs on hs.sup_id = mis.sup_id and hs.group_id = mis.group_id and hs.hos_id = mis.hos_id and hs.is_stop = 0 where msi.group_id = #{group_id,jdbcType=INTEGER} and msi.hos_id = #{hos_id,jdbcType=INTEGER} and msi.copy_code = #{copy_code,jdbcType=VARCHAR}
and msi.store_id = #{store_id,jdbcType=INTEGER}
and mid.med_type_id in ( select med_type_id from med_type where med_type_code like '${med_type_code}%' and is_last = 1
and group_id = #{group_id,jdbcType=INTEGER}
and hos_id = #{hos_id,jdbcType=INTEGER}
and copy_code = #{copy_code,jdbcType=VARCHAR}
)
and msi.secu_limit > 0 and ((nvl(msi.warn_amount,0) - (nvl(mih.cur_amount,0) - nvl(msi.secu_limit,0)))) > 0

