insert.foreach:
INSERT INTO MED_REQUIRE_DETAIL ( req_detail_id , group_id , hos_id , copy_code , req_id , req_code , inv_id , inv_no , amount , price , num, pack_code, sup_no, sup_id, num_exchange, memo
)values( MED_REQUIRE_DETAIL_SEQ.NEXTVAL, #{item.group_id,jdbcType=INTEGER} , #{item.hos_id,jdbcType=INTEGER} , #{item.copy_code,jdbcType=VARCHAR} , #{item.req_id,jdbcType=INTEGER} , #{item.req_code,jdbcType=VARCHAR} , #{item.inv_id,jdbcType=INTEGER} , #{item.inv_no,jdbcType=INTEGER} , #{item.amount,jdbcType=INTEGER} , #{item.price,jdbcType=FLOAT} , #{item.num,jdbcType=INTEGER}, #{item.pack_code,jdbcType=VARCHAR}, #{item.sup_no,jdbcType=INTEGER}, #{item.sup_id,jdbcType=INTEGER}, #{item.num_exchange,jdbcType=FLOAT}, #{item.memo,jdbcType=VARCHAR}
)

mapper.update:
UPDATE med_require_detail 


		amount = #{amount,jdbcType=INTEGER},


		price = #{price,jdbcType=FLOAT},


		num = #{num,jdbcType=FLOAT},


		pack_code = #{pack_code,jdbcType=VARCHAR},


		sup_no = #{sup_no,jdbcType=INTEGER},


		sup_id = #{sup_id,jdbcType=INTEGER},


		num_exchange = #{num_exchange,jdbcType=FLOAT},


		memo = #{memo,jdbcType=VARCHAR},




		group_id = #{group_id,jdbcType=INTEGER}


		and hos_id = #{hos_id,jdbcType=INTEGER}


		and copy_code = #{copy_code,jdbcType=VARCHAR}


		and req_id = #{req_id,jdbcType=INTEGER}


		and req_code = #{req_code,jdbcType=VARCHAR}


		and req_detail_id = #{req_detail_id,jdbcType=INTEGER}


		and inv_id = #{inv_id,jdbcType=INTEGER}

update.foreach:
UPDATE med_require_detail 


		amount = #{item.amount,jdbcType=INTEGER},


		price = #{item.price,jdbcType=FLOAT},


		num = #{item.num,jdbcType=FLOAT},


		pack_code = #{item.pack_code,jdbcType=VARCHAR},


