mapper.delete:
DELETE FROM med_require_detail 
WHERE	
	group_id = #{group_id,jdbcType=INTEGER} 
and hos_id = #{hos_id,jdbcType=INTEGER} 
and copy_code = #{copy_code,jdbcType=VARCHAR} 
and req_id = #{req_id,jdbcType=INTEGER} 
and req_detail_id not in (${req_detail_id})

DELETE FROM med_require_detail WHERE

	group_id = #{item.group_id,jdbcType=INTEGER}
	and	hos_id = #{item.hos_id,jdbcType=INTEGER}
	and	copy_code = #{item.copy_code,jdbcType=VARCHAR}
	and	req_id = #{item.req_id,jdbcType=INTEGER}

mapper.select:
SELECT a.group_id, a.hos_id, a.copy_code, a.req_id, a.req_detail_id, a.inv_no, a.inv_id, b.inv_code, b.inv_name, b.inv_model, c.unit_name, a.pack_code, hp.pack_name, a.num_exchange num_exchange, nvl(a.num,0) num, nvl(a.amount,0) amount, nvl(a.price,0) price, nvl(a.amount,0)*nvl(a.price,0) sum_money, a.sup_no, a.sup_id, d.sup_name, e.fac_no, e.fac_id, e.fac_name, a.memo, b.inv_structure FROM MED_REQUIRE_DETAIL a left join med_inv_dict b on a.inv_no = b.inv_no and a.inv_id = b.inv_id and a.hos_id = b.hos_id and a.group_id = b.group_id and a.copy_code = b.copy_code
left join hos_unit c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.unit_code = c.unit_code left join hos_sup_dict d on a.group_id = d.group_id and a.hos_id = d.hos_id and a.sup_id = d.sup_id and a.sup_no = d.sup_no and d.is_stop = 0 left join hos_fac_dict e on b.group_id = e.group_id and b.hos_id = e.hos_id and b.fac_id = e.fac_id and b.fac_no = e.fac_no left join hos_package hp on hp.pack_code = a.pack_code and hp.hos_id = a.hos_id and hp.group_id = a.group_id and hp.is_stop = 0
a.group_id = #{group_id,jdbcType=INTEGER}
AND a.hos_id = #{hos_id,jdbcType=INTEGER}
AND a.copy_code = #{copy_code,jdbcType=VARCHAR}
AND a.req_id = #{req_id,jdbcType=INTEGER}
AND a.req_code = #{req_code,jdbcType=VARCHAR}
order by a.req_detail_id asc

select a.req_id,
	b.inv_id,
	b.inv_no,
	b.amount 
from med_require_main a
left join med_require_detail b on a.req_id = b.req_id 
	and a.hos_id = b.hos_id 
	and a.group_id = b.group_id 
	and a.copy_code = b.copy_code 


		 a.group_id = #{group_id,jdbcType=INTEGER}


		AND a.hos_id = #{hos_id,jdbcType=INTEGER}


		AND a.copy_code = #{copy_code,jdbcType=VARCHAR}


		 AND a.make_date >=  to_date(#{begin_date},'yyyy/MM/dd') 


		 AND a.make_date <=  to_date(#{end_date},'yyyy/MM/dd') 


		 AND a.stock_id = #{store_id,jdbcType=INTEGER}


		 AND a.stock_no = #{store_no,jdbcType=INTEGER}


		 AND a.dept_id = #{dept_id,jdbcType=INTEGER}


		 AND a.dept_no = #{dept_no,jdbcType=INTEGER}

	and b.inv_id is not null
 	and a.is_submit = 1   
 	and a.state = 2

 order by a.req_id,b.inv_id

SELECT group_id, hos_id, copy_code, req_id, req_detail_id, inv_no, inv_id, amount, price, num, num_exchange, pack_code, sup_no, sup_id, memo FROM med_require_detail
group_id = #{group_id,jdbcType=INTEGER}
AND hos_id = #{hos_id,jdbcType=INTEGER}
AND copy_code = #{copy_code,jdbcType=VARCHAR}
AND req_id = #{req_id,jdbcType=INTEGER}
AND req_code = #{req_code,jdbcType=VARCHAR}
order by req_detail_id

SELECT group_id, hos_id, copy_code, req_id, req_detail_id, inv_no, inv_id, amount, price, num, pack_code, sup_no, sup_id, memo FROM med_require_detail
group_id = #{group_id,jdbcType=INTEGER}
AND hos_id = #{hos_id,jdbcType=INTEGER}
AND copy_code = #{copy_code,jdbcType=VARCHAR}
AND req_id = #{req_id,jdbcType=INTEGER}
AND req_code = #{req_code,jdbcType=VARCHAR}
order by req_detail_id

SELECT
	group_id,
	hos_id,
	copy_code,
	req_id,
	req_detail_id,
	inv_no,
	inv_id,
	amount,
	price,
	num,
	pack_code,
	rdate,
	sup_no,
	sup_id,
	exec_amount,
	memo
FROM MED_REQUIRE_DETAIL


		AND group_id = #{group_id,jdbcType=INTEGER}


		AND hos_id = #{hos_id,jdbcType=INTEGER}


		AND copy_code = #{copy_code,jdbcType=VARCHAR}


		AND req_id = #{req_id,jdbcType=INTEGER}


		AND req_detail_id = #{req_detail_id,jdbcType=INTEGER}


		AND inv_no = #{inv_no,jdbcType=INTEGER}


		AND inv_id = #{inv_id,jdbcType=INTEGER}


		AND amount = #{amount,jdbcType=INTEGER}


		AND price = #{price,jdbcType=INTEGER}


		AND num = #{num,jdbcType=INTEGER}


		AND pack_code = #{pack_code,jdbcType=VARCHAR}


		AND rdate = #{rdate,jdbcType=DATE}


		AND sup_no = #{sup_no,jdbcType=INTEGER}


		AND sup_id = #{sup_id,jdbcType=INTEGER}


		AND exec_amount = #{exec_amount,jdbcType=INTEGER}


		AND memo = #{memo,jdbcType=VARCHAR}


order by group_id asc
