mapper.select:
with tmp_imme as ( select inv_id, store_id, sum(amount) as amount from ( --出库未确认单据 select inv_id, mom.store_id, sum(nvl(amount, 0)) as amount from med_out_main mom left join med_out_detail medod on mom.group_id = medod.group_id and mom.hos_id = medod.hos_id and mom.copy_code = medod.copy_code and mom.out_id = medod.out_id where medod.group_id = #{group_id,jdbcType=INTEGER} AND medod.hos_id = #{hos_id,jdbcType=INTEGER} AND medod.copy_code = #{copy_code,jdbcType=VARCHAR}
AND mom.state < 3 AND mom.bus_type_code <> '21' AND medod.amount_money > 0
group by inv_id, mom.store_id --退货未确认单据 union all select inv_id, mim.store_id, -1 * sum(nvl(amount, 0)) as amount from med_in_main mim left join med_in_detail medid on mim.group_id = medid.group_id and mim.hos_id = medid.hos_id and mim.copy_code = medid.copy_code and mim.in_id = medid.in_id where medid.group_id = #{group_id,jdbcType=INTEGER} AND medid.hos_id = #{hos_id,jdbcType=INTEGER} AND medid.copy_code = #{copy_code,jdbcType=VARCHAR}
AND mim.state < 3 AND mim.bus_type_code in ('10','12','16','22')
and medid.amount_money < 0 group by inv_id, mim.store_id ) group by inv_id, store_id ), tmp_affi_imme as ( select inv_id, store_id, sum(amount) as amount from ( --出库未确认单据 select inv_id, mom.store_id, sum(nvl(amount, 0)) as amount from med_affi_out mom left join med_affi_out_detail medod on mom.group_id = medod.group_id and mom.hos_id = medod.hos_id and mom.copy_code = medod.copy_code and mom.out_id = medod.out_id where medod.group_id = #{group_id,jdbcType=INTEGER} AND medod.hos_id = #{hos_id,jdbcType=INTEGER} AND medod.copy_code = #{copy_code,jdbcType=VARCHAR}
AND mom.state < 3 AND mom.bus_type_code <> '30'
group by inv_id, mom.store_id --退货未确认单据 union all select inv_id, mim.store_id, -1 * sum(nvl(amount, 0)) as amount from med_affi_in mim left join med_affi_in_detail medid on mim.group_id = medid.group_id and mim.hos_id = medid.hos_id and mim.copy_code = medid.copy_code and mim.in_id = medid.in_id where medid.group_id = #{group_id,jdbcType=INTEGER} AND medid.hos_id = #{hos_id,jdbcType=INTEGER} AND medid.copy_code = #{copy_code,jdbcType=VARCHAR}
AND mim.state < 3 AND mim.bus_type_code = '29'
group by inv_id, mim.store_id ) group by inv_id, store_id ), tmp_rela as ( --申领单总处理数量 select mad.apply_id, mad.detail_id,mad.inv_id, mad.app_amount, sum(nvl(maor.rela_amount, 0)) rela_amount from med_apply_detail mad left join med_apply_out_rela maor on mad.group_id = maor.group_id and mad.hos_id = maor.hos_id and mad.copy_code = maor.copy_code and mad.apply_id = maor.apply_id and mad.detail_id = maor.app_detail_id and maor.rela_type <> 5
LEFT JOIN med_inv_dict mid ON mad.group_id = mid.group_id and mad.hos_id = mid.hos_id and mad.copy_code = mid.copy_code and mad.inv_id = mid.inv_id and mad.inv_no = mid.inv_no
where mad.group_id = #{group_id,jdbcType=INTEGER} and mad.hos_id = #{hos_id,jdbcType=INTEGER} and mad.copy_code = #{copy_code,jdbcType=VARCHAR} and nvl(mad.is_closed, 0) = 0
and (mid.inv_code like '%${inv_code}%' or mid.inv_name like '%${inv_code}%')
group by mad.apply_id, mad.detail_id, mad.app_amount,mad.inv_id ), tmp_app_left as( --申请单药品总库存 select mad.apply_id,mad.inv_id, sum(nvl(hold.cur_amount, 0) + nvl(afhold.cur_amount, 0) - nvl(imme.amount, 0) - nvl(afimme.amount, 0)) left_amount from med_apply_main mam left join med_apply_detail mad on mam.group_id = mad.group_id and mam.hos_id = mad.hos_id and mam.copy_code = mad.copy_code and mam.apply_id = mad.apply_id left join tmp_rela tr on mad.apply_id = tr.apply_id and mad.detail_id = tr.detail_id left join med_inv_hold hold on mam.group_id = hold.group_id and mam.hos_id = hold.hos_id and mam.copy_code = hold.copy_code and mam.store_id = hold.store_id and mad.inv_id = hold.inv_id left join med_affi_inv_hold afhold on mam.group_id = afhold.group_id and mam.hos_id = afhold.hos_id and mam.copy_code = afhold.copy_code and mam.store_id = afhold.store_id and mad.inv_id = afhold.inv_id left join tmp_imme imme on mam.store_id = imme.store_id and mad.inv_id = imme.inv_id left join tmp_affi_imme afimme on mam.store_id = afimme.store_id and mad.inv_id = afimme.inv_id where mad.group_id = #{group_id,jdbcType=INTEGER} and mad.hos_id = #{hos_id,jdbcType=INTEGER} and mad.copy_code = #{copy_code,jdbcType=VARCHAR} and nvl(mad.is_closed, 0) = 0 and tr.app_amount <> tr.rela_amount group by mad.apply_id,mad.inv_id ), tem_app_kczt as ( select a.apply_id,a.inv_id,case when a.app_amount > b.left_amount then 0 else 1 end kczt from tmp_rela a left join tmp_app_left b on a.apply_id=b.apply_id and a.inv_id=b.inv_id ), tem_app_kczt1 as ( select a.apply_id,sum(a.kczt) kczt2,count(*) as kczt3 from tem_app_kczt a group by a.apply_id ), tem_app_kczt2 as ( select a.apply_id,case when kczt2=kczt3 then 1 when kczt2 <> 0 and kczt2 < kczt3 then 2 else 3 end as kczt4 from tem_app_kczt1 a
) select a.group_id, a.hos_id, a.copy_code, a.apply_id, a.apply_no, to_char(a.app_date, 'yyyy-MM-dd hh24:mi:ss') app_date, a.brief, a.store_id, hsd.store_name, a.dept_id, b.dept_name, c.emp_name, a.sender, d.user_name sender_name,e.emp_name as response_emp, a.send_date, case when e.rela_amount >= e.app_amount then 3 when e.rela_amount < e.app_amount and e.rela_amount > 0 then 2 else 1 end rela_state, case nvl(f.apply_id, 0) when 0 then 0 else 1 end is_req, kczt4 as left_state from med_apply_main a left join hos_store_dict hsd on a.group_id = hsd.group_id and a.hos_id = hsd.hos_id and a.store_id = hsd.store_id
and a.store_no = hsd.store_no
and hsd.is_stop = 0
left join hos_dept_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.dept_id = b.dept_id
and a.dept_no = b.dept_no
and b.is_stop = 0
left join hos_emp_dict c on a.group_id = c.group_id and a.hos_id = c.hos_id and a.app_emp = c.emp_id and c.is_stop = 0 left join hos_emp_dict e on a.group_id = e.group_id and a.hos_id = e.hos_id and a.app_emp = e.emp_id and e.is_stop = 0 left join sys_user d on a.group_id = d.group_id and a.hos_id = d.hos_id and a.sender = d.user_id inner join ( select apply_id, sum(app_amount) app_amount, sum(rela_amount) rela_amount from tmp_rela group by apply_id ) e on a.apply_id = e.apply_id left join( select distinct apply_id from med_apply_out_rela where group_id = #{group_id,jdbcType=INTEGER} and hos_id = #{hos_id,jdbcType=INTEGER} and copy_code = #{copy_code,jdbcType=VARCHAR} and rela_type = 5 ) f on a.apply_id = f.apply_id left join tem_app_kczt2 g on a.apply_id = g.apply_id
and a.group_id = #{group_id,jdbcType=INTEGER}
and a.hos_id = #{hos_id,jdbcType=INTEGER}
and a.copy_code = #{copy_code,jdbcType=VARCHAR}
and a.app_date >= #{begin_app_date,jdbcType=TIMESTAMP}
and a.app_date <= #{end_app_date,jdbcType=TIMESTAMP}
and a.send_date >= #{begin_send_date,jdbcType=TIMESTAMP}
and a.send_date <= #{end_send_date,jdbcType=TIMESTAMP}
and a.dept_id = #{dept_id,jdbcType=INTEGER}
and a.brief = #{brief,jdbcType=VARCHAR}
and a.store_id = #{store_id,jdbcType=INTEGER}
and nvl(e.rela_amount, 0) = 0
and e.rela_amount = e.app_amount
and e.rela_amount != e.app_amount and nvl(e.rela_amount, 0) != 0
and nvl(e.rela_amount, 0) != e.app_amount
and nvl(f.apply_id, 0) = 0
and nvl(f.apply_id, 0) <> 0
AND a.apply_no like '%${apply_no}%'
and ((to_char(a.store_id) in (select perm_code from v_user_data_perm where group_id = ${group_id} and hos_id = ${hos_id} and user_id = ${user_id} and table_code = 'HOS_STORE_DICT' and is_read = 1 and is_write = 1 ) ) or (a.store_id is null))
and a.app_emp = #{app_emp,jdbcType=INTEGER}
and a.response_emp = #{response_emp,jdbcType=INTEGER}
and a.state = 3
order by a.app_date desc, a.store_id, a.dept_id

with tmp_imme as ( select inv_id, sum(amount) as amount from ( --出库未确认单据 select inv_id, sum(nvl(amount, 0)) as amount from med_out_main mom left join med_out_detail medod on mom.group_id = medod.group_id and mom.hos_id = medod.hos_id and mom.copy_code = medod.copy_code and mom.out_id = medod.out_id where medod.group_id = #{group_id,jdbcType=INTEGER} AND medod.hos_id = #{hos_id,jdbcType=INTEGER} AND medod.copy_code = #{copy_code,jdbcType=VARCHAR} AND mom.store_id = #{store_id,jdbcType=INTEGER}
AND mom.state < 3 AND mom.bus_type_code <> '21' AND medod.amount_money > 0
group by inv_id --退货未确认单据 union all select inv_id, -1 * sum(nvl(amount, 0)) as amount from med_in_main mim left join med_in_detail medid on mim.group_id = medid.group_id and mim.hos_id = medid.hos_id and mim.copy_code = medid.copy_code and mim.in_id = medid.in_id where medid.group_id = #{group_id,jdbcType=INTEGER} AND medid.hos_id = #{hos_id,jdbcType=INTEGER} AND medid.copy_code = #{copy_code,jdbcType=VARCHAR} AND mim.store_id = #{store_id,jdbcType=INTEGER}
AND mim.state < 3 AND mim.bus_type_code in ('10','12','16','22') and medid.amount_money < 0
group by inv_id ) group by inv_id ), tmp_affi_imme as ( select inv_id, sum(amount) as amount from ( --出库未确认单据 select inv_id, sum(nvl(amount, 0)) as amount from med_affi_out mom left join med_affi_out_detail medod on mom.group_id = medod.group_id and mom.hos_id = medod.hos_id and mom.copy_code = medod.copy_code and mom.out_id = medod.out_id where medod.group_id = #{group_id,jdbcType=INTEGER} AND medod.hos_id = #{hos_id,jdbcType=INTEGER} AND medod.copy_code = #{copy_code,jdbcType=VARCHAR} AND mom.store_id = #{store_id,jdbcType=INTEGER}
AND mom.state < 3 AND mom.bus_type_code <> '30'
group by inv_id --退货未确认单据 union all select inv_id, -1 * sum(nvl(amount, 0)) as amount from med_affi_in mim left join med_affi_in_detail medid on mim.group_id = medid.group_id and mim.hos_id = medid.hos_id and mim.copy_code = medid.copy_code and mim.in_id = medid.in_id where medid.group_id = #{group_id,jdbcType=INTEGER} AND medid.hos_id = #{hos_id,jdbcType=INTEGER} AND medid.copy_code = #{copy_code,jdbcType=VARCHAR} AND mim.store_id = #{store_id,jdbcType=INTEGER}
AND mim.state < 3 AND mim.bus_type_code = '29'
group by inv_id ) group by inv_id ) SELECT a.group_id, a.hos_id, a.copy_code, a.apply_id, a.detail_id, b.bid_code,a.inv_id, a.inv_no, b.inv_code, b.inv_name, b.inv_model, c.unit_name, d.fac_name, b.is_com, a.app_amount, nvl(g.rela_amount, 0) as rela_amount, nvl(e.cur_amount, 0) common_amount, nvl(f.cur_amount, 0) affi_amount, nvl(e.cur_amount, 0) - nvl(h.amount, 0) imme_amount, nvl(f.cur_amount, 0) - nvl(i.amount, 0) affi_imme_amount, nvl(b.plan_price, 0) price, a.app_amount*nvl(b.plan_price, 0) amount_money, a.note, a.is_closed FROM med_apply_detail a left join med_inv_dict b on a.group_id = b.group_id and a.hos_id = b.hos_id and a.copy_code = b.copy_code and a.inv_id = b.inv_id and b.is_stop = 0
and a.inv_no = b.inv_no
and b.is_stop = 0
left join hos_unit c on b.group_id = c.group_id and b.hos_id = c.hos_id and b.unit_code = c.unit_code left join hos_fac_dict d on b.group_id = d.group_id and b.hos_id = d.hos_id and b.fac_id = d.fac_id and b.fac_no=d.fac_no left join med_inv_hold e on a.group_id = e.group_id and a.hos_id = e.hos_id and a.copy_code = e.copy_code and a.inv_id = e.inv_id and e.store_id = #{store_id,jdbcType=INTEGER} left join med_affi_inv_hold f on a.group_id = f.group_id and a.hos_id = f.hos_id and a.copy_code = f.copy_code and a.inv_id = f.inv_id and f.store_id = #{store_id,jdbcType=INTEGER} LEFT JOIN ( select app_detail_id, sum(nvl(rela_amount, 0)) rela_amount from med_apply_out_rela where group_id = #{group_id,jdbcType=INTEGER} and hos_id = #{hos_id,jdbcType=INTEGER} and copy_code = #{copy_code,jdbcType=VARCHAR} and apply_id = #{apply_id,jdbcType=INTEGER} and rela_type <> 5 group by app_detail_id ) g on a.detail_id = g.app_detail_id left join tmp_imme h on a.inv_id = h.inv_id left join tmp_affi_imme i on a.inv_id = i.inv_id WHERE a.group_id = #{group_id,jdbcType=INTEGER} and a.hos_id = #{hos_id,jdbcType=INTEGER} and a.copy_code = #{copy_code,jdbcType=VARCHAR} and a.apply_id = #{apply_id,jdbcType=INTEGER} ORDER BY a.detail_id

