mapper.select:
SELECT a.group_id, a.hos_id, a.copy_code, '3' bus_type_code, a.store_id, c.store_no, c.store_code, c.store_name, a.dept_id, d.dept_no, d.dept_code, d.dept_name, a.app_emp dept_emp, b.emp_no, b.emp_code, b.emp_name, a.apply_id,a.app_date,
to_char('由科室申领单汇总生成：'||WM_CONCAT(to_char(brief))) as brief FROM med_apply_main a LEFT JOIN hos_emp_dict b ON a.group_id = b.group_id and a.hos_id = b.hos_id and a.app_emp = b.emp_id and b.is_stop = 0 LEFT JOIN hos_store_dict c ON a.group_id = c.group_id and a.hos_id = c.hos_id and a.store_id = c.store_id and c.is_stop = 0 LEFT JOIN hos_dept_dict d ON a.group_id = d.group_id and a.hos_id = d.hos_id and a.dept_id = d.dept_id and d.is_stop = 0 WHERE a.group_id = #{group_id,jdbcType=INTEGER} and a.hos_id = #{hos_id,jdbcType=INTEGER} and a.copy_code = #{copy_code,jdbcType=VARCHAR} and a.apply_id = #{apply_id,jdbcType=INTEGER}
GROUP BY a.group_id, a.hos_id, a.copy_code, a.store_id, c.store_no, c.store_code, c.store_name, a.dept_id, d.dept_no, d.dept_code, d.dept_name, a.app_emp , b.emp_no, b.emp_code, b.emp_name, a.apply_id,a.app_date

select a.store_id||','||b.store_no from med_store a
left join hos_store_dict b
	on a.group_id = b.group_id
	and a.hos_id = b.hos_id
	and a.store_id = b.store_id
	and b.is_stop = 0
where a.group_id = #{group_id,jdbcType=INTEGER}
	and a.hos_id = #{hos_id,jdbcType=INTEGER}
	and a.dept_id = #{dept_id,jdbcType=INTEGER} 
	and rownum = 1

